INSERT INTO `company` VALUES (131, '上海蛙扑网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/1115105.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=131', '', '梦宝谷', '一家从事手机游戏出版与增值电信业务中的信息服务业务的公司', 'success');
INSERT INTO `company` VALUES (132, '北京神奇时代网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/1115106.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=132', 'Magic Universe', '神奇时代', '一家集研发、运营于一体的移动终端互联网游戏公司。2012年2月，神奇时代成功推出了基于HTML5制作的策略网游《三国时代》，2012年6月，神奇时代推出首款移动端重度玩法仙侠网游《忘仙》，游戏采用掌端技术同跨iOS、 Android两大系统，公测仅1周即荣登AppStore畅销榜首屏，并连续数周蝉联各大应用平台人气榜首之位。', 'success');
INSERT INTO `company` VALUES (133, '天津电子出版社有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/1115107.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=133', '', '天津电子出版社', '是由国家新闻出版总署批准的拥有音像制品、电子出版物、互联网出版资质，集电子、音像、期刊和网络出版于一体的综合性出版发行单位', 'success');
INSERT INTO `company` VALUES (134, '卓易通（北京）网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/1115108.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=134', '', '卓易通', '卓易通（北京）网络技术有限公司于2012-05-02在海淀分局登记成立。法定代表人阎廷双，公司经营范围包括技术推广服务；经济贸易咨询；企业管理咨询等。', 'success');
