INSERT INTO `company` VALUES (137, '北京互联通网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/1115111.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=137', 'Cnlink', '互联通', '一家领先的网络服务提供商，致力于为企业提供专业的IT服务、网络安全和内容传递等全方位的一站式信息技术服务。', 'success');
INSERT INTO `company` VALUES (138, '广州华多网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/1115112.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=138', 'Duowan', '多玩', '国内首家富集通讯业务运营商。经营范围包括网络技术的研究、开发;系统制造;文化娱乐经纪人;美术辅导服务;数字动漫制作;数据处理和存储服务;动漫及衍生产品设计服务;计算机技术开发、技术服务等等;', 'success');
