INSERT INTO `company` VALUES (656, '南昌掌灵科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=656', '', '南昌掌灵科技有限公司', '南昌掌灵科技公司是一家从事手机游戏研发和运营的软件公司。公司专注于无线互联网的在线互动娱乐研究、开发和运营。公司产品在中国移动、联通、电信、腾讯、网易、数字鱼、世纪龙等多家平台上运作，并有着良好的用户口碑和经营业绩。', 'success');
