INSERT INTO `company` VALUES (732, '深圳市启赋娱乐有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=732', '', '启赋', '启赋专注于AR/AI新技术领域的娱乐应用开发，致力于结合智能移动等硬件设备，并通过智能抓取和识别，打造独特的增强现实游戏体验、增强现实互动娱乐教学。公司主要开发产品有酷卡系列《大头儿子酷卡宝贝》AR早教类产品 ;互动游戏类《猪猪侠AR虚拟使命》AR射击手游。', 'success');
