INSERT INTO `company` VALUES (736, '深圳市大头互动文化传播有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=736', '', '大头文化', '深圳市大头互动文化传播有限公司成立于2010年10月，是中国领先的社交应用开发和运营商。大头文化目前运营着一系列知名、流行的产品，其中包括疯狂骰子、疯狂十三张等社交类游戏', 'success');
INSERT INTO `company` VALUES (737, '深圳市大梦龙途文化传播有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=737', '', '大梦龙途', '深圳市大梦龙途文化传播有限公司成立于2014年9月份，公司很多人均来自于腾讯，其中核心成员多数为原《御龙在天》成员，我们秉承打造精品游戏的理念，志在PVP国战领域成为领先研发商，专注于强PVP手机游戏的研发。2015年推出的国内首款动作国战游戏《战龙之刃》，因为其爽快的战斗体验、华丽的画面、丰富的玩法，加上亚洲知名女星迪丽热巴加盟代言，获得行业和玩家倾力支持，在线和收入屡创新高，日收入超过100万，目前月流水2000万。', 'success');
INSERT INTO `company` VALUES (738, '深圳市天易互娱信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=738', '', '天易互娱', '天易互娱拥有雄厚的手机游戏运营发行、分销实力。与中国大陆各大手机游戏平台、 应用市场、媒体频道保持密切的合作关系，也是多家厂商及平台的战略合作伙伴。 拥有多款产品成功发行分销的经验，游戏产品发行网络已经覆盖了港澳台， 东南亚、欧美、非洲等地区。旗下产品有《全民格斗王》、《疯狂赛车》、《进化吧皮卡丘》等多款游戏。', 'success');
INSERT INTO `company` VALUES (739, '深圳市安欧科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=739', '', '安欧科技', '深圳市安欧科技有限公司成立于2011年，安欧是移动互联网软件技术及应用服务提供商。', 'success');
INSERT INTO `company` VALUES (740, '深圳市尚愉科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=740', '', '尚愉科技', '深圳市尚愉科技有限公司于2014年8月成立。公司立足于中国市场，致力于移动游戏市场，目标是成为中国最优秀的网络游戏公司之一，成为受用户喜爱的游戏研发商，长久的服务于中国广大的网络游戏玩家。', 'success');
INSERT INTO `company` VALUES (741, '深圳市岚悦网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=741', '', '岚悦科技', '深圳市岚悦网络科技有限公司是国家级高新技术企业， 主要业务为手机游戏自主开发及推广运营。 联合中国手游开发、运营上百款的手机单机游戏和网络游戏。', 'success');
INSERT INTO `company` VALUES (742, '深圳市应美科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=742', '', '应美科技', '深圳应美科技有限公司是主打手机单机游戏的一支充满朝气的生力军，打破传统的单机游戏玩法，创造出新的富有趣味性的休闲游戏。', 'success');
INSERT INTO `company` VALUES (743, '深圳市手心游戏科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=743', '', '手心游戏', '深圳手心游戏科技有限公司是一家专注于移动互联网的高新技术，致力于多款高品质、精细化的数字娱乐产品的开发和运营。目前已有多款获得用户喜爱的休闲游戏上线，并创造了可观的月流水业绩，已经逐渐成为业界知名的手机游戏公司。', 'success');
INSERT INTO `company` VALUES (744, '深圳市拇指游玩科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=744', '', '拇指游玩', '深圳市拇指游玩科技有限公司是一家专业从事移动互联网软件研发、手机网络游戏发行、运营与推广的创新型科技公司。', 'success');
INSERT INTO `company` VALUES (745, '深圳市掌娱互动网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=745', '', '掌娱互动', '深圳市掌娱互动网络有限公司，是一家立足深圳面向全球的专业棋牌手游开发运营商，成立于2014年12月，由平均行业经验超过10年的精英成员组成。《全民雀神》为的首款产品。', 'success');
INSERT INTO `company` VALUES (746, '深圳市掌游互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=746', '', '掌游互动', '深圳市掌游互动科技有限公司成立于2013年4月，是一家专注于为app提供计费解决方案和推广服务的第三方服务提供商。掌游互动以自主研发的应用商城--“掌游乐园”为核心，联合近千家wap网站、app开发者以及内置渠道商为各类广告主提供精准广告服务。目前已经为近600多家广告主提供了推广服务。公司同时拥有多款自研、代理游戏及应用产品，与电信移动联通三大运营商的阅读基地、游戏基地、动漫基地、音乐基地建立紧密的合作关系。', 'success');
INSERT INTO `company` VALUES (747, '深圳市摩艾客科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=747', 'MOIK', '深圳市摩艾客科技股份有限公司', '深圳市摩艾客科技股份有限公司(股票代码：836666)成立于2007年10月。摩艾客一直专注于移动互联网项目的开发与运营。摩艾客除开发手机游戏外更致力于将互联网+互动高科技融入到儿童生活、娱乐、教育产品中，已成为了一家致力于AR（增强现实）&VR（虚拟现实）技术开发与应用的高科技公司。', 'success');
INSERT INTO `company` VALUES (748, '深圳市新纳环球网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=748', 'SUNNET', '深圳市新纳环球网络科技有限公司', 'SUNNET致力从事于手游研发，时刻关注国内外手游行业的趋势发展，坚持以游戏为本，创新三赢的企业发展观，以“新思维、新创意、新概念”的原创发展方针，开拓创新思维，创意优质游戏，打造新概念精品，是SUNNET团队的长远发展规划。', 'success');
INSERT INTO `company` VALUES (749, '深圳市智星互动娱乐有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=749', 'Zstar Games', '智星游戏', '深圳市智星互动娱乐有限公司成立于2015年6月，是一家从事移动终端产品研发和运营的新兴互动娱乐企业。目前，智星互动已精心打造出《智星德州》、《智星斗地主》两款经典棋牌类游戏，以及电子竞技游戏设计软件的开发，并布局海内外市场运营及推广。', 'success');
INSERT INTO `company` VALUES (750, '深圳市暴雪互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=750', '', '暴雪互动', '深圳市暴雪互动科技有限公司 ，是一家拥有自主知识产权的手游研发运营高新技术企业，秉承“玩家需求至上，用户体验至上”的开发理念。', 'success');
INSERT INTO `company` VALUES (751, '深圳市泊众网科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=751', '', '深圳泊众', '深圳市泊众网科技有限公司创立于2004年10月，致力于网络棋牌游戏技术研发及生产、提供可立即上线运营的棋牌游戏，是中国大陆领先的网络棋牌游戏技术服务商。', 'success');
INSERT INTO `company` VALUES (752, '深圳市泰尚科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=752', '', '深圳市泰尚科技有限公司', '深圳市泰尚科技有限公司是一家专注于移动互联网业务的创新型科技企业，愤怒的富翁是该公司开发的一款新型集休闲娱乐一体的移动端创新应用。', 'success');
INSERT INTO `company` VALUES (753, '深圳市派乐地科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=753', '', '派乐地科技', '深圳派乐地科技有限公司（简称派乐地科技）集自主研发、代理发行，投资孵化，IP授权为一体的游戏开发商与运营商。以专业、专注的态度，打造精品游戏；以发展和共赢为宗旨，不断发展和开拓市场。', 'success');
INSERT INTO `company` VALUES (754, '深圳市浩天投资有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=754', 'M-time', '深圳市浩天投资有限公司', '深圳市浩天投资有限公司成立于2001年5月，致力于移动互联网游戏的开发与运营，是中国电信运营商最紧密的合作伙伴之一，建立了全面接入的无线数据业务平台，以优质服务获得了广大用户的青睐。', 'success');
INSERT INTO `company` VALUES (755, '深圳市游梦科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=755', '', '游梦科技', '深圳市游梦科技有限公司（中国移动互联网基地四星合作伙伴，中国移动咪咕游戏 和 电信爱游戏 A级合作伙伴）成立于2014年9月，旗下两家全资控股子公司：广州科宁网络科技有限公司、广州西顺软件科技有限责任公司。目前专注于手机游戏产品设计研发、发行、渠道运营和SDK运营的高科技公司。', 'success');
INSERT INTO `company` VALUES (756, '深圳市瀚游互动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=756', '', '深圳市瀚游互动网络科技有限公司', '深圳市瀚游互动网络科技有限公司是一家专注于高品质移动游戏开发的创业公司。旗下《Tactile Wars》获得苹果全球推荐。', 'success');
INSERT INTO `company` VALUES (757, '深圳市火星网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=757', '', '火星网络', '深圳市火星网络有限公司，成立于2012年11月7日，专注于移动互联网应用、手机软件开发以及手机终端应用等高新技术研究。旗下有《糖果公主3》、《大头儿子2乐园酷跑》、《倒霉熊奇幻之旅》等。', 'success');
INSERT INTO `company` VALUES (758, '深圳市火舌互联科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=758', '', '火舌游戏', '火舌游戏成立于2014年，总部设于深圳，专注于手机游戏的研发和发行，致力于为游戏玩家提供精彩的游戏体验，成立至今，公司依托敏锐的视角，强有力的研发实力，独特的运营理念及方式，已成功开发出数款精品休闲游戏。', 'success');
INSERT INTO `company` VALUES (759, '深圳市灵游互娱股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120127.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=759', 'SoulGame', '灵游互娱', '灵游互娱成立于2012年8月8日，CEO是仇炜华。公司现办公地址位于：深圳市南山区。灵游互娱是一家以移动游戏研发、运营及为核心的互动娱乐企业。2015年初，灵游互娱获国内最顶尖投资机构“深圳创新投资集团”的数千万A轮融资，并于同年十月完成股改，成为股份公司。灵游互娱花费千万代理金，独家代理了3DARPG手游《天堂战记》、《盗墓OL》，并购买了《猪猪侠》相关IP，为公司后期转型3D精品网游奠定了丰厚的IP储备。2015年12月，《泡泡龙亚特兰蒂斯》荣获黑石奖“十佳单机游戏”。2016年1月，灵游互娱仇炜华荣获黑石奖“十佳新锐CEO”。', 'success');
INSERT INTO `company` VALUES (760, '深圳市炫彩酷游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=760', '', '炫彩酷游', '深圳市炫彩酷游科技有限公司是一家专注制作手机游戏的公司，公司坐落在深圳南山科兴科学园，团队成员由80/90后组成，是一家年轻开放并充满活力和热情的好玩的游戏公司。旗下有《刀塔无双》游戏产品。', 'success');
INSERT INTO `company` VALUES (761, '深圳市点乐网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=761', '', '点乐游戏', '深圳市点乐网络科技有限公司（点乐游戏）成立于2013年, 设在互联网行业的最前线城市深圳。点乐游戏一直致力于成为“最好的休闲类游戏开发和运营商”，通过自主研发和多平台精细化运营，为用户提供立体化的互联网互动娱乐体验。', 'success');
INSERT INTO `company` VALUES (762, '深圳市爱点乐科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=762', '', '深圳市爱点乐科技有限公司', '深圳市爱点乐科技有限公司,是一家新兴的中国互联网信息技术公司，主要从事移动平台下的游戏开发和运营 , 致力于成为“最好的游戏开发和运营商”。', 'success');
INSERT INTO `company` VALUES (763, '深圳市爽玩科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=763', '', '爽玩科技', '爽玩科技从棋牌游戏与休闲游戏着手, 并且利用本身在政策上与海内外市场上的资源创造公司利润', 'success');
INSERT INTO `company` VALUES (764, '深圳市猎游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=764', '', '深圳市猎游科技有限公司', '深圳市猎游科技有限公司成立于2013年，是一个创业型移动开发团队，专注于android平台的游戏以及软件的研发，公司致力于为广大互联网用户、手机用户提供休闲竞技及娱乐服务，公司目前已拥有自研成功多款手游产品。', 'success');
INSERT INTO `company` VALUES (765, '深圳市白日梦网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=765', '', '白日梦网络', '旗下产品《蛇蛇争霸》', 'success');
INSERT INTO `company` VALUES (766, '深圳市皇聚游戏开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=766', '', '皇聚游戏', '深圳市皇聚游戏开发有限公司是目前中国领先的棋牌类游戏开发和运营商。皇聚游戏一直致力于成为“最好的棋牌类游戏开发和运营商”，通过自主研发和多平台精细化运营，为用户提供立体化的互联网互动娱乐体验。 在', 'success');
INSERT INTO `company` VALUES (767, '深圳市益玩网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120128.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=767', '', '益玩网络', '深圳市益玩网络科技有限公司成立于2011年5月1日，CEO是Taro。公司现办公地址位于：深圳市南山区。益玩网络拥有自研的E3DE引擎，是集游戏媒体与游戏发行于一身的国内领先网页游戏平台，于2014年获得三七互娱千万级的A轮战略投资。现拥有8000万以上用户，其中手游日分发量500万，与国内1000余家移动游戏研发商、渠道商建立紧密合作，平台月流水突破1.5亿。公司研发的3D页游产品《魔尊》在腾讯平台表现优秀，并获得最高的Ｓ级评级。《魔尊》、《龙回三国》等产品在国内37、腾讯、360等一线平台表现优异。2015年，《魔尊》登陆腾讯游戏大厅，月流水破千万。2016年，推出震撼大作《九天封神》。', 'success');
INSERT INTO `company` VALUES (768, '深圳市禅游科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=768', '', '禅游科技', '深圳市禅游科技股份有限公司（简称禅游科技）是一家专注于打造国际一流休闲竞技娱乐平台的综合服务商。 总部设立在深圳，成立于2010年7月', 'success');
INSERT INTO `company` VALUES (769, '深圳市第一波网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120129.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=769', 'DIYIBO', '第一波网络', '深圳市第一波网络科技有限公司成立于2013年9月2日，代表人物及董事长是付强。公司现办公地址位于：深圳市南山区。第一波游戏是天津颐博数码科技有限公司旗下游戏交互平台。旗下代表游戏包括：《唐门世界》、《莽荒纪》、《绝世天府》、《校花的贴身高手》、《冰火魔厨》等起点白金作者小说改编游戏产品。目前，第一波游戏正联合文化各产业链，积极为网文IP拓展影视、动漫、游戏、时尚、娱乐等全新产业领域，《莽荒纪》游戏、动画剧集、电影、电视剧项目目前都已经进入全面实施阶段。2015年1月，《莽荒纪》页游上线；3月《莽荒纪》尚荒新文化盛典成功召开。', 'success');
INSERT INTO `company` VALUES (770, '深圳市艾德聚乐科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=770', '', '艾德聚乐', '艾德聚乐是一家专注于移动互联网应用及游戏市场的新兴公司，公司产品和服务全面涵盖安卓、IOS、WP等智能平台，涉及游戏研发与发行以及游戏运营等多个方面。公司致力于精品游戏研发，同时积极引进国外精品游戏和代理运营国内高质量游戏，不断的完善和拓展产品线，为广大玩家提供最具吸引力的游戏力作。', 'success');
INSERT INTO `company` VALUES (771, '深圳市艾秀信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=771', '', '深圳市艾秀信息技术有限公司', '成立于2010年，注册资金100W元，总部位于深圳。一家有着雄厚实力的电信增值业务提供商和手机软件开发及无线互联网开发公司，依托自身优势资源，大力开展移动无线终端的应用软件研发', 'success');
INSERT INTO `company` VALUES (772, '深圳市萌蛋互动网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=772', '', '萌蛋互动', '旗下产品《猪来了》', 'success');
INSERT INTO `company` VALUES (773, '深圳市蓝游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=773', '', '蓝游网络', '深圳市蓝游网络科技有限公司成产于2013年6月，总部位于中国深圳，是专业的全球化娱乐内容研发及发行公司。公司旗下拥有深圳蓝游研发、天天游戏平台等众多品牌，同时，作为专业的娱乐内容研发及发行公司，蓝游网络面向全球开展业务，全面拓展海外市场。旗下有《全民盗墓》、《雄霸隋唐》等多款游戏。', 'success');
INSERT INTO `company` VALUES (774, '深圳市谷大网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=774', 'casgame', '谷大游戏', '谷大游戏专注于单机精品游戏运营，旗下有《猪猪侠之百变星战》、《熊出没之疯狂弹射》、《猪猪侠向前冲》等多款精品游戏。', 'success');
INSERT INTO `company` VALUES (775, '深圳市豆悦网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=775', '', '深圳市豆悦网络科技有限公司', '深圳市豆悦网络科技有限公司，是中手游集团旗下全资子公司，是领先的移动游戏开发商与发行商，专注于棋牌游戏的开发与发行。代表作品有《逍遥麻将系列》、《酷玩三张牌系列》棋牌网游等。', 'success');
INSERT INTO `company` VALUES (776, '深圳市轩游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=776', '', '轩游科技', '轩游科技创立于2013年3年，公司位于深圳市宝安区互联网基地附近，专注于手机游戏的研发及运营。距今为止，轩游自主研发的产品有棋牌、休闲等多款不同类型的单机游戏', 'success');
INSERT INTO `company` VALUES (777, '深圳市达趣科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=777', '', '达趣游戏', '深圳市达趣科技有限公司，专注于智能手机游戏研发，深圳市重点软件企业和高新技术企业。达趣游戏已拥有一支具有超强竞争实力的手机游戏开发团队', 'success');
INSERT INTO `company` VALUES (778, '深圳市途人互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=778', '', '途人互动', '深圳市途人互动科技有限公司，成立于2014年底，主营智能手机游戏开发和发行，为移动用户提供优质的手机游戏。', 'success');
INSERT INTO `company` VALUES (779, '深圳市逸游无限科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=779', '', '逸游无限', '逸游无限成立于2014年9月，作为一家新兴的手游发行及运营商，逸游无限目前的业务范围已经涵盖休闲棋牌游戏的研发发行、网游海内外发行及自有流量平台。自2016年第二季度开始，逸游无限将不断推出网游作品以及精品单机。2016年6月，由逸游无限的总公司研发、咪咕游戏发行的跑酷类游戏《泰迪快跑》将与电影《泰迪熊之玩具大战》同期上线', 'success');
INSERT INTO `company` VALUES (780, '深圳市酷炫游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=780', '', '酷炫游', '深圳市酷炫游科技有限公司成立于2012年6月，位于深圳市福田区，是一家专门提供移动互联网应用开发与服务的高科技软件企业。主要从事手游产品的研发，发行，运营；还专注于新兴产业VR产品研发，VR产品内容提供商，工具制造商等。', 'success');
INSERT INTO `company` VALUES (781, '深圳市酷鸽无线技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=781', '', '酷鸽无线', '深圳市酷鸽无线技术有限公司成立于2014年7月，是一家专业从事手机游戏软件开发和运营的移动互联网企业，目前已成功发布《开心跳跳兔之泡泡龙》、《消丫小苹果》、《苹果消消乐》、《天天爱祖玛》、《大牌斗地主》、《怒射吧水果》、《无敌泡泡龙之纵横四海》、《天天泡泡龙》及《保卫部落之合体达人》等20余款休闲游戏。', 'success');
INSERT INTO `company` VALUES (782, '深圳市金卓网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=782', 'KING JOY', '金卓游戏', '深圳市金卓网络科技有限公司正式成立于2013年5月， 目前正致力于使用最新的互联网技术研发手机游戏。', 'success');
INSERT INTO `company` VALUES (783, '深圳市金环天朗信息技术服务有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=783', '', '金环天朗', '深圳市金环天朗信息技术服务有限公司成立于2005年，公司主营业务品牌“蛙蛙游戏”，是业内第一个跨大陆、香港、台湾和澳门两岸四地的轻度休闲网游社交平台。', 'success');
INSERT INTO `company` VALUES (784, '深圳市风车互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=784', '', '风车互动', '风车互动专注于移动游戏的开发及发行，我们由精英的设计团队、强力的开发团队构建而成，全力打造精品游戏，为用户带来无尽的游戏乐趣。努力开创海外市场，让中国的手游也能赢得国外玩家的喜爱。', 'success');
INSERT INTO `company` VALUES (785, '深圳市魂动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=785', '', '魂动科技', '', 'success');
INSERT INTO `company` VALUES (786, '深圳市鱼丸互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=786', '', '鱼丸互动', '鱼丸互动专注于HTML5移动轻游戏的发行和社区化运营，依托旗下的宇宙首家游戏弹幕站(m.5miao.com)和HTML5游戏开发者平台(dev.5miao.com)，连接开发者和用户，为年轻人群提供贴近和有趣的“轻游戏+弹幕”娱乐服务。', 'success');
INSERT INTO `company` VALUES (787, '深圳市麦游互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=787', 'MIGAME', '麦游互动', '深圳市麦游互动科技有限公司是一家集网络棋牌开发、网络棋牌手游开发、运营为一体的新兴高科技企业。现正式面世的棋牌手游产品有：斗牛大亨、麦游斗地主、掼蛋大师。', 'success');
INSERT INTO `company` VALUES (788, '广州科宁网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=788', '', '科宁网络', '广州科宁网络科技有限公司是一家专业游戏开发公司，是集研究、开发、销售于一体的高科技企业。', 'success');
INSERT INTO `company` VALUES (789, '深圳市龙宇天下科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=789', '', '龙宇天下', '龙宇天下科技是国内领先的手机研发，运营公司。龙宇天下科技与国内伙伴广泛开展合作，覆盖了多数发行渠道并与中国移动，中国联通，中国电信建立了深度合作关系，为国内用户提供安全的增值服务。龙宇天下科技拥有完整的产品线，发行了龙珠保卫战，合金生化危机，消灭星星，雷电狂飙，超级百万捕鱼等游戏。', 'success');
INSERT INTO `company` VALUES (791, '苏州尚游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=791', '', '尚游网络', '苏州尚游网络科技有限公司成立于2011年4月，是一家专注于手游研发领域的公司。', 'success');
INSERT INTO `company` VALUES (792, '苏州工业园区丰游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=792', '', '丰游网络', '苏州工业园区丰游网络科技有限公司于2012年创立，是一家专注于智能手机平台的网络游戏公司。丰游科技初创团队均来自国内知名网游公司，由10年从业经验的资深游戏开发人员与管理人员组成。丰游网络科技以智能手机平台网络游戏为方向，以资深的行业经验和完备的运营资源为优势，立志在短时期内成为同行业中的佼佼者。', 'success');
INSERT INTO `company` VALUES (793, '苏州工业园区圣骑士信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=793', '', '圣骑士信息', '苏州工业园区圣骑士信息科技有限公司成立于2015年1月，业务包括制作动漫网络游戏，以及手机游戏开发、代理、发行等。', 'success');
INSERT INTO `company` VALUES (794, '苏州市若水网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=794', 'RUOSHUI NETWORK', '苏州市若水网络科技有限公司', '苏州市若水网络科技有限公司创立于2013年，是一家集大型多人在线网络游戏（MMORPG）研发、全国性网络销售、网络游戏运营和地面宣传推广等为一身的综合性高新技术企业。', 'success');
INSERT INTO `company` VALUES (795, '苏州梅酒网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=795', 'MAKE JOY', '梅酒游戏', '苏州梅酒网络科技有限公司成立于2014年，是一家集手机游戏研发、发行、运营于一体的综合性移动互联网公司。公司已与多家主流渠道开展了深度合作，产品覆盖超过5000万用户，且在2014年度就已实现盈利，具有强劲的发展势头。而几位创始人都是具有十年互联网行业经验的资深从业者，拥有非常强的技术开发、美术设计及产品推广运营能力。', 'success');
INSERT INTO `company` VALUES (796, '苏州番茄互娱信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=796', 'TOMATOJOY', '番茄互娱', '苏州番茄互娱信息科技有限公司于2015年成立，是一家以全球互动娱乐市场为战略目标，专注于移动互联网跨平台游戏研发的创新型公司。公司拥有成熟的移动互联网游戏核心开发技术、具备丰富运营经验的优秀团队，公司业务主要集中在移动（手机）设备、电视设备为载体的电子游戏内容的研发和发行。', 'success');
INSERT INTO `company` VALUES (797, '苏州美生元信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=797', '', '美生元', '苏州美生元信息科技有限公司创立于2013年11月，是专业经营游戏研发、发行及运营的公司。公司一直坚持手机游戏专业研发、发行及运营，锐意创新，始终以提升用户游戏体验及生活乐趣，为用户提供更多更全 的手机游戏为目标，提供互动娱乐业务、移动互联网增值业务及海外游戏运营业务。', 'success');
INSERT INTO `company` VALUES (798, '苏州酷德信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=798', '', '酷德信息', '苏州酷德信息科技有限公司成立于2014年5月，是一家创新型游戏开发公司，公司面向全球市场开发手机游戏产品，核心成员均来自国内顶尖游戏开发厂商，拥有十多年大型网络游戏及手机游戏开发经验。我们的宗旨是为全球玩家提供高端、精品的手机游戏及数字娱乐产品，将最好的游戏内容和最优质的服务提供给全球玩家。', 'success');
INSERT INTO `company` VALUES (799, '苏州金游数码科技有限责任公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=799', 'GOLDEN GAME DIGITAL', '金游数码', '苏州金游数码科技有限责任公司成立于2003年3月，是一家棋牌游戏开发、运营的高科技软件技术公司，致力于网络游戏及相关领域的软件开发工作。自成立伊始，金游专业开发、运营、维护具有本地特色的棋牌类休闲游戏平台。目前为止共有成熟的地方特色游戏100多款，全国通用类棋牌游戏10多款，手机游戏多款。近年来，金游软件凭借对网络游戏的深刻理解、先进成熟的游戏软件产品、深厚的技术积累与良好的行业口碑，与电信级网络运营商广泛合作，提供大型的ICP网络游戏系统，增值网络内容应用平台，以及增值类网络服务基础设施平台的开发和建设，并提供多款手机游戏的合作。', 'success');
INSERT INTO `company` VALUES (800, '草莓互娱（北京）文化有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=800', '', '草莓互娱', '草莓互娱（北京）科技有限公司于2010年1月成立，初期是一家以多媒体互动技术和软件服务外包为基础的高新技术企业，公司于2010年12月尝试介入网页游戏领域，协同北京众诚协作科技开发有限公司共同开发了《大海战》网页游戏，该款游戏已经由国内网游巨头盛大公司收购。经过一年的酝酿，公司正式进军网页游戏运营市场，创建了旗下网站e侠网（www.webxgame.com），并且已于2011年3月31日正式运营了网站首款网页游戏《帝国远征》，并大获成功。', 'success');
INSERT INTO `company` VALUES (801, '芬兰Rovio Entertainment Ltd.公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=801', 'ROVIO', 'Rovio娱乐', 'Rovio娱乐成立于2003年，以手机游戏开发起家，现在是一家颠覆行业的娱乐媒体公司。2009年，Rovio发布了愤怒的小鸟游戏，一款基于智能手机触摸屏的休闲解谜游戏，并因此在2010年以后成为了一种全球现象。愤怒的小鸟游戏不断地在全球刷新成功记录，公司从此迅速扩张至了一系列新的商业领域，包括媒体，衍生品，出版，服务等。', 'success');
INSERT INTO `company` VALUES (802, '萌娱科技（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=802', '', '萌娱科技', '萌娱科技（上海）有限公司于2014年9月成立，是一家游戏研发企业。', 'success');
INSERT INTO `company` VALUES (803, '萍乡射雕科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=803', 'SHEDIAO LIMITED', '射雕科技', '萍乡射雕科技有限公司于2012年成立，公司专注于网页游戏及移动游戏开发和发行。目前，公司已成功发行多款游戏产品，在安卓和ios市场均获得了快速增长。其中，公司旗下的《御天》是以中国历史经典神话为背景的ARPG角色扮演类网页游戏，该游戏是2013年8月上线联运平台包括360、37wan、YYgame等100多家平台，目前月流水已破千万。同时，《一剑灭天》ARPG手游产品从月流水不足100万，到目前Android版月流水在1000万以上，虽然已经是小2年的产品但数据依旧平稳。另外，《战神黎明》自2014年7月上线迄今为止全球总流水将近2亿。在港台地区月流水已超过2000万，2014年12月港台上线初期霸占畅销榜前十长达2月，且在国内、韩国、泰国、马来等国家同样取得傲人的成绩。', 'success');
INSERT INTO `company` VALUES (804, '西安咪修信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=804', 'MISSUGAME', '咪修游戏', '咪修游戏成立于2014年，是一家坚持创新，坚持精品的手游团队，创始人团队平均有6年以上的游戏项目经验，多款产品上线，涵盖端游页游手游各个领域。其中，公司策划开发的手游《入魔三国》、《石器总动员》上市后均受到了良好的市场反响。公司的理念就是要做不“快餐”的、不“数值碾压”的、需要动脑子的游戏。', 'success');
INSERT INTO `company` VALUES (805, '西安应龙网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=805', 'inlone', '应龙网络', '西安应龙网络科技有限公司成立于2015年，是专业的手机游戏研发商及企事业单位管理信息化、业务平台建设的综合解决方案提供商。致力于手机游戏研发，软件业务、互联网平台的开发实施建设和服务。同时，公司拥有一支较强的队伍并和国内多家单位强强联手，并且一贯坚持“信誉第一、客户至上”，以最完善的服务体系和最细致的工作作风，为全球客户提供最优质的服务。', 'success');
INSERT INTO `company` VALUES (806, '西安新浪游戏开发信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=806', '', '西安新浪游戏开发信息科技有限公司', '西安新浪游戏开发信息科技游戏公司，成立于2014年7月，截至目前，公司是西北地区引进的最大创意产业投资项目，我们致力于打造中国覆盖面最大，服务最好，产业链最完善的综合服务平台----www.17g.com。', 'success');
INSERT INTO `company` VALUES (807, '西安磐石信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=807', 'PANSHI', '磐石信息', '西安磐石信息科技有限公司成立于2011年7月，自成立以来，公司致力于游戏研发、游戏美术外包、虚拟现实应用技术开发等业务领域，并不断向周边产业链拓展。另外，在2013年，公司在传统游戏开发技术基础上进行不断创新，将军事、医疗、教育等领域的知识技能等解决行业实际应用的元素植入游戏的核心功能，使游戏在各个行业得以应用而不只是单纯在娱乐领域发展，得到业界的普遍 认同，将游 戏传承文化，游戏提升技能、游戏陶冶情操的理念传播到各个行业，为公司业务市场的拓展提供了巨大的空间。', 'success');
INSERT INTO `company` VALUES (808, '西安蓝岸电子科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=808', 'lanan', '蓝岸', '西安蓝岸电子科技有限公司（蓝岸）成立于2012年，专注于数码娱乐领域，致力于打造业内领先的创新科技企业。公司拥有国际化的团队，汇集了国内外知名游戏公司的资深成员，拥有设计、研发、发行、维护等游戏开发及运营实力，积累了十几款原创和自由版权的游戏产品。同时，公司在VR/AR未来科技应用方面，潜心研究技术，在增强现实应用领域处于国内同行的领先地位。', 'success');
INSERT INTO `company` VALUES (809, '贵州金智网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=809', '', '金智网络', '金智网络成立于2013年，是中国新兴的集手机游戏、手机软件开发为一体的互联网公司。旗下拥有98游戏、Face Visa等平台，总部坐落于贵阳贵安新区电子信息产业园内，杭州子公司位于西湖区IT核心区域。其中，98游戏目前已开发运营多款精品RPG及休闲类游戏，并与App Store、百度、360、应用宝等国内主流渠道合作密切。未来，金智网络将继续本着服务大众、娱乐大众、休闲大众，为玩家在工作之余减轻一份劳顿，增加一分乐趣的服务理念，继续为人们提供更精彩的手机娱乐体验。', 'success');
INSERT INTO `company` VALUES (810, '辽宁巨全网络发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=810', '', '巨合网络', '辽宁巨合网络发展有限公司成立于2007年3月,是一家专业从事网络棋牌游戏开发、运营的高科技软件技术公司。核心技术人员具有专业棋牌开发经验，开发速度在国内属一流，可根据客户需求定制游戏产品和服务。市场核心运营人员具有成功运营国内知名棋牌游戏站点的经验，对于整个棋牌游戏市场有着深刻认识，有着强大市场资源。', 'success');
INSERT INTO `company` VALUES (811, '辽宁巨合网络发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=811', '', '巨合网络', '辽宁巨合网络发展有限公司成立于2007年3月,是一家专业从事网络棋牌游戏开发、运营的高科技软件技术公司。核心技术人员具有专业棋牌开发经验，开发速度在国内属一流，可根据客户需求定制游戏产品和服务。市场核心运营人员具有成功运营国内知名棋牌游戏站点的经验，对于整个棋牌游戏市场有着深刻认识，有着强大市场资源。', 'success');
INSERT INTO `company` VALUES (812, '途游（北京）科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=812', 'Tuyoo', '途游游戏', '途游(北京)科技有限公司（简称途游游戏）成立于2013年2月，是中国成长最快的游戏公司之一。途游游戏以棋牌游戏为核心业务，集游戏研发及发行于一体，致力于为全球用户提供优质精彩的棋牌、游戏、娱乐及服务的互联网公司。其中棋牌产品涵盖途游斗地主、途游德州、麻将、象棋等数十款棋牌种类。玩法新、体验爽、省流量以及良好的适配性为途游游戏赢得了更多移动互联网用户。途游游戏也被用户誉为创新、安全、物有所值的游戏品牌。', 'success');
INSERT INTO `company` VALUES (813, '郑州游戏啦网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=813', '', '游戏啦', '郑州游戏啦网络科技有限公司成立于2015年11月，是一家集研发、运营、服务为一体的游戏服务提供商。公司所研发的手游《人人捕鱼》上线之后，成为了一款广受好评的休闲游戏。', 'success');
INSERT INTO `company` VALUES (814, '郑州百易科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=814', 'Bai Yi Technology', '白易科技', '郑州百易科技有限公司成立于2011年，是一家集研发、运营和销售为一体的网络娱乐软件开发商与运营商。目前百易科技在不断优化自身产品的同时，将推出更多适应市场的互联网娱乐软件，在已经成功开发能与PC互联的手机棋牌游戏平台的同时，将更加进一步拓展产品的终端渠道。在组件完善、优化公司产品的同时，让789棋牌游戏平台出售和游戏运营齐头并进，成功打造属于百易科技的互联网产业链。', 'success');
INSERT INTO `company` VALUES (815, '郑州赛驰计算机科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=815', 'zzinfor', '赛弛科技', '郑州赛驰计算机科技有限公司成立于2011月6月，公司以广告媒体分发、网吧管理辅助软件、游戏研发运营为主线，辅以移动端软件研发推广及地方性门户网站媒体合作，构成了公司强大的媒体业务体系。旗下自主运营的九五网吧大师、企鹅桌面软件、网吧安全浏览器等软件产品覆盖全国网吧PC终端30%以上；比目鱼网吧增值联盟、易腾广告传媒的广告业务覆盖全国，单个广告每日千万次展现，为合作伙伴提供强力的推广和宣传；酷乐手游移动端APP数万台手机终端安装量，为百度、4399、37玩等一线游戏厂商提供游戏营销解决方案，是国内优秀的手机游戏传播媒体。', 'success');
INSERT INTO `company` VALUES (816, '重庆临冬科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=816', '', '临冬科技', '重庆临冬科技有限公司成立于2015年8月，是一家致力于移动媒体业务的企业，成立以来持续专注于移动互联网产品及服务，业务范围涉及无线产品和无线互联网多个领域。', 'success');
INSERT INTO `company` VALUES (817, '重庆博大志远科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=817', '', '博大志远', '重庆博大志远科技有限公司成立于2014年，是一家专注于移动互联网广告平台和游戏联运的创业公司，公司一直致力于为优质客户提供具有核心竞争力、多元化的商业解决方案。我们坚持以客户的需求为导向，持续为客户提供优质的产品体验和增值服务；经过2年多的发展，公司实现了安卓、IOS多个平台；国内、海外多条业务线的协同发展和商业布局。', 'success');
INSERT INTO `company` VALUES (818, '重庆天蝎科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=818', '', '天蝎科技', '重庆天蝎科技有限公司是集研发和运营于一体的互动游戏平台，核心成员均来自国内外一线游戏企业，致力于打造家庭及多人游戏的产品研发和国内外运营。我们始终坚持用户至上，用心服务于客户，坚持用自己的服务去打动客户。', 'success');
INSERT INTO `company` VALUES (819, '重庆市热岛科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=819', 'HEATISLAND  TECHNOLOGY', '热岛科技', '重庆市热岛科技有限公司成立于2010年9月，是一家主营业务为互联网应用开发和网络游戏研发的企业。目前公司具有自主研发的网页游戏引擎，突破了实时碰撞计算、FLASH光影计算、多达几千人的单服承载技术等多项技术壁垒，经济价值极高，可满足多种类型的网页游戏产品开发需求。公司所推出的网页游戏《命运格斗》、手机游戏《龙珠超神传》、《角头大仔》均获得了良好的市场反响。', 'success');
INSERT INTO `company` VALUES (820, '重庆惊世科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=820', '', '惊世科技', '重庆惊世科技有限公司成立于2011年7月，是一家致力于互联网应用和跨移动平台应用的开发商，拥有一支极富互联网/移动平台开发经验的开发团队，擅长各种技术的灵活整合，在前端开发、服务器架构、调优，以及iOS/Android跨平台的各类应用开发领域都处于国内领先水平。产品涉及游戏、社交、艺术、企业管理、金融、教育等多个领域，产品线涵盖完全自主定制的企业级管理系统和网站，基于iOS/Android的企业级定制应用与娱乐应用，是客户信赖的IT服务提供商与开发商合作伙伴。', 'success');
INSERT INTO `company` VALUES (821, '重庆海游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=821', 'HAIYOU', '海游科技', '重庆海游科技有限公司成立于2010年，是一家专注于休闲风格类网页游戏及手机游戏开发及运营的高新科技企业，并立志成为中国最好的互联网互动娱乐内容提供商以及运营商。在积累了多年的运营资源后，海游科技重新整合渠道资源，2014年11月增资到1000万，并对自主开发运营的网页游戏——杀手OL（www.ss911.cn）进行优化整合，推出了杀手OL移动版（IOS、Android）。 同时，公司研发的移动直播软件“分贝直播”2016年7月28日已正式上线，目前注册人数已突破200万，注册主播已突破4万，是一家在重庆市场集聚潜力和竞争力的移动直播软件。', 'success');
INSERT INTO `company` VALUES (822, '重庆轩进软件有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=822', 'XUANJIN SOFTWARE', '轩进软件', '重庆轩进软件有限公司成立于2013年3月，是一家致力于iOS应用及游戏开发的创新型企业。公司拥有一个经验丰富、充满活力的团队。团队主要成员拥有多年海内外工作经验，对移动市场有着深刻的理解，曾开发运营众多顶级iOS应用。', 'success');
INSERT INTO `company` VALUES (823, '重庆迅游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=823', 'soonyo', '迅游', '重庆迅游科技有限公司成立于2009年6月，是一家自主研发端游、页游、手游产品的高新技术企业，公司拥有雄厚的资金基础、专业的研发技术团队、经验丰富的运营团队、人性化服务的客服团队、广泛的市场推广平台，充分保证了公司在不断变化的市场环境中，适应市场的需求，推出符合消费者需求的产品。同时旗下手机游戏产品已在世界各国或地区成功运营。重庆迅游和全国百余家游戏媒体建立深度合作关系，使重庆迅游被越来越多的人群所认识，通过在报刊、电台、电视台等媒体的宣传，将重庆迅游产品战略、品牌战略得以延伸，扩大企业知名度、提高市场竞争力。', 'success');
INSERT INTO `company` VALUES (824, '金华傲盾信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=824', 'ZJDB', '金华傲盾', '金华傲盾信息技术有限公司是一家为客户提供各平台设计解决方案，致力于改善产品用户体验，为企业创造市场核心竞争力，树立企业品牌影响力为宗旨的设计公司。我们有最好的产品和专业的销售和技术团队，我们为客户提供最好的产品、良好的技术支持、健全的售后服务，全体员工的共同努力下，荣获多项荣誉，企业综合素质不断提升。特别是在主营业务（其他软件服务）方面的发展成就获得一致好评。', 'success');
INSERT INTO `company` VALUES (825, '金华市亿博网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=825', '', '亿博网络', '金华市亿博网络科技有限公司于2011年12月成立，是一家专门从事互联网游戏开发及运营的公司。公司的主要产品有pc版集结号游戏中心，以及自主研发的经典捕鱼游戏李逵劈鱼。其中，集结号游戏中心上线以来，受到广大玩家喜爱，高峰在线用户突破5000。', 'success');
INSERT INTO `company` VALUES (826, '金华市盘古信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=826', '', '盘古信息', '金华市盘古信息技术有限公司，成立于2012年，是一家以提供质量可靠的产品和完善服务为目标的高新技术企业！目前公司主要从事电脑软件开发、网络游戏软件开发、互联网游戏平台及网站的运营和维护，公司拥有完整网站运营资质，包括文化部核发的“网络文化经营许可证” 、“增值电信业务经营许可证”等，公司现有员工都是拥有大专以上学历的专业技术人员，是一个充满活力，且具有创新性的团队！', 'success');
INSERT INTO `company` VALUES (827, '金华市网盟网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=827', '', '网盟网络', '金华市网盟网络游戏投资有限公司成立于2015年7月，是一家专业从事网络棋牌游戏开发、手机棋牌游戏开发、网页棋牌游戏开发、软件定制开发、销售及运营支持的高科技软件技术公司。旗下现在经营开心山庄棋牌游戏平台，核心技术人员具有五年以上专业棋牌游戏开发经验，开发速度在国内一流，可根据客户需求定制游戏产品和服务。自成立以来，我们一直以追求一流的技术和服务为目标，并始终处于高速、稳定的发展状态。在销售方面，市场运营人员具有成功运营国内知名棋牌游戏站点的运营经验，对于整个棋牌游戏市场有着深刻的认识，并有着强大的市场资源。', 'success');
INSERT INTO `company` VALUES (828, '银川圣地国际游戏投资有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=828', '', '圣地国际', '银川圣地国际游戏投资有限公司于2014年6月成立，是世界电子竞技大赛（即WCA）永久主办方。主要负责筹办WCA赛事，投资电子游戏（如页游、手游等）及相关产品。', 'success');
INSERT INTO `company` VALUES (829, '锐战软件（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=829', '', '锐战网络', '上海锐战网络科技有限公司（以下简称\"锐战网络\"）成立于2009年8月，是一家以网页游戏为发展起点，始终专注于自主研发的本土网页游戏研发公司。公司旗下知名精英研发团队\"傲世堂\"，以\"打造超一流游戏，提升行业整体水平,推动国民页游发展\"为己任，在近两年推出了《傲视天地》、《征战四方》、《攻城掠地》等具有革新意义SLG的网页游戏，获得了业界及玩家的一致好评。', 'success');
INSERT INTO `company` VALUES (830, '镇江微端网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=830', 'VD GAME', '微端网络', '镇江微端网络科技有限公司成立于2012年1月,公司主营业务为大型MMORPG网络游戏的开发,微端和页游、手机游戏、行业软件的开发等。公司现有技术开发人员100多名，团队实力雄厚、运营思路超前，是一支以科技为先导、经验丰富又敢于创新的年轻团队。旗下有多款运营月充值过千万的产品，其中《屠龙传说》获得2012年十佳页游，每月充值突破5000万。', 'success');
INSERT INTO `company` VALUES (831, '镇江梦工场网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=831', '', '梦工场网络', '镇江梦工场网络科技有限公司成立于2008年，是一家从事网络动漫游戏研发的公司。', 'success');
INSERT INTO `company` VALUES (832, '镇江福动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=832', '', '福动网络', '镇江福动网络科技有限公司成立于2016年5月，是一家从事网络游戏开发的公司。', 'success');
INSERT INTO `company` VALUES (833, '陕西至尊网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=833', 'supreme network', '至尊网络', ' 陕西至尊网络科技有限公司成立于2006年3月，是一家专业从事互联网产品运营，互联网产业开发以及互联网应用平台建设、维护的综合型高薪技术企业。经过近10年的发展，公司锻炼出了一支高素质、高效率的管理团队。在努力发展游戏产业的同时，也加大了对互联网互动平台等其他业务领域的拓展。如今，至尊网络的业务范围涵盖到互联网、软件开发、应用开发等多个方面，已经成长成为一个是一个集研发、运营、销售、服务于一体互联网应用平台、软件解决方案提供商。目前，公司整合各项资源，在逐步加大PC互联网市场开发的同时开始拓展手机端互联网市场的开发，着手打造符合中国人特性喜好的互联网产品，公司拥有强大的技术开发优势，有志成为中国西北最大的网络游戏开发商和运营商之一。', 'success');
INSERT INTO `company` VALUES (834, '陕西风云在线信息网络有限责任公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=834', '', '风云在线', '陕西风云在线信息网络有限责任公司成立于2004年4月，是一家以网络游戏的研发和运营为主的高科技企业。本公司于2005年2月正式推出独立研发，拥有完全自主知识产权的大型网络棋牌游戏——“乐透游戏世界”。截至2011年末，“乐透游戏世界”的有效注册用户超过200万人，稳定用户50余万人，同时在线人数达到25000余人。', 'success');
INSERT INTO `company` VALUES (835, '霍尔果斯小小橙软件开发有限责任公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=835', '', '小小橙', '霍尔果斯小小橙软件开发有限责任公司成立于2015年10月，是一家从事手游运营的公司，旗下运营的手游《战火与秩序》目前在两大手机平台上都广受好评，未来也将会把更多的优质游戏带给玩家。', 'success');
INSERT INTO `company` VALUES (836, '青岛万化网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=836', '', '万化网络', '青岛万化网络技术有限公司成立于2013年7月,公司早期以外包形式迅速自负盈亏,在积累了一定资金后于2014年5月开始正式研发大型中重度网游《英雄与迷宫》，目前取得了良好的成绩，并且新游也有开发之中。', 'success');
INSERT INTO `company` VALUES (837, '青岛热蜂互动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=837', '', '热蜂互动', '青岛热蜂互动网络科技游戏公司成立于2016年，是一家从事游戏开发、运营的公司。', 'success');
INSERT INTO `company` VALUES (838, '青岛超联网通信工程有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=838', '', '超联网', '青岛超联网工程有限公司成立于2010年、是通信集通信，网络游戏开发运营与一体的技术型公司。超联网以创造优秀的互动娱乐产品，打造有高质量的网游精品为目标不懈努力。公司核心团队成员均来自国内知名网络公司，有深厚的技术累计和成熟的管理经验。', 'success');
INSERT INTO `company` VALUES (839, '韩国Gravity公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=839', 'Gravity', 'Gravity', '韩国Gravity公司创办于2000年，是韩国第一代的游戏公司。公司凭借着开发《仙境传说》而闻名，并且在2005年2月顺利成为韩国国内第一家在纳斯达克上市的网络游戏公司而受到关注。2011年，公司运营《Have》、《Canaan》等新网络游戏，逐渐扩大游戏运营。', 'success');
INSERT INTO `company` VALUES (840, '韩国JOYCITY公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=840', 'JOYCITY', 'JOYCITY', '韩国JOYCITY公司是韩国著名的游戏开发公司，公司以优秀的开发能力和别具一格的创意著称。公司所制作的全世界第一款街头风格的篮球游戏《街头篮球》风靡全球，取得良好的市场成绩，并且成为了第一代韩国专业游戏公司的一员。同时，在韩国手机游戏市场形成初期，凭借社交经营类游戏《天空之城》取得良好成绩，成功的迈出了移动游戏市场的第一步。另外，在2014年起至今开始进行VR与主机游戏的研发。', 'success');
INSERT INTO `company` VALUES (841, '韩国MGAME公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=841', 'MGAME', 'MGAME', '韩国Mgame为韩国著名的游戏开发商之一，于1997年3月成立，位于韩国首尔。公司以把所有乐趣添加到网络游戏中为目标，开发着面向世界的网络游戏。目前在中国、日本、美国等地创办分公司的MGAME公司，代表作包括了《热血江湖》、《热血江湖2》、《玛雅战记》等。', 'success');
INSERT INTO `company` VALUES (842, '韩国NCsoft公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=842', 'NCSoft', 'NCSoft', 'NCSoft是韩国最具代表性的网络游戏公司之一，成立于1997年。公司以提供全世界玩家们新经验和快乐为目标，不断地发展。旗下游戏品牌有剑灵、激战2、荒野星球、永恒之塔、天堂、天堂2、T R、City等。游戏发行区域覆盖在美国、日本、中国大陆、中国台湾、香港等国家和地区，并不断的加快全球化的步伐。', 'success');
INSERT INTO `company` VALUES (843, '韩国NHN Entertainment Corporation', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=843', 'NHN Corp.', 'NHN', '韩国NHN公司是韩国最优秀的网络企业之一，拥有韩国排名第一的检索门户网站Naver和最大网络游戏门户网站Hangame，在韩国IT行业排名调查中多年来稳居第一位，正在成为亚洲最大的网络企业。在同行业中，NHN 最先进军海外市场开展了IT相关服务。目前在日本、中国和美国建立了稳固的事业基石。其中，NHN China成立于2004年1月，是在中国提供网络游戏与信息的互联网公司。其在2004年6月开始与海虹控股合作经营联众世界. 作为中国最大的网络游戏门户之一，联众世界与NHN Corp.、NHN日本和NHN美国合作推广游戏。', 'success');
INSERT INTO `company` VALUES (844, '韩国网禅公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=844', 'WEBZEN', '网禅', '韩国网禅公司是韩国知名的网游公司，于2000年于韩国首尔创立，初期为一小型游戏开发设计工作室，凭借著2000年推出的3D化多人线上游戏Mu Online（《奇迹MU》）一举成名，打开国际市场，并多次在E3、China Joy等大型多媒体展览场展出，积极将自身发展成为国际知名游戏公司。2003年先后在高斯达克和纳斯达克上市，直到2010年因经济情况不佳从纳斯达克退市。2008年，最大股东变为NHN Games。现在的网禅为原网禅与NHN Games在2010年合并后的公司。', 'success');
INSERT INTO `company` VALUES (845, '飞石互动（北京）科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=845', '', '飞石互动', '飞石互动（北京）科技有限公司是中国领先的专业移动社交游戏开发商和运营商之一，创立于2008年。目前公司致力于移动互联网，尤其是Android系统上的游戏开发，因为有着良好的前瞻性及创新能力，现已成为中国领先的社会化游戏开发运营商，推出了多款广受欢迎的手游作品。公司是经国家相关部门认定的双软企业，拥有主知识产权，资质齐全。', 'success');
INSERT INTO `company` VALUES (846, '马耳他King.com,Limited公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=846', 'King', '马耳他King.com,Limited公司', '马耳他King.com,Limited公司成立于2003年，主要于Facebook开发应用游戏程式，以及博弈游戏，游戏发行全世界各个国家和地区，并在多种平台上运营(网页、Facebook、 iOS、Android)。主要办事处马耳他、马尔默、布加勒斯特、伦敦（总部）、旧金山、巴塞罗那、汉堡，以及斯德哥尔摩。员工数目为约300人。', 'success');
INSERT INTO `company` VALUES (847, '马鞍山中尧网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=847', '', '中尧网络', '马鞍山中尧网络科技有限公司是专业从事游戏开发，运营和数字娱乐的高科技公司，拥有中国领先的互动娱乐运营平台玩家网。作为领先的游戏媒体门户企业，公司拥有规范的互联网公司管理体系、较强的创新能力和研发能力、专业的内容编辑团队以及经验丰富的营销团队，向广大玩家提供游戏资讯和交流沟通等全方位内容和服务，并全面整合游戏开发商、游戏运营商、地方网吧、玩家社群等各种资源，与多家一线游戏厂商、媒体、网站建立了长期战略合作伙伴关系，关注游戏产业每一个环节，致力于为中国整个游戏产业提供专业化服务。同时，公司还涉足游戏研发，所开发的《明星大亨OL》是国内第一款融合了商业策略、明星养成和竞技PK等诸多时尚元素的模拟经营类网页游戏，获得了相当好的成绩。', 'success');
INSERT INTO `company` VALUES (848, '黑泉信息技术（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=848', 'HQ Games', '黑泉游戏', '黑泉信息技术（上海）有限公司（黑泉游戏）成立于2014年，是一家专注于游戏产品研发与发行的高科技公司。在2016年公司推出的主力产品是军事题材策略游戏《闪电突袭》，目前已正式登陆中国区App Store。《闪电突袭》的国际化版本，也将陆续登陆多个国家和地区，为全球玩家提供高品质的军事游戏产品。', 'success');
INSERT INTO `company` VALUES (849, '齐游网络科技（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=849', 'WiseGame', '齐游网络', '齐游网络成立于2013年3月，是一家集游戏研发、运营、发行为一体的高质量的互动娱乐公司，总部位于中国上海，拥有经验丰富的董事会、雄厚的资本实力、强大的研发能力、娴熟的运营能力、精湛的技术支持能力，由一支充满活力的管理团队带领，致力于为游戏玩家和行业合作伙伴提供良好的体验。同时，公司所开发的手游作品不仅在国内受到欢迎，而且我们还将自己开发的游戏授权给世界各地的发行商合作伙伴，这些合作伙伴位于新加坡、菲律宾、马来西亚、泰国、土耳其、巴西等国家和地区。', 'success');
INSERT INTO `company` VALUES (850, '龙掌网络科技（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=850', '', '龙掌网络', '龙掌网络科技（上海）有限公司成立于2015年。研发团队由多位资深游戏从业者组成，平均从业年限超过10年。制作人曾是《火影OL》主策，团队研发人员担任过《部落守卫战》《名将三国》《街机三国》等多个成功项目的核心开发人员。', 'success');
INSERT INTO `company` VALUES (858, '互爱（北京）科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120130.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=858', 'Hoolai Games', '胡莱游戏', '互爱(北京)科技股份有限公司是一家网络游戏研发、运营和服务公司，于2011年成立，代表人物及现CEO为乔万里，互爱(北京)科技股份有限公司是腾讯平台领先开发商之一，产品主打“胡莱(Hoolai)”游戏品牌。总部位于北京朝阳区。公司凭借着原创的战争策略社交游戏《胡莱三国》，在腾讯平台上一举创下了超千万日活跃用户，以及后期研发的ARPG类游戏《斩仙》，曾创下月收入过亿流水的业界记录，每一款产品皆有胡莱团队的独特设计理念。之后，陆续推出了《御天》、《焚天之怒》、《热血归来》、《龙破九天》等网页游戏精品。除了网页游戏产品外，还研发出了《新神曲》、《全民斩仙》、《一剑灭天》及《寻龙诀》等多款手机游戏和微端游戏。2011年12月，公司荣获创业帮“2011中国年度创新成长企业100强”。2012年3月，公司荣获艾瑞咨询集团“2011年度移动互联网创新力产品奖”。2013年4月，公司荣获金页奖“2012十佳网页游戏研发公司”。2014年1月，公司荣获“2013年度金口袋最佳RPG手游奖”。2015年12月，公司获得donews最佳研发团队奖。2016年万达院线26亿并购互爱互动。', 'success');
INSERT INTO `company` VALUES (860, '北京白鹭创想科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120132.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=860', 'Egret', '白鹭时代', '北京白鹭创想科技有限公司成立于2014年2月1日,代表人物及董事长是陈书艺,公司现办公地址位于：北京市朝阳区望京阜荣街悠乐汇商业C座321室。北京白鹭时代信息技术有限公司（简称白鹭时代），是全球最大的HTML5一站式移动技术和服务提供商，致力于为移动互联网全行业提供技术解决方案与服务，携手行业伙伴共建开放、务实、繁荣的HTML5移动生态系统。白鹭时代于2014年2月创立于北京，同年11月获得顺为资本千万美元A轮融资。2015年5月,获得深创投和经纬创投等机构B轮融资；2016年3月，成功登陆新三板（股票代码836615）。', 'success');
INSERT INTO `company` VALUES (861, '北京搜狗信息服务有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120133.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=861', 'Sogou', '搜狗', '北京搜狗信息服务有限公司成立于2004年8月3日,代表人物及董事长是王小川,公司现办公地址位于：北京市海淀区中关村东路1号院搜狐网络大厦15层。搜狗是中国互联网领先的搜索、输入法、浏览器和其它互联网产品及服务提供商。从2004年8月搜狐公司推出全球首个第三代互动式中文搜索引擎——搜狗搜索以来，历经十载，搜狗搜索已发展成为PC端搜索三强之一，移动搜索排名第二。根据艾瑞咨询2015年8月数据，搜狗PC用户规模达5.21亿，仅次于腾讯，成为中国第二大互联网公司。移动端APP用户仅次于腾讯，成为中国互联网快速发展的标杆性企业。', 'success');
INSERT INTO `company` VALUES (862, '北京天马时空网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120134.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=862', '', '天马时空', '北京天马时空网络技术有限公司成立于2010年,代表人物及董事长是刘惠城,公司现办公地址位于：北京海淀区上地七街1号。天马时空专注网络在线游戏领域，于2014年推出首款MMORPG手游《全民奇迹MU》，一举成为年度现象级产品，天马时空也迅速跻身手游行业的领先者行列。天马时空于2015年加入掌趣集团，目前立足于国内市场，同时积极布局海外，秉持着精品化研发与专业化发行一体化理念，致力于为玩家持续贡献出好玩的高质量游戏。2016年4月，《全民奇迹MU》上线俄罗斯地区，成功进入各大榜单前列。2016年7月，《全民奇迹MU》在欧美等更多地区上线。', 'success');
INSERT INTO `company` VALUES (863, '北京百度多酷科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120135.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=863', '', '百度多酷', '北京百度多酷科技有限公司成立于2011年7月26日,代表人物是李明远,公司现办公地址位于：北京市海淀区上地十街10号1幢一层AE。百度多酷是百度基于手机游戏产品向用户提供娱乐服务的平台，聚合了百度手机助手、百度91助手、安卓市场、百度游戏等高人气分发资源，坐拥百度搜索、手机百度、Hao123、百度视频、百度贴吧、口袋巴士、91手机娱乐门户、安卓网、18183等重要入口，形成“搜索引擎+应用商店+媒体社区”的多核发行模式。通过与国内外数千家移动游戏研发商、发行商的良好合作，承载从单机到网游，从动作、休闲到策略、卡牌等全类型移动游戏的运营与推广，旨在第一时间为用户提供最精彩的掌中娱乐服务。据易观智库发布的《2014年第一季度中国移动游戏运营平台监测报告》显示，百度多酷用户覆盖份额达28.7%，APP日均启动次数达3209.08万次，两项核心指标双双位居行业第一，成为目前手游分发与代理运营市场中最具竞争力的参与者。', 'success');
INSERT INTO `company` VALUES (865, '北京盖娅互娱网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120137.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=865', 'GAEA', '盖娅互娱', '北京盖娅互娱网络科技股份有限公司成立于2014年10月,代表人物及CEO是王彦直,公司现办公地址位于：北京市朝阳区。盖娅互娱是一家坚持产品精品化的全球研运一体型游戏公司。旗下代表游戏包括：《自由之战》、《仙剑奇侠传》、《轩辕剑之天之痕》、《百将行》、《爆爆堂》、《雏蜂-尖兵少女》等。2015年7月，盖娅网络正式登陆新三板资本市场，并被认为是新三板最具潜力的互动娱乐公司之一。9月底，盖娅网络在登陆新三板后相关操作进展顺利，正式更名为“盖娅互娱”。2016年7月18日，盖娅互娱和《巫师 3：狂猎》开发商 CD Projekt RED 宣布合作，双方将联手把《巫师：昆特牌》引进中国大陆。', 'success');
INSERT INTO `company` VALUES (866, '北京爱乐游文化发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120138.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=866', '5agame', '爱乐游', '北京爱乐游文化发展有限公司成立于2015年9月8日,代表人物是CEO孟洋,公司现办公地址位于：北京市海淀区。北京爱乐游是专业的移动游戏及泛娱乐内容提供商和运营商。公司自成立以来，便专注于泛娱乐产品的研发、发行与运营。其自研手机游戏产品《雷霆战机》曾经连续两个月进入全球iOS游戏收入排行榜前十位，其代理手游产品《雷霆舰队》上线次日便荣耀登顶App Store付费榜榜首。北京爱乐游凭借其卓越的研发能力和业务运营能力，逐渐发展成为国内最优秀的泛娱乐产品研发和发行商之一。2016年，雷霆战争三部曲之收官作《雷霆战争》正式推出，雷霆战机系列泛娱乐布局正式开启，包括泛娱乐衍生品、小说漫画、电影等。', 'success');
INSERT INTO `company` VALUES (867, '北京爱奇艺科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120139.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=867', 'iQIYI.COM', '爱奇艺', '爱奇艺成立于2010年4月22日,代表人物是创始人兼CEO龚宇,公司现办公地址位于：北京海淀区。爱奇艺(iQIYI.COM)是全球领先的提供海量、优质、高清的网络视频服务的大型视频网站。影视内容丰富多元,涵盖电影、电视剧、动漫、综艺等方面。2013年5月7日，百度宣布3.7亿美元收购PPS视频业务，并将PPS视频业务与爱奇艺进行合并，PPS将作为爱奇艺的子品牌运营。合并完成后，龚宇将出任新爱奇艺公司CEO，负责新公司的统一管理。2016年2月12日，百度宣布，公司董事会接到公司董事长兼CEO李彦宏和爱奇艺CEO龚宇的初步非约束性收购提议，计划收购爱奇艺100%的股份。2016年7月25日，百度董事长兼首席执行官李彦宏和爱奇艺首席执行官龚宇代表买方财团致信百度董事会，宣布撤回今年2月提出的爱奇艺私有化要约。2016年12月1日，爱奇艺“新电影主义”自制网络电影战略合作发布会在京召开。', 'success');
INSERT INTO `company` VALUES (868, '广州四三九九信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120140.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=868', 'Web.4399.com', '4399', '四三九九网络股份有限公司成立于2002年，代表人物是董事长兼总经理骆海坚，副总经理李兴平，公司现办公地址位于：福建省厦门市。4399一直以优秀的整体形象、数量庞大的精品小游戏、动漫作品赢得众多用户的钟爱，拥有超过60%以上的用户是通过朋友介绍方式访问4399，忠实度很高。从alexa数据来看，4399流量稳定在世界排名400多名。游戏类型丰富，如即时战斗类、回合制战斗类、策略类、休闲竞技类、动作类、模拟经营类等等，可满足不同用户群体的需求。4399网页游戏平台运营的产品以高度的可玩性和趣味性、真实爽快的战斗感、精致的美术画面、炫丽的战斗特效、先进稳定的技术支持，赢得了广大用户的喜爱。', 'success');
INSERT INTO `company` VALUES (869, '北京艺游天下信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120141.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=869', 'Euplay', '艺游天下', '北京艺游天下信息技术有限公司成立于2014年1月1日,董事长是任玲玲,公司现办公地址位于：北京市石景山区。北京艺游天下信息技术有限公司是一家集研发、发行、运营为一体的移动游戏开发商与运营商。旗下代表游戏包括：《羞羞的魔女》、《pokemon移动版》、《魔界统一战》等。目前分别在深圳、成都、上海设有分公司。北京艺游天下信息技术有限公司专注于移动游戏的开发及发行，并充分整合自身强大运营体系、立体化的营销能力和卓越的技术优势，建立了丰富的代理发行储备，拥有复制成功的能力。', 'success');
INSERT INTO `company` VALUES (870, '上海黑桃互动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120142.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=870', 'Heitao.com', '黑桃互动', '上海黑桃互动网络科技有限公司成立于2014年3月21日,CEO是王进强,公司现办公地址位于：上海市浦东新区。黑桃互动公司主营业务为游戏，同时开展影视娱乐、直播、VR内容研发、艺人经纪等多平台互动娱乐业务。目前已经成功发行《新大主宰》、《古龙群侠传》、《莽荒纪》等游戏，并即将上线《犬夜叉》、《新世纪福音战士OL》等多款手游作品。旗下51游戏平台已成功研发并发行了《铁血皇城》、《街机武侠》等口碑级游戏产品。2016年1月黑桃互动在第五届“金鹏奖”中国原创Android游戏评选大赛中，荣获“五星运营手游厂商奖”和“最佳原创合作奖”，旗下《新大主宰》手游，获得“最佳IP改编网络游戏奖”。', 'success');
INSERT INTO `company` VALUES (871, '北京小奥科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120143.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=871', '', '小奥科技', '北京小奥科技有限公司成立于2007年1月1日,CEO是孙健,公司现办公地址位于：北京市海淀区。北京中科奥科技有限公司是一家集手游研发、运营与推广于一体的综合型游戏企业。旗下代表游戏包括：《3D摩托飞车》、《全民枪神》。公司与腾讯、360、小米、百度、华为、联想、爱奇艺等数百家互联网渠道、手机厂商渠道及垂直渠道建立了长期深度的合作关系，与中国三大通信运营商（移动、电信、联通）建立了战略合作伙伴关系，曾被文化部、工信部、电信、微软授予“中国十佳原创手机游戏企业”、“TOP手机游戏企业”、“优秀动漫企业”、“战略合作伙伴”、“最佳合作伙伴”等称号。2016年，小奥与美国EA Chillingo、日本SEGA等国际知名游戏公司达成合作，正式建立起“小奥”一线游戏品牌。2016年，小奥与天娱互动、金港汽车，成立国内首个赛游竞技品牌，共同举办GT 2016赛事。2016年3月，小奥完成新三板挂牌，股票代码836031,宣告小奥品牌进军资本市场。', 'success');
INSERT INTO `company` VALUES (872, '成都好玩一二三科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120144.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=872', '', '好玩一二三', '成都好玩一二三科技有限公司成立于2012年2月2日,CEO是赵琼玉,公司现办公地址位于：成都市高新区。好玩一二三立志成为国内最优秀的手游研发及发行公司，为用户提供轻、快、好的游戏内容。该公司曾先后推出了《凡仙》、《小师妹》、《战龙之刃》等知名手游产品，并在国内各大平台和海外市场取得优秀的运营业绩。旗下手游运营平台553.com发展迅猛，成功跃升国内准一线手游平台，移动客户端拥有数十万活跃用户。', 'success');
INSERT INTO `company` VALUES (873, '北京中清龙图网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120145.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=873', 'LONGTUGAME', '龙图游戏', '北京中清龙图网络技术有限公司成立于2008年1月1日,董事长是杨圣辉,公司现办公地址位于：北京市朝阳区。龙图游戏2014年成功代理并发行手机游戏《刀塔传奇》，创造了手机游戏的行业神话。公司代表游戏包括：《刀塔传奇》、《山口山战记》、《CF穿越火线》、《白小飞大冒险》。业务范围涉及网页游戏、手机游戏、APP、动漫、开发者平台、互联网泛娱乐平台等领域，已经发展成为一家布局全球化、业务多元化、产品多样化的生态型互联网公司。', 'success');
INSERT INTO `company` VALUES (874, '上海雪鲤鱼计算机科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120146.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=874', '', '雪鲤鱼', '上海雪鲤鱼计算机科技有限公司成立于2005年1月1日,董事长是俞思敏,公司现办公地址位于：上海市徐汇区。上海雪鲤鱼计算机科技有限公司凭借出色的游戏研发能力，成功打造了如《鲤》、《末日731》、《灵翼战机》、《赛车总动员》、《加勒比海盗》等国内外知名游戏产品。时至今日，已累计拥有用户超过2亿，用户覆盖全国31个省市以及部分海外国家，活跃用户超过700万。和国内200多家安卓应用商店建立合作关系，并和其中50多家保持着紧密良好的合作关系。', 'success');
INSERT INTO `company` VALUES (875, '广州爱九游信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120147.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=875', '', '九游', '广州爱九游信息技术有限公司成立于2013年10月9日,代表人物及董事长是俞永福,公司现办公地址位于：广州市天河区。九游网集媒体与游戏分发业务于一身，代表游戏包括《生死狙击》、《极无双》、《放开那三国2》等。九游依托于UC浏览器这一拥有超过4亿用户的全球最大移动浏览器平台，充分利用用户资源优势和游戏平台相关技术优势，为广大开发者提供了一个强大的游戏发行平台及在线交互平台。目前九游平台月活跃用户超过6000万，已经上线数万款游戏，有上千个合作伙伴与九游联合运营。', 'success');
INSERT INTO `company` VALUES (876, '掌上纵横信息技术（北京）股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120148.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=876', '', '掌上纵横', '掌上纵横成立于2010年，现CEO是刘仕儒，公司现办公地址位于：北京市东城区。掌上纵横是一家主打影游联动、移动游戏O2O的公司，主营影视、明星、体育版权运营及游戏的研发和发行。致力于成为集娱乐内容生产、传播、消费为一体的新型泛娱乐、泛体育综合体。掌上纵横旗下代表游戏包括：《新蛮荒纪》、《天书奇谈》、《葫芦娃》、《大闹天宫：悟空》、《兰陵王》、《魔范学院》等。2015年4月发行手游《范冰冰魔范学院》，2015年8月获郭敬明小说《幻城》双端游戏改编授权，2015年9月发行手游《琅琊榜》，2015年10月公司新三板上市。', 'success');
INSERT INTO `company` VALUES (877, '竞技世界（北京）网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120149.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=877', '', '竞技世界', '竞技世界是国内领先的棋牌游戏开发商和运营商，公司成立于2007年12月，代表人物是杨林。竞技世界以研发和运营棋牌游戏平台JJ比赛为核心业务，是中国竞技棋牌的缔造者和领军者。公司秉承“弘扬棋牌文化、汇聚天下高手、享受竞技快乐”宗旨，开展了多届线下及线上玩家比赛，在棋牌游戏用户群中口碑颇丰。除此之外，竞技世界还涵盖了5599网页游戏平台、JJDota对战平台、3399小游戏平台、无线应用等互联网业务，为玩家日常生活创造全方位的快乐。2015年11月23日，JJ比赛注册用户累计达到3亿。2015年JJ斗地主荣获金翎奖“玩家最关注的移动网络游戏”奖项、金立易用汇“最佳棋牌类游戏奖”、魅族游戏中心“最具生命力手游奖”。', 'success');
INSERT INTO `company` VALUES (878, '拓维信息系统股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120150.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=878', 'Talkweb', '拓维信息', '拓维信息成立于1996年，代表人物及董事长是李新宇，公司现办公地址位于：湖南省长沙市。拓维信息凭借脍炙人口的《愤怒的小鸟》系列移动游戏，在国内休闲移动游戏领域取得了不小的成绩。2013年9月9日，与美国艺电(ElectronicArts,简称“EA”)签订运营合作协议，正式获得《植物大战僵尸2》运营代理权。公司的代表游戏还包括《愤怒的小鸟变形金刚》、《全城酷跑小时代版》、《刀锋酷跑》、《3D极品赛车》、《愤怒的小鸟泡泡大战》、《全民蜀山OL》等。', 'success');
INSERT INTO `company` VALUES (879, '掌游天下（北京）信息技术股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120151.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=879', 'ZPLAY', '掌游天下', '掌游天下成立于2009年5月，代表人物及CEO是何鹏，公司现办公地址位于：北京市海淀区。依托强有力的发行能力获得巨大可控流量，成功构建全球移动大数据运营平台，实现高效的流量变现模式。掌游天下凭借《消灭星星》、《长腿爸爸》、《魔灵战纪》、《印第安大冒险2》、《弹弹猫》、《将是尖叫》等简洁而不失灵活的代表作品，迅速抢占市场份额。秉承“全球一体化，全领域发展”的理念，依托敏锐的视角、强有力的发行渠道及独特的运营方式，将旗下产品分消30多个地区。2015年，掌游天下在新三板正式上市。2016年，掌游天下继续发力轻度休闲类手游，并不断推出新品，更新玩法内容，旗下发行的打砖块题材休闲游戏《BB弹》，又一次获得了非凡成绩，被iOS连续多图多位置推荐一周左右。', 'success');
INSERT INTO `company` VALUES (881, '奥飞娱乐股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120153.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=881', 'ALPHA', '奥飞娱乐', '广东奥飞动漫文化股份有限公司成立于1993年，代表人物及董事长是蔡东青，公司现办公地址位于：北京市。广东奥飞动漫文化股份有限公司以发展民族动漫文化产业为主导,前身为中国玩具行业领导品牌：广东奥迪玩具实业有限公司。2015年8月11日奥飞动漫9亿收购有妖气，实现内容创作、影视、游戏、媒体、消费品等全生态领域发展，致力构筑东方迪士尼。2016年，投资的国产电影《美人鱼》成为内地票房冠军，投资的好莱坞影片《荒野猎人》全球票房飘红，斩获多项重量级奖项。', 'success');
INSERT INTO `company` VALUES (882, '中国手游娱乐集团有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120154.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=882', '', 'CMGE中手游', 'CMGE中手游成立于2007年，代表人物及CEO是肖健，公司现办公地址位于：北京市朝阳区。CMGE中手游于2012年9月25日登陆美国纳斯达克，股票代码为“CMGE”。CMGE中手游是国内首家登陆纳斯达克资本市场的手机游戏公司，并在极短的时间完成私有化。公司及旗下游戏品牌\"胜利游戏\"自主研发的游戏产品超过450个。代表游戏包括：《新仙剑奇侠传》、《暴风战舰》、《决战沙城》、《航海王强者之路》、《倚天屠龙记》、《火影忍者-忍者大师》等。公司多次荣获“最佳移动平台网络游戏”、“最佳手机网络游戏”、“中国十大最受欢迎民族手机游戏”、“最佳手机游戏开商”等国内重大手机游戏类和企业类奖项。2016年9月，胜利游戏获评腾讯应用宝“飞跃新星奖”，旗下《倚天屠龙记》手游获得“年度十大热门游戏奖”。2016年10月，胜利游戏凭借着2016年在精品游戏发行以及全球性IP引入的卓越贡献，荣膺“易观之星”大奖。2016年11月，胜利游戏在GMGC全球移动游戏联盟举办的第五届全球移动游戏开发者大会暨天府奖盛典中，荣获十佳发行商奖。', 'success');
INSERT INTO `company` VALUES (883, '北京玩蟹科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120155.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=883', 'Playcrab', '玩蟹科技', '玩蟹科技成立于2009年，代表人物是叶凯，公司现办公地址位于：北京市海淀区。玩蟹科技致力于全球移动互联网游戏产品的开发与运营，旗下产品涉及历史、武侠、魔幻等多种题材，代表作品包括：《穿越吧！主公》、《大掌门》、《拳皇98终极之战》等。其中，《大掌门》手游自上线以来长期保持在AppStore、iOS越狱版及各大Android渠道前三名，创下了单日收入破千万的成绩。2013年10月15日，掌趣科技以17.39亿元收购玩蟹科技100%股权。2016年06月，《拳皇98终极之战OL》越语、泰语版上线。同期，《拳皇98终极之战OL》玩家总量突破千万并获得AppStore畅销榜TOP1。', 'success');
INSERT INTO `company` VALUES (884, '深圳市星河互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120156.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=884', '', '咸鱼游戏', '咸鱼游戏成立于2013年，代表人物及创始人是沈曌，公司现办公地址位于北京市。咸鱼游戏是一家集研发、运营、发行为一体的综合性企业，旗下拥有发行品牌咸鱼游戏，研发品牌时光机工作室、魔力鸟工作室，成功推出《最佳阵容》、《街球联盟》、《那兔之大国梦》等行业垂直细分领域标杆产品。咸鱼游戏先后获得来自华谊兄弟、动域资本、景林投资、集结号资本的A轮融资以及金科娱乐、澜亭投资、华谊兄弟的B轮融资并与狂战网络、动域资本合资成立上海高竞文化传媒有限公司，积极打造全球性的大学生电子竞技平台。2016年1月6日，咸鱼游戏携《那年那兔那些事儿》召开发布会。7月27日，咸鱼游戏举办“星计划”战略发布会，制定加强自研、深耕体育、进军电竞三大战略。2016年12月，咸鱼游戏被评选为2016年度中国十大新锐游戏企业。', 'success');
INSERT INTO `company` VALUES (885, '北京掌趣科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/120157.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=885', 'OURPALM CO.,LTD', '掌趣科技', '北京掌趣科技股份有限公司成立于2004年8月，代表人物是姚文彬，公司现办公地址位于：北京市海淀区。2012年5月11日，公司在深圳证券交易所创业板挂牌上市，股票代码300315。公司主要业务包括移动终端游戏、互联网页面游戏及其周边产品的开发、代理发行和运营维护。 公司拥有员工近2000人，已自主研发和代理发行了200余款游戏产品。公司确立了“精品化、泛娱乐化、平台化、全球化”的战略，在“内生增长”的基础上，加强“外延发展”，持续投资、并购有发展潜力的公司及团队。动网先锋、玩蟹科技、上游网络、天马时空等行业领军企业先后加入掌趣大家庭。并战略投资了Unity Software Inc、欢瑞世纪，通过内生加外延的整合、协同，掌趣的手游及页游产品全面覆盖了卡牌、重度ARPG、休闲、竞速、 射击、体育、策略塔防等主流游戏类型，致力于为广大用户提供多元化、高品质的游戏娱乐体验。', 'success');
INSERT INTO `company` VALUES (886, '成都西山居世游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703241.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=886', 'Seasun Entertainment', '西山居', '成都西山居世游科技有限公司于2012年7月30日在成都市高新工商局登记成立。法定代表人邹涛，公司经营范围包括计算机软件的开发、销售及技术服务；销售：计算机及辅助设备等。', 'success');
INSERT INTO `company` VALUES (887, '杭州斯凯网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703242.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=887', 'SKY-mobi', '斯凯网络', '杭州斯凯网络科技有限公司[1]  成立于2005年，2010年在美国纳斯达克交易所正式挂牌上市股票代码：MOBI，是国内第一家在美上市的移动互联网企业。总部位于天堂硅谷—杭州，在上海、深圳、北京、新德里和雅加达等地均设有分支机构，业务遍布全球100多个国家和地区，目前有员工600余人，员工平均年龄26岁，90%具有本科及以上学历。', 'success');
INSERT INTO `company` VALUES (888, '成都北游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=888', '', '北游科技', '成都北游科技有限公司位于高新区成都高新区盛和一路88号1栋2单元18层1811号交通便利。成都北游科技有限公司本着“客户第一，诚信至上”的原则，欢迎国内外企业/公司/机构与本单位建立长期的合作关系。', 'success');
INSERT INTO `company` VALUES (889, '挚爱互动（北京）网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=889', '', '挚爱互动', '挚爱互动(北京)网络科技有限公司，是国内领先的集互联网游戏和电子商务为一体的娱乐平台。57k的宗旨是为互联网高端用户服务，为用户提供体感游戏、休闲游戏、网页游戏、团购服务、购物返利、娱乐社区等服务，力图打造中国互联网的高端游戏娱乐平台。同时57K也是中国第一家专以网页体感游戏为业务方向的网站，网站提供上千款独家或联合运营的体感游戏产品。', 'success');
INSERT INTO `company` VALUES (890, '杭州米趣网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703243.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=890', 'Hangzhou MiQ Technology Inc.', '米趣科技', '杭州米趣网络科技有限公司（简称：米趣科技）是一家集移动互联网产品研发、发行、互动娱乐为一体的全球化创新型科技公司。公司成立于2012年，覆盖游戏孵化及投资、移动游戏、移动应用等，“5050”计划重点企业，在上一轮融资中，公司获达晨、前海母基金等机构数亿级B轮融资，市值近20亿人民币。', 'success');
INSERT INTO `company` VALUES (891, '北京畅元国讯科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703244.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=891', '', '畅元国讯', '北京畅元国讯科技有限公司于2004-02-24在海淀分局登记成立。法定代表人杨超，公司经营范围包括科技开发；计算机软硬件开发；销售计算机软硬件及外围设备等。', 'success');
INSERT INTO `company` VALUES (892, '北京月宝盒信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=892', '', '北京月宝盒信息科技有限公司', '', 'success');
INSERT INTO `company` VALUES (893, '北京网聚人和科技发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=893', 'WangJuRenHe ShareNetwork Polytron Technologies Inc.', '网聚人和', '北京网聚人和科技发展有限公司主要经营：技术开发、技术咨询、技术推广、技术服务；数据处理；基础软件服务；计算机系统服务；会议服务；承办展览展示活动；组织文化艺术交流活动（不含营业性演出）；设计、制作、代理、发布广告；电脑动画设计；销售电子产品、计算机软件及辅助设备、机械设备。（未取得行政许可的项目除外）等产品。公司尊崇“踏实、拼搏、责任”的企业精神，并以诚信、共赢、开创经营理念，创造良好的企业环境，以全新的管理模式，完善的技术，周到的服务，卓越的品质为生存根本，我们始终坚持用户至上 用心服务于客户，坚持用自己的服务去打动客户。', 'success');
INSERT INTO `company` VALUES (894, '北京罗曼空间科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703245.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=894', 'RomanLife', '罗曼空间', '罗曼空间科技有限公司是中国领先的手游发行商，总部位于北京。公司具有强大的策划、运营实力，我们致力于为全球移动用户提供更好玩、更易用的移动游戏娱乐产品和在线服务,开拓全新的无线互动娱乐生活方式。公司从创立之初，就将打造顶级运营团队和精品游戏合作作为企业发展的核心和重点。公司围绕着创始人团队，汇聚了一批喜爱游戏，真正懂得游戏，愿意为高品质游戏全心投入的年轻人，并已成长为企业的中坚力量。', 'success');
INSERT INTO `company` VALUES (895, '杭州冰魄网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703246.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=895', '', '冰魄游戏', '州冰魄网络科技有限公司（冰魄游戏）成立于2014年, 总部位于互联网行业前线城市——杭州。冰魄游戏致力为玩家提供“值得信赖的”、“快乐的”和“专业的”游戏体验，以专注的精神和独特的创意设计能力，通过自主研发向广大提供手游服务，其中大部分是以长久经典轻松愉快为基础的休闲类游戏，如《保皇》、《够级》、《消消看》等。 未来，冰魄游戏将进一步扩大游戏组合、加大市场渗透力度、持续提升玩家游戏体验、不断加强研发，向“手机休闲游戏优秀品牌”的目标迈进。', 'success');
INSERT INTO `company` VALUES (896, '北京中科奥科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=896', '', '中科奥', '北京中科奥科技有限公司于2004-08-26在海淀分局登记成立。法定代表人孙建，公司经营范围包括技术开发、技术转让、技术咨询；设计、制作等。', 'success');
INSERT INTO `company` VALUES (897, '上海光时信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703247.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=897', 'Lighttime', '光时', '上海光时信息科技有限公司成立于2009年，是一家专业从事网络游戏研发、运营和销售为一体的互联网娱乐企业。光时致力于自主研发互联网棋牌游戏、网页游戏，并以此为起点，逐步向互联网娱乐行业各领域发展。主营研发平台有“踢狗游戏网”，“僵尸必须死”。', 'success');
INSERT INTO `company` VALUES (898, '北京易迅无限信息技术股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=898', '', '易讯无限', '北京易讯无限信息技术股份有限公司是移动互联网行业应用服务商，主营业务为移动互联网应用业务，主要包括移动电子商务、手机游戏、移动增值服务业务三部分。', 'success');
INSERT INTO `company` VALUES (899, '武汉拇指通科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=899', '', '拇指通', '武汉拇指通科技有限公司成立于2004年，由中国风险投资机构--深圳创新投资集团投资设立，公司主要从事网络游戏和手机游戏等互动娱乐项目的开发与运营。公司目前独立开发并运营的网络棋牌游戏平台《赖子山庄》注册用户数已超过3000万，是一家同时具有自主研发和运营资质的棋牌网络游戏公司。是目前湖北省拥有文化部网络文化经营许可证且自主开发与运营的网络游戏公司之一。', 'success');
INSERT INTO `company` VALUES (900, '杭州歆享科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=900', '', '歆享科技', '杭州歆享科技有限公司位于杭州市西湖区文二西路712号西溪乐谷创意产业园2号楼2层A3交通便利。杭州歆享科技有限公司本着“客户第一，诚信至上”的原则，欢迎国内外企业/公司/机构与本单位建立长期的合作关系。', 'success');
INSERT INTO `company` VALUES (901, '广西全游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703248.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=901', '', '全游科技', '广西全游科技有限公司成立日期:2015年8月17日。经营范围：计算机软硬件、多媒体技术开发、技术转让、技术推广、技术咨询服务；网络游戏运营（经营性互联网文化单位，具体项目以审批部门批准为准）；计算机系统集成；市场营销策划；商务服务，会议服务，翻译服务（除国家有专项规定外）；市场调查；受托资产管理；商务信息咨询，教育信息咨询，企业投资管理信息咨询（以上项目除国家有专项规定外）；国内各类文化艺术交流活动策划；展览展示服务；批发兼零售：计算机软硬件及配件、通讯产品（除国家专控产品）。', 'success');
