INSERT INTO `company` VALUES (903, '魔力小鸟（北京）信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/201703249.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=903', '', '魔力小鸟', '魔力小鸟（北京）信息技术有限公司于2012-08-08在西城分局登记成立。法定代表人翟睿，公司经营范围包括技术推广、技术服务、技术开发；电脑动画设计等。', 'success');
INSERT INTO `company` VALUES (904, '武汉卓讯互动信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=904', '', '卓讯互动', '武汉卓讯互动信息科技有限公司于2012年1月11日在武汉东湖新技术开发区登记成立。法定代表人刘亚卓，公司经营范围包括计算机软硬件研发、设计及技术服务；游戏动漫软件研发等。', 'success');
INSERT INTO `company` VALUES (905, '风云速动（北京）科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=905', '', '风云速动', '公司以提升企业和个人的品牌素质为己任，致力于企业商务礼仪、客户服务礼仪、销售礼仪；人力资源管理、企业管理等培训工作。我们拥有一流的讲师团队，丰富的培训经验，根据企业的培训需求制定针对性、实效性的培训方案。 公司自主研发各类精品课程，为您塑造有内涵、高素质的企业团队，树立企业完善的品牌形象。', 'success');
INSERT INTO `company` VALUES (906, '北京赛诺斯科技发展有限责任公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032410.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=906', '', '赛诺斯', '北京赛诺斯科技发展有限责任公司是专业从事移动增值服务产品策划、设计、研发的内容服务商（Content Provider）,致力于成为一流的手机游戏开发商。公司与2009年成立，目前产品已经形成了一定的规模，为众多的合作伙伴和最终用户提供了众多的优秀产品。面对不断涌现的新技术，公司关注移动增值应用发展的新方向，并将在ANDROID、KJAVA、 MTK市场准备就绪后进一步扩展业务集成内容，并积极参与各类新技术开发和应用实践。公司汇集了一大批具备丰富无线增值业务运营知识和实践经验，开拓能力强，对移动业务前景充满热情的专业人员。', 'success');
INSERT INTO `company` VALUES (907, '北京快游互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032411.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=907', '', '快游互动', '北京快游互动科技有限公司主要经营：等产品。公司尊崇“踏实、拼搏、责任”的企业精神，并以诚信、共赢、开创经营理念，创造良好的企业环境，以全新的管理模式，完善的技术，周到的服务，卓越的品质为生存根本，我们始终坚持用户至上 用心服务于客户，坚持用自己的服务去打动客户。', 'success');
INSERT INTO `company` VALUES (908, '上海大始信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=908', '', '大始游戏', '上海大始公司是一家专业从事互联网游戏行业服务的公司，主要业务涵盖游戏版号业务办理，各类广告业务的受理等。', 'success');
INSERT INTO `company` VALUES (909, '北京凤凰互动娱乐网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032412.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=909', '', '凤凰互娱', '凤凰互娱深耕细作，生产优质的IP，延伸产业链，深挖凤凰及其他版权衍生价值，以“生产快乐”为理念，汇聚行业内最优质的游戏内容提供商，与iFree合作发行、运营海外游戏，大力发展精品单机手游，为用户的带来“全方位”、“个性化”、“无与伦比”的游戏互动盛宴。', 'success');
INSERT INTO `company` VALUES (910, '成都艺馨达科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032413.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=910', '', '艺馨达', '成都艺馨达科技有限公司(以下简称“成都艺馨达”)成立于2014年1月，是西南地区领先的移动互联网互动娱乐、数字音乐公司和互联网业务综合服务提供商。公司成立以来，已成功自研和发行百余款精品游戏，拥有软件著作权82个，商标3个，还拥有集三网计费、大数据分析统计和广告分发于一体的综合服务平台。', 'success');
INSERT INTO `company` VALUES (911, '上海豆子网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032414.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=911', '', '豆子网络', '上海豆子网络科技有限公司（豆子网络）14年正式经营地方棋牌游戏市场，从游戏研发、产品运营、市场营销等都是一体化的游戏经营公司。坚持为玩家提供高品质的游戏体检为宗旨，在产品、技术、市场、社会关系等方面都积累的丰富的经验和资源。强大的技术实力和富有创造力的设计能力，加之对国内外文化的深刻理解和丰富的运营经验，迅速推出适合于大众的游戏，以满足用户需求及游戏市场的发展。', 'success');
INSERT INTO `company` VALUES (912, '新疆健讯互联科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032415.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=912', 'JOHNSON ADVANCE', '健讯互联', '新疆健讯互联科技股份有限公司于1997年6月23日在新疆维吾尔自治区工商局经济技术开发区分局登记成立。法定代表人林小健，公司经营范围包括许可经营项目：第二类增值电信业务中的信息服务业务（不含固定网电话信息服务和互联网信息服务）等。', 'success');
INSERT INTO `company` VALUES (913, '上海乾尊网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=913', '', '乾尊网络', '上海乾尊网络科技有限公司以“为玩家创造最好的游戏娱乐生活”为发展目标，致力于为中国的玩家提供最优质的网络游戏服务。公司以弘扬中华文化为己任， 坚持秉承“凝炼品牌，传承乐趣。”的理念，以提供全新、全方位综合娱乐服务产品为目标，用快乐的方式传承中国传统文化。', 'success');
INSERT INTO `company` VALUES (914, '广州分给网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032416.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=914', '', '分给网络', '公司成立于2010年，位于天河软件园内，占地利之优，聚才俊之贤，欲以龙腾之势跻身于网游产业前列。公司秉承创新、专注、负责的行业精神，致力于网络游戏的研发和运营。', 'success');
INSERT INTO `company` VALUES (915, '互爱互动（北京）科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032417.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=915', '', '互爱互动（北京）科技有限公司', '', 'success');
INSERT INTO `company` VALUES (916, '南京魔盒信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032418.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=916', 'IMOHOO', '魔盒科技', '魔盒信息科技有限公司（简称魔盒科技/IMOHOO）于2009年8月，正式成立于文化之都金陵。魔盒科技专注于传媒行业的移动信息技术及终端应用技术的科研开发，提供全媒体平台的文化产品定制及相关应用软件的外包服务，致力于为文化产业打造基于移动互联网的系统化平台解决方案，并与国内外多家移动运营商、互联网公司、终端设备厂商及内容服务厂商建立紧密的战略合作关系。公司提供的软件外包服务共包括应用软件设计、开发、测试、维护、本地化及全球化。支持在岸（ONSITE）、离岸（OFFSHORE）和混合（HYBRID）交付模式。', 'success');
INSERT INTO `company` VALUES (917, '深圳市正易龙科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032419.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=917', '', '正易龙科技', '深圳市正易龙科技有限公司专注于为棋牌游戏行业七年，为棋牌游戏运营商提供全方位多元化的产品、服务、解决方案。现有成员百余名。产品可应用于包括Windows、Mac OS X、Android、Symbian、Wince、Linux等操作系统，有PC客户端棋牌，网页棋牌、Iphone棋牌、智能手机棋牌等众多产品，正努力成为国际一流的棋牌游戏方案提供商。', 'success');
INSERT INTO `company` VALUES (918, '上海要娱网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032420.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=918', '', '要娱网络', '上海要娱网络技术有限公司位于上海市嘉定区陈翔路768号6幢B区2318室交通便利。上海要娱网络技术有限公司本着客户第一，诚信至上的原则，与多家企业建立了长期的合作关系。公司拥有一支较强的技术研发队伍并和国内大专院校强强联手，研发出拥有自主知识产权。', 'success');
INSERT INTO `company` VALUES (919, '武汉市多比特信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=919', '', '多比特', '武汉市多比特信息科技有限公司于2014年7月23日在武汉东湖新技术开发区登记成立。法定代表人刘江，公司经营范围包括手机软件的研发、推广、销售、维护；广告设计等。', 'success');
INSERT INTO `company` VALUES (920, '深圳市水牛拉车网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=920', '', '水牛拉车', '深圳市水牛拉车网络科技有限公司是一家网络游戏公司。公司地址位于深圳市南山区南山街道南园枫叶公寓A座920。', 'success');
INSERT INTO `company` VALUES (921, '上海酷幻网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=921', '', '酷幻网络', '上海酷幻网络科技有限公司位于上海市普陀区银杏路659号5号楼268室交通便利。上海酷幻网络科技有限公司本着“客户第一，诚信至上”的原则。热诚欢迎各界朋友前来参观、考察、洽谈业务。', 'success');
INSERT INTO `company` VALUES (922, '杭州哲信信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=922', '', '哲信', '杭州哲信信息技术有限公司创立于2010年5月，是业内领先的轻度手游发行商。公司旗下拥有者数百款的优质产品，累计独立用户突破近2亿。与中国、韩国及当地高校也有着密切合作，为培养优秀的游戏人才，公司专门设立游戏学院以及专项的游戏基金,更好的建设国际游戏产业基地，并吸引数十支来自海内外的专业研发团队入驻。', 'success');
INSERT INTO `company` VALUES (923, '北京易美时代科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032421.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=923', 'EGAMECHINA', '易美时代', '易美时代EGAMECHINA致力于为国内外优秀的开发商提供“一站式”的服务体系，从产品本土化策划，大数据收集与分析，技术优化改进，运营推广等方面为CP厂商提供全方位服务。 与三大运营商（中国移动、中国联通、中国电信）建立了深度合作关系，结合运营商便利的支付手段，为国内用户提供安全的增值游戏服务。', 'success');
INSERT INTO `company` VALUES (924, '深圳市唯变科技开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032422.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=924', 'VIGAME', '唯变科技', '唯变科技作为移动互联网先锋企业，公司主营业务为手机客户端应用及游戏开发，技术团队由原腾讯、迅雷游戏部门技术精英组建，核心团队有超过10年游戏开发及运营经验。已推出多款优秀的游戏，如消灭星星，斗地主、大鱼吃小鱼、厕所矿工等等，画面精美，操作简单，AI智能优秀，多款产品下载量在360、安卓市场等下载市场已接近百万级。', 'success');
INSERT INTO `company` VALUES (925, '南京畅乐软件科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=925', '', '畅乐软件', '南京畅乐软件科技有限公司于2013年08月02日在南京市建邺区市场监督管理局登记成立。法定代表人施华贵，公司经营范围包括电子产品的软硬件开发及销售、计算机软硬件开发及销售等。', 'success');
INSERT INTO `company` VALUES (926, '北京新洲基业科技发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032423.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=926', '', '新洲基业', '新洲基业科技发展有限公司成立于05年是北京新洲国际集团(NICG）旗下新洲咨询的子公司。从事互联网企业政府公关、资质代办、企业并购、版权登记、检测认证、知识产权、投资融资等专业咨询服务。', 'success');
INSERT INTO `company` VALUES (927, '深圳市米点科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032424.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=927', '', '米点科技', '深圳市米点科技有限公司成立于2012年，公司位于深圳福田，现在是一家创业型公司。公司专注于高品质手机游戏的研发、运营和发行，以专业的团队力量、完善的技术解决方案、强大的渠道整合能力，为手游产品提供高效发行服务、全平台品牌建设和专业运营服务，致力将公司打造成为一流的移动互联网游戏新锐。', 'success');
INSERT INTO `company` VALUES (928, '深圳口袋科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032425.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=928', '', '口袋科技', '深圳口袋科技有限公司(简称口袋科技)于2012年6月在深圳成立。口袋科技致力于移动互联网领域的在线棋牌休闲游戏，为用户提供优质的产品和服务。口袋科技以精品休闲棋牌游戏为战略核心，以开放共赢的合作理念推进开放平台合作，与各大分销渠道运营商实现共赢。', 'success');
INSERT INTO `company` VALUES (929, '上海易娱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032426.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=929', 'EYUGAME', '易娱网络', '易娱网络，前身为九娱网络，创建于2009年，总部坐落于美丽的羊城广州，目前员工500余人，拥有国内顶尖的技术及运营团队，是一家国内领先的互联网公司。自创立以来，易娱网络持续推出多款自主研发的网页及手机游戏，包括：《龙将》《暗黑西游记》《三国魂》《勇者之塔》《天天爱西游》等知名游戏，与国内顶级互联网公司，如：腾讯、百度、360、阿里巴巴、三七互娱等一直保持着深度合作的关系。易娱网络已经全面覆盖到亚洲、欧洲、北美洲、南美洲及大洋洲的数十个国家和地区，在世界各个角落都能轻易体验到易娱产品所带来的乐趣。', 'success');
INSERT INTO `company` VALUES (930, '广州杨梅网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032427.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=930', '', '杨梅网络', '广州杨梅网络科技有限公司是一家成立于2011年初的高科技企业，由一群热爱游戏、有过多年的游戏行业经验立志创业的年轻人创立，同时由对游戏产业充满激情的投资人鼎力支持，公司的运营中心位于广州市。公司致力与自主开发网页游戏运营平台陆游网，并利用广泛的媒体资源及良好的厂商合作关系，秉承着“以增强玩家体验为本，为玩家提供精品的游戏服务”的信念，努力做到玩家心目中最好的游戏平台。', 'success');
INSERT INTO `company` VALUES (931, '北京方圆天下企业管理顾问有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=931', '', '方圆天下', '北京方圆天下企业管理顾问有限公司是一家从事互联网文化活动的公司。公司地址位于北京市石景山区石景山路乙18号院3号楼。', 'success');
INSERT INTO `company` VALUES (932, '北京分播时代网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032428.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=932', '', '分播时代', '北京分播时代网络科技有限公司于2007-08-21在海淀分局登记成立。法定代表人刘勇，公司经营范围包括技术开发、技术转让、技术咨询、技术推广、技术服务等。', 'success');
INSERT INTO `company` VALUES (933, '北京柒柒网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=933', '', '柒柒网络', '北京柒柒网络科技有限公司成立于2009年8月，是一家专业从事网络棋牌游戏开发及运营的高科技软件技术公司。注册资金1000万元。现有研发部、运营部、市场部、客服部、财务部、人事部等多个部门，公司创建伊始，就很注重人才的引进和团队建设，将\"以人为本、脚踏实地\"的理念贯穿人才聘用、人才培养的始终。柒柒认为人才是企业最宝贵的财富，人才发展是企业发展的重要保障。因此柒柒重视每一位员工的成长与提升，注重对员工进行多方位的培养及潜能的激发。', 'success');
INSERT INTO `company` VALUES (934, '成都风际网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032429.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=934', 'wind play', '风际网络', '成都风际网络科技股份有限公司于2013年3月27日在成都市高新工商局登记成立。法定代表人张霆，公司经营范围包括网络技术开发、技术咨询；研发、销售计算机软硬件并提供技术咨询等。', 'success');
INSERT INTO `company` VALUES (935, '北京软多科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032430.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=935', '', '软多科技', '北京软多科技有限公司是一家互联网公司，专注手游的研发和发行，公司以移动游戏联运平台和手机游戏的研发及运营为主营业务，团队成员经验丰富。', 'success');
INSERT INTO `company` VALUES (936, '北京麒麟网文科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032431.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=936', '', '麒麟游戏', '北京麒麟网文化股份有限公司（简称“麒麟游戏”）成立于2007年7月7日，是一家综合性文化娱乐企业。麒麟游戏拥有扎实的核心技术，以及多个经典游戏品牌。自成立以来，麒麟游戏一直强调创新理念的游戏设计和弘扬民族文化精神，走在国产原创游戏的潮流前端。', 'success');
INSERT INTO `company` VALUES (937, '北京爱酷游科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032432.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=937', '', '爱酷游', '北京爱酷游广告传媒有限责任公司创立于2010年，后续创立北京猫尾草科技有限公司(www.Le4.com)，是在移动互联领域从事应用分发和内容推荐的平台型企业。旗下独立开发并运营了多款流量分发和内容推荐的移动APP，“乐市场”安卓手机助手、“ Mobomaster ”苹果手机助手、“猫尾草助手”iOS版、“乐思助手”，服务于千万级的用户，公司将长期从事移动应用和内容分发，在手游及应用社区方向深度运营。', 'success');
INSERT INTO `company` VALUES (938, '北京诚誉天下科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=938', '', '诚誉天下', '北京诚誉天下科技有限公司地址位于海淀区北清路103号4号楼2层214室。经营范围有：技术开发、技术推广、技术转让、技术咨询、技术服务；计算机系统服务；基础软件服务；应用软件服务；软件开发；软件咨询；产品设计、模型设计等。', 'success');
INSERT INTO `company` VALUES (939, '广州天祈网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=939', '', '天祈网络', '广州天祈网络科技有限公司成立于2011年初，是一家集网络游戏研发和运营于一体的互联网企业。天祈目前还处于成长期，公司拥有自主研发的游戏引擎，自主研发的游戏产品和一个团结向上，充满激情的团队。天祈的发展目标是：从一个游戏公司开始，最终打造属于自己的平台，为用户提供全方位的服务。坚持创新，成为最有创造力的中国游戏企业，将最有创意的产品和内容带给所有热爱游戏的中国玩家。', 'success');
INSERT INTO `company` VALUES (940, '广州遇悦网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032433.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=940', '', '遇悦网络', '广东遇悦网络科技有限公司，是凡跃集团旗下休闲竞技事业集群的领导企业。遇悦休闲竞技游戏中心（www.068.com）由遇悦网络科技打造，是国内首款集合休闲竞技和益智元素于一体的免费游戏平台。我们一贯倡导的游戏理念是：绿色健康，杜绝作弊，公平随机；我们的目标是致力于打造最公平、安全的游戏中心。', 'success');
INSERT INTO `company` VALUES (941, '杭州烈焰网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032434.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=941', 'LIEYAN NETWORK', '烈焰网络', '杭州烈焰网络技术有限公司尊崇“踏实、拼搏、责任”的企业精神，并以诚信、共赢、开创经营理念，创造良好的企业环境，以全新的管理模式，完善的技术，周到的服务，卓越的品质为生存根本，我们始终坚持用户至上 用心服务于客户，坚持用自己的服务去打动客户。', 'success');
INSERT INTO `company` VALUES (942, '深圳市掌炫世纪网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=942', '', '掌炫世纪', '深圳市掌炫世纪网络科技公司是一家有国内IT精英和资深MTK平台工程师为研发团队，有经验丰富的项目运营专业人士组成的，专注于MTK平台手机杂志、图片、卡通漫画等娱乐功能研发和运营。 掌炫世纪以MTK为平台，坚持以市场和客户为导向，以技术为核心，已经开发出多款完善的手机在线娱乐功能，为用户提供界面精美，图文混排，生动活泼，人性化的休闲娱乐体验。公司自成立以来，始终保持快速稳健的发展，当前已经和国内众多大中型方案商、集成商建立了密切合作关系，逐渐成长为业界知名服务商。', 'success');
INSERT INTO `company` VALUES (943, '深圳市迷思互动娱乐有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=943', '', '迷思互动', '深圳市迷思互动娱乐有限公司作为游戏设计开发公司，是一家年轻的企业，公司的主要成员是充满朝气的年轻人。力争通过创造一个良好的团队协作氛围，给每个人提供一个发挥自己能力的舞台，通过公司所有成员的努力，创造出更大的价值，实现个人与公司的共同进步。', 'success');
INSERT INTO `company` VALUES (944, '广州提子网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=944', '', '提子网络', '广州提子网络科技有限公司（简称：提子网络）成立于2013年，业务品牌为“葡萄手游”（pt.cn）。提子网络主营业务是游戏研发与运营，服务于智能手机、平板电脑，电视盒子等智能终端的用户。', 'success');
INSERT INTO `company` VALUES (945, '杭州破晓网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032435.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=945', '', '破晓科技', '杭州破晓科技有限公司是上市公司旗下全资子公司，公司自成立以来，始终坚持“服务、创新、用户第一”的企业宗旨，确定智能手机休闲类游戏软件应用为中短期发展目标，以整合尽可能丰富的游戏和服务来满足用户移动互联网需求，为智能机网民带来更为极致的软件使用体验为长期目标，是一家集游戏研发，产品运营于一体的手机软件应用企业。', 'success');
INSERT INTO `company` VALUES (946, '武汉福禄网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032436.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=946', '', '福禄网络', '武汉福禄网络科技有限公司是国内领先的数字产品在线销售平台提供商，主营包括话费充值、网游点卡、缴费支付、在线票务等虚拟充值业务。一直以来，公司秉承着“让网络服务商人，让技术提高效益，让诚信建立交易，让服务得到满意”的使命，从客户需求出发，不断完善服务流程，深化客户服务；与客户共成长。经过多年的努力，公司已经发展活跃代理商8万多家，销售渠道包括各类网站、网店卖家、渠道代理商、网吧、报刊、便利店等合作商，公司深受众多上游运营商和业内人士的高度赞赏。', 'success');
INSERT INTO `company` VALUES (947, '北京悦成三际科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=947', '', '悦成三际', '北京悦成三际科技公司主要从事针对大学生职业技能培训和软件开发业务，经过八年的努力，职业技能培训已经成为业界口碑最好，影响力最高的机构之一。公司通过整合，新成立基础教育事业部，业务之一是从事幼儿教育服务和高端教育产品的市场推广。', 'success');
INSERT INTO `company` VALUES (948, '广州掌新信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=948', '', '掌新信息', '广州掌新公司成立于2009年，公司凭借其在电信增值服务领域的行业优势，积极投入到电信增值业务、电信工程建设、互联网业务的开发和推广中。公司将依托强大的研发实力，运用先进的管理理念、领先的运营能力及丰富的行业经验，面向用户、面向竞争、面向未来，立足自身资源优势，不断积累运营经验、提高服务水平。', 'success');
INSERT INTO `company` VALUES (949, '成都黑鸭子科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032437.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=949', '', '黑鸭子', '成都黑鸭子科技有限公司成立于2015年12月，注册资金人民币1500万元，注册在天府之国“成都”，为有限责任公司。公司现处于扩张期，广纳贤才，开发有品质的产品、提供最贴心的服务。', 'success');
INSERT INTO `company` VALUES (950, '上海点点乐信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032438.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=950', '', '点点乐', '上海点点乐信息科技有限公司是国内领先的移动互联网网络游戏开发公司，致力于无线游戏的开发和运营。目前公司专注移动互联网（IOS,Android）音乐舞蹈休闲网络游戏的开发，主要产品有《恋舞OL》、《中国好舞蹈》、《开心爱消除》。', 'success');
INSERT INTO `company` VALUES (951, '上海炫际网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=951', '', '炫际网络', '上海炫际网络科技有限公司,位于上海市普陀区银杏路659号5号楼277室,欢迎新老客户联系,共谋发展！', 'success');
INSERT INTO `company` VALUES (952, '上海驰游信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=952', '', '驰游信息', '上海驰游信息技术有限公司于2014年6月12日在徐汇区市场监督管理局登记成立。法定代表人林奇，公司经营范围包括计算机软硬件及配件领域内的技术开发、技术转让等。', 'success');
INSERT INTO `company` VALUES (953, '芜湖乐善网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032439.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=953', '', '乐善网络', '芜湖乐善网络科技有限公司是一家拥有国内外最精品网页游戏的专业公司。芜湖乐善网络科技有限公司专注代理海外游戏进行国内运营，立志打造经典客户端游戏平台。芜湖乐善网络科技有限公司是2014年奋力崛起的全新火爆网页平台。', 'success');
INSERT INTO `company` VALUES (954, '苏州乐麟无线信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032440.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=954', '', '乐麟无线', '苏州乐麟无线信息科技有限公司 简介：主要经营研发：无线通信及相关配套软件。从事网络科技领域内的技术开发、技术转让、技术咨询、技术服务；网络信息技术服务。游戏软件的研发、销售、技术服务、技术转让；计算机软硬件的技术开发与销售。（依法须经批准的项目，经相关部门批准后方可展开经营活动） ，我们始终坚持用户至上，用心服务于客户，坚持用自己的服务去打动客户，公司秉承“保证一流质量，保持一级信誉”的经营理念，坚持“客户第一”的原则为广大客户提供优质的服务。', 'success');
INSERT INTO `company` VALUES (955, '微乐星空（北京）网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032441.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=955', '', '微乐星空', '微乐星空（北京）网络技术有限公司于2013-09-09在海淀分局登记成立。法定代表人李伟，公司经营范围包括技术开发、技术推广、技术转让、技术咨询、技术服务等。', 'success');
INSERT INTO `company` VALUES (956, '杭州网易雷火科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032442.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=956', 'LEIHUO GAME', '网易雷火', '杭州网易雷火科技有限公司于2009年04月15日在杭州市高新区（滨江）工商行政管理局登记成立。法定代表人胡志鹏，公司经营范围包括一般经营项目：服务：第一类增值电信业务中的因特网数据中心业务等。', 'success');
INSERT INTO `company` VALUES (957, '福州慧多丰信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=957', '', '慧多丰', '福州慧多丰信息技术有限公司成立于2003年。本单位位于福建省福州市鼓楼区五四路119号8层，主营项目：电信增值 。自成立以来不断创新研发，目前公司稳步提升。', 'success');
INSERT INTO `company` VALUES (958, '杭州米玩网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=958', '', '米玩网络', '杭州米玩网络科技有限公司主要经营：网络游戏产品。公司尊崇“踏实、拼搏、责任”的企业精神，并以诚信、共赢、开创经营理念，创造良好的企业环境，以全新的管理模式，完善的技术，周到的服务，卓越的品质为生存根本，我们始终坚持用户至上 用心服务于客户，坚持用自己的服务去打动客户。', 'success');
INSERT INTO `company` VALUES (959, '北京一铭铭鑫软件有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032443.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=959', '', '一铭铭鑫', '北京一铭铭鑫软件有限公司是广西一铭软件股份有限公司的全资子公司，公司借助北京地理位置优势和科技优势，同国内的科研机构、高等学府和高端人才进行合作，开发软件产品。', 'success');
INSERT INTO `company` VALUES (960, '苏州全真网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032444.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=960', '', '全真网络', '苏州全真网络科技有限公司于2014年09月18日在苏州工业园区市场监督管理局登记成立。法定代表人刘步成，公司经营范围包括网络技术开发；软件设计、开发、销售，并提供相关技术服务等。', 'success');
INSERT INTO `company` VALUES (961, '星罗天下（北京）科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032445.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=961', '', '星罗天下', '星罗天下（北京）科技有限公司是一家网络游戏公司。主要经营项目包括：技术开发、技术咨询、技术服务、技术转让；计算机系统服务；销售自行开发后的产品；设计、制作、代理、发布广告；从事互联网文化活动。（从事互联网文化活动以及依法须经批准的项目，经相关部门批准后依批准的内容开展经营活动。）', 'success');
INSERT INTO `company` VALUES (962, '山东小伙伴软件股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032446.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=962', '', '小伙伴软件', '山东小伙伴软件有限公司，致力于打造品质优秀的游戏研发基地，在成都拥有上百人的技术研发、美术、运营中心，为国内外一线平台提供高品质网页游戏、手机游戏。年轻、踏实、苦干、专精是企业一贯奉行的理念。', 'success');
INSERT INTO `company` VALUES (963, '石家庄炫酷软件科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=963', '', '炫酷软件', '石家庄炫酷软件科技有限公司，专注于IOS、Android、JAVA，手机游戏的研发和发行，是一家极具竞争实力的手机游戏产品提供商。炫酷软件制作并发行的产品有《天天狙击》、《天天狙击2》、《僵尸战场》等手机游戏。', 'success');
INSERT INTO `company` VALUES (964, '惠州市虫洞网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=964', '', '虫洞网络', '惠州市虫洞网络科技有限公司成立于2013年7月，是一家创业型公司，是游戏界的新生儿，是一个以游戏运营为主业务方向的高新技术中心。团队核心成员均在网络游戏行业从业2-3年，有深厚的专业积累和成熟的管理经验。', 'success');
INSERT INTO `company` VALUES (965, '深圳市佳游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032447.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=965', 'FINE GAME', '佳游科技', '佳游科技是国内领先的手机研发，运营公司。作为ICP研发了龙珠保卫战等风靡一时的手游，联运代理了消灭星星，合金生化危机等手游。公司还针对中国市场的快速反应树立了自己的竞争优势，目前正在开发多款手机游戏。', 'success');
INSERT INTO `company` VALUES (966, '济南恒健信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=966', '', '恒健信息', '济南恒健信息技术有限公司是一家互联网产品公司。位于山东省济南市高新区工业南路44号丁豪广场6号楼1单元101室。', 'success');
INSERT INTO `company` VALUES (967, '杭州掌卓网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032448.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=967', '', '掌卓网络', '杭州市掌卓网络科技有限公司（掌卓游戏）成立于2015年, 设在互联网行业的前线城市之一的杭州。公司致力于成为国内专业的“互联网及周边应用”开发商和服务商，面向互动娱乐平台、数码电子产品及专业网站的开发和运营。紧密依靠各级IT部门和各类互联网产品推广商，让产品、服务、信息在这个广泛而高效的网络上流畅运转。', 'success');
INSERT INTO `company` VALUES (968, '湖北英科瑞思软件开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032449.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=968', '', '英科瑞思', '湖北英科瑞思软件开发有限公司成立于2010年1月25日, 注册资金一千万。总部座落于湖北宜昌，是一家专注研发和运营的高科技公司。公司坚持以“立足于软件专业产品研发，着眼软件专业服务应用”为企业发展战略；秉承“以用户满意为自身价值体现”的企业价值观。', 'success');
INSERT INTO `company` VALUES (969, '浙江印象软件有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032450.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=969', '', '印象软件', '浙江印象软件有限公司,是一家以高新技术为先导，依靠多年的互联网丰富管理、运营经验，致力于绿色、健康网络娱乐生活的互动平台建设.公司成立以来，先后投资建设了多家互联网企业，分布上海、杭州、苏州、金华、盐城等沿海经济发达城市，多个产品项目在模式、产品、规模上处于行业领先地位，多为当地知名高新企业、文化企业、网络企业、纳税大户。', 'success');
INSERT INTO `company` VALUES (970, '深圳市九五玖玖网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032451.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=970', '', '九五玖玖', '深圳市九五玖玖网络科技有限公司于2015年正式在深圳市注册成立，是广东省一家专业从事大型网络游戏自主研发、运营的高新技术企业。九五玖玖一直持续着稳定高速的发展，不断地积累宝贵的运营与开发经验，将经验化做铁锤，时刻打造公司在游戏开发、维护、销售及推广等方面的业务能力。公司历时开发两年的大作——大型社区类休闲棋牌网游《玖玖游》上线公测后受到广大玩家的一致好评。', 'success');
INSERT INTO `company` VALUES (971, '杭州开启网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032452.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=971', '', '开启网络', '杭州开启网络科技有限公司成立于2010年初，注册资金1000万人民币。公司坐落于环境优美的西子湖畔，依托“天堂硅谷”--杭州的区位优势及政府大力扶持的政策优势，公司发展迅猛，目前已成为专业从事开发和运营地方互联网应用服务平台、提供互联网安全解决方案和电子商务服务的高新技术企业。', 'success');
INSERT INTO `company` VALUES (972, '广州星晋网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032453.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=972', '', '星晋网络', '广州星晋网络科技有限公司致力于优质游戏的设计与制作，对国产游戏的发展具有强烈的使命感，在海量游戏产品的浪潮中，旗下有98游游戏中心，789y游戏中心，星星游戏中心，脱颖而出，在游戏产业中制造良性因素，不仅希望能够改善游戏产业，更希望给予广大玩家不同的游戏感受，做为游戏人，“创造”是我们的立足之本。', 'success');
INSERT INTO `company` VALUES (973, '广州彩途信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032454.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=973', '', '彩途科技', '广州彩途信息科技有限公司提供贸易咨询服务，信息技术服务，商品信息咨询服务；专业从事活动策划、互联网业务、无线数据业务、系统以及相关应用的研究、开发、实施、宣传推广和运营。', 'success');
INSERT INTO `company` VALUES (974, '杭州咖凯网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032455.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=974', '', '咖凯科技', '杭州咖凯科技有限公司致力于通过数字娱乐服务提升人们的生活品质及生活乐趣，形成网络平台、网络交友、互联网增值业务等业务体系，并形成“一站式”综合服务体系。咖凯科技有着在业界独树一帜的风格和文化，充满快乐的办公环境；另类独特的展示活动；个性十足的娱乐派对，哈贝游戏中心将创意、自由、梦想展现的淋漓尽致，并追求极致，成为业内名副其实的最具创意的互联网企业。', 'success');
INSERT INTO `company` VALUES (975, '北京树熊科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032456.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=975', '', '树熊科技', '北京树熊科技有限公司成立于2014年，是一家从事移动网络游戏研发的高新技术企业，团队由拥有十年以上网游研发经验的人员组成。公司秉承国际化的管理理念，以发挥员工最大创造力为核心，营造了自由开放的工作氛围。为游戏从业人员以及想加入游戏行业的有志之士提供一个广阔优质的平台和发展空间。目前团队开发的游戏《炫舞浪漫爱》已正式上线运营，并在App store取得了良好的成绩。', 'success');
INSERT INTO `company` VALUES (976, '苏州酷曼软件技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032457.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=976', '', '酷曼软件', '苏州酷曼软件技术有限公司目前拥有各类技术人员12名，专职产品销售业务人员16名，代理商不少于50家公司或个人；公司现拟定开展的业务有：软硬件开发、互联网技术研发、网络基础建设、Internet网络服务和网络商业应用研究（包括电子商务、网络营销、网络广告、商业网站规划和网页设计等）。为迎合商业企业高端应用需求，集合多种互联网资源信息服务，为生产制造、医疗保险、金融证券、网络服务型企业以及社会各界用户提供多项核心技术支持和最优质的服务，为互联网信息提供良好传播环境。', 'success');
INSERT INTO `company` VALUES (977, '杭州悦豆科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=977', '', '悦豆科技', '杭州悦豆科技有限公司，是一家专注移动互联网游戏开发及运营的IT公司。公司以创新意识引领发展、以重视人才培养为宗旨，注重团队协作、集思广益。公司努力打造现代化与人性化的管理制度，为公司的发展与个人的发展提供制度保证。“共同努力、共同成长、共同辉煌”是公司的理念，同时也是公司与员工的承诺；以“认真做事、诚信做人”来指导实际行动，共同构建美好的明天。公司以人性化方针来管理人才，为员工提供与创造发展的空间，使员工与公司一同成长；同时公司注重团队精神，在团队中充分挖掘与发挥个人的才能。', 'success');
INSERT INTO `company` VALUES (978, '深圳市迷你玩科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032458.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=978', '', '迷你玩', '深圳市迷你玩由来自腾讯网的游戏行业资深人士创立， 致力于为青少年儿童开发下一代的游戏产品，打造一个年轻的社区。 目前开发运营的产品为沙盒游戏《迷你世界》，在腾讯应用宝，4399游戏盒，百度手机助手等多个平台都取得排行榜前三位的成绩，有望成为新一代的明星产品。', 'success');
INSERT INTO `company` VALUES (979, '北京掌上新城信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032459.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=979', '', '掌上新城', '北京掌上新城信息技术有限公司，隶属爱丽国际集团；爱丽集团业务涉及互联网、广告、教育、出国服务、餐饮、网络游戏、电子商务领域，年营业额6亿人民币。掌上新城公司，成立于2005年，注册资金1000万元，是一家技术实力雄厚的高科技企业，2008年取得了国家文化部颁发的游戏运营企业资质。', 'success');
INSERT INTO `company` VALUES (980, '浙江古川科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032460.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=980', '', '浙江古川', '杭州古川科技有限公司是一家致力于手游研发和发行的移动游戏公司，公司成立于2013年1月，总部位于杭州。杭州古川科技有限公司是一家年轻而有活力的手游公司，旗下有自主研发团队“元气星工作室”。公司专业致力于手游研发和发行，主打移动单机游戏、弱联网游戏和html5游戏。', 'success');
INSERT INTO `company` VALUES (981, '广州鑫途网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032461.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=981', '', '鑫途网络', '广州鑫途网络科技有限公司，简称鑫途游戏，是一家移动互联网游戏的开发商，为移动平台开发游戏。技术团队来自腾讯、乐逗、UC等国内顶尖移动互联网公司，有开发经历，合作人均有手游行业经历。', 'success');
INSERT INTO `company` VALUES (982, '广州家庭医生信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=982', '', '家庭医生', '广州家庭医生信息技术有限公司，是国内领先的移动增值信息提供商，发行数字图书和杂志超50类、按月服务人群近500万。在提供健康在线资讯的同时，还将整合用户资源和行业资源，为大众提供个性化、优质的健康产品和服务。提供资讯、产品、服务等全方位的健康解决方案。', 'success');
INSERT INTO `company` VALUES (983, '北京中胜元鑫科技有限责任公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=983', '', '中胜元鑫', '北京中胜元鑫科技有限责任公司是一家互联网游戏研发公司。公司地址位于北京市海淀区信息路甲28号D座06A-6127。', 'success');
INSERT INTO `company` VALUES (984, '北京新欣信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=984', '', '新欣信息', '北京新欣信息技术有限公司是一家致力于研制开发，自主生产，代理尖端电子技术设备的高科技企业.拥有多项专利。', 'success');
INSERT INTO `company` VALUES (985, '北京风云万象科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1500452073504.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=985', '', '风云万象', '北京风云万象科技有限公司是一家网络游戏公司。公司地址位于北京市丰台区新宫体育健身休闲园8号中福4号楼西座138室。', 'success');
INSERT INTO `company` VALUES (986, '武汉灵动在线科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032462.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=986', '', '灵动在线', '武汉灵动在线成立于2014年,是一家综合娱乐的研发和运营商,丰富不同市场人群的精神文明需求,运营的产品类型含手机网游,教育游戏,休闲小游戏,社区游戏等。', 'success');
INSERT INTO `company` VALUES (987, '上海闪速软件发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=987', '', '闪速软件', '上海闪速软件发展有限公司自成立以来发展迅速，业务不断发展壮大。公司主要经营计算机软硬件及系统集成，通讯设备、电子产品、互联网电信增值服务，电子商务的“四技”服务，互联网信息咨询服务，网站建设，网络营销，数码软件产品设计制作销售，计算机及外部设备、电子产品、通讯设备、五金交电，我们有最好的产品和专业的销售和技术团队，为客户提供优质的产品。', 'success');
INSERT INTO `company` VALUES (988, '广州炼爱网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032463.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=988', '', '炼爱网络', '广州炼爱网络科技股份有限公司于2013年10月21日在广州市工商行政管理局登记成立。法定代表人王玉柱，公司经营范围包括软件开发;软件服务;网络技术的研究、开发等。', 'success');
INSERT INTO `company` VALUES (989, '江西商联通网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032464.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=989', '', '商联通', '江西商联通网络科技有限公司是一家经营网络棋牌游戏的公司。主营软件设计、开发、销售；网站建设、服务器托管；网络技术开发、服务等。主要产品是同城乐，同城乐有上饶圆角麻将，南昌麻将,万载麻将等一系列休闲益智棋牌游戏。', 'success');
INSERT INTO `company` VALUES (990, '上海都玩网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032465.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=990', '', '都玩网络', '上海都玩网络科技有限公司于2012年3月8日成立，主要从事网页游戏开发及平台运营业务。与国内多家游戏平台达成合作协议，并与海外6家游戏公司签订合作运营协议。', 'success');
INSERT INTO `company` VALUES (991, '北京网圣飞腾信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032466.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=991', '', '网圣飞腾', '北京网圣飞腾信息技术有限公司是一家重发展、重质量的企业。一直以稳定可靠的产品质量和良好的经营信誉，取得了广大客户的信任，紧随市场发展趋势，不断地开发新产品，以满足广大客户最新需求。所有的产品均已于通过内部质量标准。', 'success');
INSERT INTO `company` VALUES (992, '北京明臣卡网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=992', '', '名臣卡', '名臣卡科技有限公司是一家专业为用户提供数字产品交易服务的电子商务平台。是国内最受欢迎、最具影响力的数字产品交易网站之一。公司的合作伙伴遍及美国、欧洲、韩国、中国台湾等地。名臣卡科技拥有数百名研发工程师、商业模式研究者和数千名专业数字产品交易团队，已经发展成为一家集信息技术研发、游戏增值服务运营、电子商务交易服务于一体的跨区域集团化企业。', 'success');
INSERT INTO `company` VALUES (993, '杭州尚福网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032467.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=993', '', '尚福网络', '杭州尚福网络科技有限公司始终坚持用户至上，用心服务于客户，坚持用自己的服务去打动客户，公司秉承“保证一流质量，保持一级信誉”的经营理念，坚持“客户第一”的原则为广大客户提供优质的服务。', 'success');
INSERT INTO `company` VALUES (994, '广州复成信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=994', '', '复成信息', '广州复成信息科技有限公司，主要经营信息科技，注册资本万元，公司是广东广州服务的知名企业，公司坚持诚信、互利，为客户提供最好的服务和最实惠的价格。公司的办公地址在广州市天河区黄埔大道西100号。', 'success');
INSERT INTO `company` VALUES (995, '成都趣乐多科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032468.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=995', '', '趣乐多', '成都趣乐多科技有公司是一家以网页游戏和移动游戏为基础的，集研发、发行、运营为一体的专业化游戏公司，目前总部在成都，北京、深圳设有分公司。', 'success');
INSERT INTO `company` VALUES (996, '广州容讯科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=996', '', '容讯科技', '广州容讯科技有限公司，主要经营通讯技术的开发、服务、咨询。计算机软硬件、网络技术的研究开发，系统集成及技术服务。文化学术交流，营销策划，企业形象设计，产品外观、标识设计。', 'success');
INSERT INTO `company` VALUES (997, '博襄（上海）信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032469.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=997', '', '博襄信息', '博襄（上海）信息技术有限公司成立于2014年，  是一家本土从事互联网+增值电信业务以及网络文化等相关业务的专业信息咨询公司，并为网络行业客户及传统行业客户提供专业咨询服务，旨在通过优质咨询及专业化的顾问方案，帮助客户解决企业发展中的诸多难题，提高行业竞争力。', 'success');
INSERT INTO `company` VALUES (998, '杭州盈顺网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032470.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=998', '', '盈顺网络', '杭州盈顺网络是目前浙江省知名上市企业，在本省乃至全国领先的网络游戏开发商、运营商和发行商旗下的一支年青的生力军 本公司成立于2016年，注册资金1000万，公司致力于打造中国乃至全球领先的网络游戏平台。目前公司拥有50多名自主研发人员，其中核心开发人员具有20-30年的产品研发经验，并与多家知名开发商合作，坚持丰富多样化的产品线，目前我司正致力于开发新的游戏项目，对新兴市场有着非常敏锐的感知度。随着公司的迅速发展，公司将投入更多的技术力量，引进更先进的管理理念，不断提升员工的能力。在互联网游戏业不断发展的今天，热忱欢迎对网络游戏充满热情，有意于在此平台上发展的伙伴们加入我们的大家庭，为公司、为网络游戏事业创造更加美好的未来！', 'success');
INSERT INTO `company` VALUES (999, '上海隐志网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=999', '', '心动游戏', '心动游戏（XD.com）是国内最权威的网络游戏运营商，致力于提供互联网即时娱乐内容，主要包括RPG、SLG、MMORPG等类网络游戏的运营与研发。公司具有良好的行业背景和雄厚的运营能力，致力于成为一家长久发展的网络游戏研发和运营企业。公司高层对网游公司运作有着非常深的认识，公司的核心成员，在游戏行业中有多年的经验，制作过多款成功的游戏。公司具有良好的行业背景和雄厚的运营能力，致力于成为一家长久发展的网络游戏研发和运营企业。公司高层对网游公司运作有着非常深的认识，公司的核心成员，在游戏行业中有多年的经验，制作过多款成功的游戏。', 'success');
INSERT INTO `company` VALUES (1000, '北京阿瑞亚科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1000', '', '北京阿瑞亚科技有限公司', '北京阿瑞亚科技有限公司是以经营手机游戏为主的公司。', 'success');
INSERT INTO `company` VALUES (1001, '杭州启意科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1001', '', '杭州启意科技有限公司', '杭州启意科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (1002, '广州火舞软件开发股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032471.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1002', '', '火舞游戏', '广州火舞软件开发股份有限公司成立于2012年末，从成立之日起，便专注于打造精品游戏。并以此为公司企业文化的重要组成部分，凝聚了一批有能力，有激情，有追寻梦想之心的人!公司前期以网页游戏为主，后紧跟移动互联网的时代浪潮，把重心转移至手游开发。以高端自主研发技术为支撑，以玩家需求为核心且不拘泥于俗套的创新意识为主导，以精致的游戏画面、酷炫的游戏特效、充满代入感的背景音乐为保障，力求向玩家呈现完美的饕餮盛宴。目前火舞已拥有《决战杀邑》、《异能勇者》、《召唤三国》、《龙城争霸》系列等优秀作品，从创作之初便吸引业界瞩目，上线即获得玩家的好评。除了上述游戏外，公司计划在2017年推出更加优质的精品手游大作。', 'success');
INSERT INTO `company` VALUES (1003, '上海夺游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1003', '', '上海夺游网络科技有限公司', '上海夺游网络科技有限公司是一家专注于网络游戏、移动游戏开发和运营的互联网公司。', 'success');
INSERT INTO `company` VALUES (1004, '江苏凤凰数字传媒有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032472.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1004', '', '江苏凤凰数字传媒有限公司', '江苏凤凰数字传媒有限公司（凤凰传媒数字化中心），成立于2008年5月，是凤凰传媒数字化建设的主要管理机构、协调机构和实施机构。公司致力于打造数字化管理平台，通过建设OA、ERP系统提高企业管理水平，推进数字出版及新媒体业务；开发运营凤凰优课数字教学系统、凤凰悦读等项目，并代表凤凰传媒在数字教育、大众阅读、游戏产品开发与运营等领域广泛开展对外合作，已投资、控股和管理多家业内知名企业。', 'success');
INSERT INTO `company` VALUES (1005, '东莞市圣火网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1005', '', '东莞市圣火网络科技有限公司', '东莞市圣火网络科持有限公司成立于2007年11月份,本公司是一家专业从事互联网、软件开发、电信增值，系统运营等为一体的综合性公司。公司由一大批专业技术人员和经验丰富的管理人员组成，目前设立有技术研发部、移动项目部、营销策划部、客户服务部等部门。公司秉承“诚信服务、用心沟通”的理念，致力于为移动电子商务平台开发及应用、电信增值服务、信息化领域的系统集成和应用创新提供移动增值业务的整体解决方案。作为中国移动的紧密合作伙伴，我们在短信，彩信，WAP，多媒体数字服务，流媒体等领域积累了丰富的整合和实施经验。推出了国内规模最大的移动购物商城-中国移动移购俱乐部。将多年的传统商业运作模式和21世纪先进的电子商务、无线网络营销推广技术紧密结合，缔造了电子商务全新的数字化营销模式的典范！', 'success');
INSERT INTO `company` VALUES (1006, '北京蓝色创想网络科技有限责任公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1006', '', '北京蓝色创想网络科技有限责任公司', '北京蓝色创想网络科技有限责任公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (1007, '浙江畅唐网络股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032473.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1007', '', '畅唐网络', '浙江畅唐网络股份有限公司成立于2007年，注册资金7500万元，目前拥有员工800余人。作为一家自主研发与运营互联网游戏，电子商务和生活服务网站的创新型IT企业，致力于打造中国传统地方游戏第一平台，为互联网用户提供多元化的娱乐服务。', 'success');
INSERT INTO `company` VALUES (1008, '深圳市掌上乐游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032474.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1008', '', '掌上乐游', '深圳市掌上乐游科技有限公司，成立于2013年，专注于手机游戏的代理发行，凭借公司各渠道优势资源及专业的游戏发行团队，致力于成为“中国顶尖的手机游戏发行商“，为移动终端用户提供优质的多元化的手机娱乐产品。', 'success');
INSERT INTO `company` VALUES (1009, '北京掌上飞鱼网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032475.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1009', 'flyfish', '飞鱼科技', '飞鱼科技(flyfish)是国内著名的移动开发团队，专注于android以及ios游戏及软件的研究和开发。公司开发的多款游戏及软件都排在各大手机市场的前列，是一个发展迅速、潜力巨大的手机游戏公司。', 'success');
INSERT INTO `company` VALUES (1010, '深圳市五一五游戏网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032476.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1010', '', '五一五游戏', '深圳市五一五游戏网络有限公司（简称\"五一五游戏\"）于2014年11月在深圳成立，坐落于深圳市南山区深南大道北侧艺园路西佳嘉豪商务大厦，五一五游戏致力于移动互联网领域的在线棋牌休闲游戏，为用户提供优质的产品和服务。五一五游戏以精品休闲棋牌游戏为战略核心，以开放共赢的合作理念推进开放平台合作，与各大分销渠道运营商实现共赢。', 'success');
INSERT INTO `company` VALUES (1011, '上海靓铭网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1011', '', '上海靓铭网络科技有限公司', '上海靓铭网络科技有限公司定位于专业的数字娱乐内容提供商和服务商，主要致力于大型互动娱乐平台、网络游戏的研发和运营。', 'success');
INSERT INTO `company` VALUES (1012, '微屏软件科技（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032477.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1012', '', '微屏软件科技（上海）有限公司', '微屏软件科技（上海）有限公司是国内最领先的互联网棋牌游戏公司，总部位于上海，在全国十余重要城市设有子公司、 分公司和办事处。作为国内最早的一批互联网游戏公司，历经十年发展，已从当初的十余人的小团队发展成为了上百人的成 熟企业。以自主研发运营网络休闲棋牌游戏为主要业务，其产品覆盖了PC端、移动端和智能电视端，研发和运营的游戏产品 超过200款，注册用户数已经突破1亿大关。PC端业务以\"人民棋牌\"为主打品牌，以区域化游戏平台为主要模式，建立了 20个区域化游戏平台。移动端业务与腾讯、百度、91、360、知名手机厂商等各大游戏渠道建立了深入的合作关系，其运营 的核心产品——\"掌心游\"系列，更成为国内最首屈一指的移动棋牌游戏品牌。智能电视端与中国电信IPTV、广电以及国内 知名电视厂商建立了深入的合作关系，成效显著。', 'success');
INSERT INTO `company` VALUES (1013, '九江世纪华游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1013', '', '九江世纪华游网络科技有限公司', '九江世纪华游网络科技有限公司是一家专注于网络棋牌研究、开发、及推广的高科技企业，总部及研发基地设立于风景秀丽的广州，并在全国各地设有分支机构。公司技术和研发实力雄厚，是国家项目的参与者，并被政府认定为“高新技术企业”。', 'success');
INSERT INTO `company` VALUES (1014, '成都简乐互动远景科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032478.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1014', '', '简乐互动', '成都简乐互动科技有限公司（简称简乐互动）是一家主要从事Android/iOS/WP等智能系统移动平台游戏开发以及发行的新型科技公司。公司技术实力雄厚，拥有自主研发的业内顶尖游戏引擎Phoenix3D。自2005年成立以来，在移动端智能平台上成功开发以及发行了近百款单机和网络游戏，饱受海内外用户好评，被国内外众多知名平台评为最佳合作研发商之一。', 'success');
INSERT INTO `company` VALUES (1015, '成都雷兽互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032479.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1015', '', '雷兽互动', '成都雷兽互动科技有限公司成立于2011年，作为一支坚韧、专注而又富有活力、爱心的优秀研发以及运营型团队，为玩家而设计，打造最温暖的游戏，让玩家重拾最简单的快乐是我们创作游戏的初衷。雷兽互动目前已经开发了《巴啦啦小魔仙》系列、《果宝特攻》系列、《龙族无双》、《九姑娘》等多款精品手机游戏（单机+网游），合作渠道过百家，包括和游戏、MM商城、爱游戏、沃商店、360、小米、百度、腾讯、UC、当乐、安智、应用汇、豌豆荚、悠悠村、爱奇艺、优酷、机锋、华为、OPPO、酷派、联想、金立、vivo等优质合作伙伴！', 'success');
INSERT INTO `company` VALUES (1016, '扬州易久天下网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1016', '', '扬州易久天下网络科技有限公司', '扬州易久天下网络科技有限公司成立于2015年8月，主要从事游戏平台建设，手机游戏的研发与发行，目前www.17istar.com已经搭建完成，年内重磅游戏《CBA传奇》及《鬼吹灯之摸金校尉》《鬼吹灯之寻龙诀》可在平台下载。', 'success');
INSERT INTO `company` VALUES (1017, '北京数巨互动创新技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032480.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1017', '', '数巨互动', '北京数巨互动创新技术有限公司成立于2005年，是一家专注于文化产业的互联网公司，公司目前员工总数30人左右，全面覆盖管理、技术研发、产品运维、市场营销等各个方面，核心团队拥有8年以上行业经验，是一个充满创造性和使命感的团队。数据互动致力于精品手机网游的研发与发行，力图打造国内最有趣最好玩的手游，给用户完美的终端游戏体验。', 'success');
INSERT INTO `company` VALUES (1018, '成都川报锋趣网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032481.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1018', '', '锋趣棋牌', '成都川报锋趣网络技术有限公司成立于2014年11月，坐落于四川日报集团办公大楼，隶属于四川日报报业集团，该公司由四川日报集团和浙江报业集团两大国内传媒巨头注资成立，同时引入国内一线的游戏平台——边锋游戏的技术力量，是一家潜力巨大的新型互联网游戏公司。成都川报锋趣网络技术有限公司在边锋游戏的技术支持下，现已开发出的19wan游戏平台，是有着四川本地独特风格的棋牌游戏平台。', 'success');
INSERT INTO `company` VALUES (1019, '广州无比乐网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032482.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1019', '', '无比乐游戏', '广州无比乐网络技术有限公司2004年正式成立，公司不断的尝试创新，并且坚持企业的核心价值观“专注”，始终执着开发高品质的智能手机游戏，开发了《土豪斗地主》、《土豪炸翻天》、《魔界奇兵光荣之路》等优秀的游戏。截至2014年，无比乐已开发并成功开发并运营了数款手机游戏，同时单机游戏陆续有优秀的作品问世。无比乐开发的游戏已实现全面跨平台支持iOS、Android、Symbian S60、Windows Mobile。', 'success');
INSERT INTO `company` VALUES (1020, '浙江达业网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1020', '', '浙江达业网络科技有限公司', '浙江达业网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (1021, '武汉指尖互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032483.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1021', '', '指尖互动', '武汉指尖互动科技有限公司是中国领先的移动游戏研发商与发行商，专注于手机游戏的研发发行。', 'success');
INSERT INTO `company` VALUES (1022, '江苏天网计算机技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1022', '', '江苏天网', '江苏天网计算机技术有限公司是江苏省从事信息安全测评、评估的第三方专业机构。公司本着“保障网络信息安全、维护网络环境和谐”的宗旨，坚持“专业、公开、公正、公平”的原则提供以下服务：信息系统安全等级保护测评、信息系统安全风险评估、信息系统定级及备案服务、网站安全检查、信息安全培训、信息安全咨询。依据授权对外开展信息系统项目安全测评服务。', 'success');
INSERT INTO `company` VALUES (1023, '广州凌鑫达实业有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1023', '', '广州凌鑫达实业有限公司', '广州凌鑫达实业有限公司，是云游控股有限公司（即Forgame集团）战略投资的公司，专注于自主研发和创新，公司70%员工为研发人员，注册资本逾千万元，地址在汇聚最高端科技水平和引领潮流时尚的广州市天河商圈，是一家集手机游戏研发和运营为一体的高新技术企业。', 'success');
INSERT INTO `company` VALUES (1024, '广州声动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032484.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1024', '', '广州声动', '广州声动网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (1025, '广州斯内普软件信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1025', '', '广州斯内普软件信息科技有限公司', '广州斯内普软件信息科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (1026, '北京久如技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032485.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1026', '', '北京久如技术有限公司', '北京久如技术有限公司是一家专注于移动互联网的创新型企业，2014年创建， 公司CEO在手游领域有着多年的管理经营经验。我们本着“专注于游戏、致力于为玩家创造快乐的互联网娱乐公司”的宗旨，为众多玩家及业内伙伴们提供一站式服务。业务范围：移动互联网产品的研发及运营、手机游戏自行研发、手机有限代理发行等。我们的团队年轻、朝气、温馨而又充满活力！如果你有足够的自信，还等什么，赶快加入我们去实现自己的梦想吧。', 'success');
INSERT INTO `company` VALUES (1027, '心动网络股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032486.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1027', 'XD.com', '心动网络', '心动网络（XD.com），国内极具知名度的游戏公司，中国互联网百强企业，旗下业务涉及游戏研发运营、动画制作、偶像娱乐等多个产业。公司创立于2002年，前身为中国最早的互联网分享网站之一的VeryCD。2009年起，公司开始打造心动游戏的自主品牌，致力于网页及移动客户端游戏的研发与运营。旗下拥有多款自主研发及独家代理，且在业内领先的互联网游戏产品：如网页游戏《天地英雄》、《盛世三国》、《神仙道》、《开天辟地》、《盛世三国2》、《开天辟地2》、《将神》、《深渊》、《仙侠道》，移动客户端游戏《神仙道》、《将神》、《仙侠道》、《横扫千军》、《接招吧！吕布》、《塔塔塔防陌陌哒》等众多深受玩家追捧喜爱的游戏作品。多年来，公司获得“中国最佳运营平台”、“十佳研发公司”、“最佳自研游戏厂商”等众多殊荣，其用心感动的企业理念深受广大玩家的肯定。', 'success');
INSERT INTO `company` VALUES (1028, '深圳柏思睿特信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1028', '', '柏思睿特', '柏思睿特是一家全球化网络信息科技公司。主营业务涵盖数据中心（英文缩写：IDC）、网络游戏开发运营、电子商务等领域，通过三大组合拳构筑起从原点至终端的一体化服务体系，从而为全球客户提供“超越价值，真心为您”的完美服务。旗下精品运营游戏：《秦美人》、《烈火战神》、《海神》、《龙将2》、《大侠传》、《将神》、《龙将》、《神曲》、《龙回三国》、《新梦幻之城》、《动天地》、《热血海贼王》、《龙城》、《大将军》、《小小海贼王》、《神仙道》、《盛世三国》、《傲视遮天》、《凡人修真2》等已超过120多款！', 'success');
INSERT INTO `company` VALUES (1029, '杭州掌动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032487.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1029', '', '掌动科技', '杭州掌动科技股份有限公司是由团队与浙江华策影视（股票代码：300133）共同投资成立，是一家集自主研发、运营于一体的移动终端互联网游戏企业。掌动科技的主要研发产品有《萌宠学园》、《傲天录》、《仙魔决》、《进击的海盗》等。', 'success');
INSERT INTO `company` VALUES (1030, '珠海达人网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032488.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1030', '', '达人游戏', '珠海达人网络科技有限公司成立于2006年12月28日，由全球首个富集通讯业务运营商“欢聚时代”旗下公司广州华多网络科技有限公司参股成立的互联网创业公司。2008年开始，达人游戏专注于网页类棋牌游戏中心的开发。', 'success');
INSERT INTO `company` VALUES (1031, '杭州爱掌科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032489.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1031', '', '爱掌科技', '杭州爱掌科技有限公司主要从事移动端（iOS和Android）游戏的开发和运营。', 'success');
INSERT INTO `company` VALUES (1032, '北京游道易网络文化有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032490.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1032', '', '游道易', '北京游道易网络文化有限公司成立于2011年11月，游道易(Yodo1)致力于发现并将优质的海内外游戏在中国乃至全球范围内发行推广。对于中国乃至全球的游戏玩家来说，游道易平台游戏是品质的保障，并在持续不断的输出超前的游戏体验。目前游道易已经有与60余家海内外知名开发商合作的经验，成功出品的游戏主要有《疯狂动物园》、《逃出实验室》、《削削达人》、《滑雪大冒险》、《滑雪大冒险2》、《割绳子2》、《天天过马路》、《狙击行动：代号猎鹰》等。', 'success');
INSERT INTO `company` VALUES (1033, '上海蓝指网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1033', '', '上海蓝指网络科技有限公司', '上海蓝指网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (1034, '北京梦幻天下科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032491.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1034', '', '梦幻互娱', '梦幻互娱成立于2007年，是一家研运一体的综合性互动娱乐企业。旗下主要业务有凤凰山庄游戏平台、天道游戏、天镜科技，全面布局网络游戏、移动游戏及VR游戏的开发及运营，产品类型丰富，全方位满足用户不断变化的娱乐需求。公司设立于北京，市场覆盖中国大陆、港澳台及东南亚等地区。', 'success');
INSERT INTO `company` VALUES (1035, '福建爱玩网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2017032492.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1035', '', '福建爱玩网络科技有限公司', '福建爱玩网络科技有限公司是创建于2011年的中国福建一家运营棋牌游戏、网页游戏、手机游戏为一体的运营公司。旗下189游戏平台是综合性游戏平台整合国内一线品牌的产品真正意义上实现了集游戏、交流、休闲、服务为一体的多元化平台。满足用户的浏览、交流、分享、娱乐等多元化与个性化诉求，并高度集成于后续发展自主研发的游戏运营平台，形成动网游戏联动服务模式，为用户提供丰富的娱乐体验与完善的服务。', 'success');
