INSERT INTO `company` VALUES (1147, '深圳市亿佳网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324155.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1147', '', '亿佳网络', '深圳市亿佳网络技术有限公司自2008年成立以来，历经中国游戏产业客户端游戏、网页游戏及手机手机游戏三次产业巨变，一直致力于“自研+自营”发展战略，是一家“立足本土，与世界竞争”的互联网互动数字娱乐研发与运营商。', 'success');
