INSERT INTO `company` VALUES (1196, '深圳悦游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324181.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1196', '', '悦游网络', '深圳市悦游网络科技有限公司旗下拥有网络游戏平台、数字交易平台和国际金融服务平台，是新时代金融体系的先行者和引领者。 公司定位于全球化金融战略，创建了数字货币交易平台大壹币，得到国际间用户的广泛认可和好评，迅速成为了全球最大的数字交易平台之一。', 'success');
