INSERT INTO `company` VALUES (1249, '深圳云中信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324210.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1249', '', '深圳云中信息技术有限公司', '深圳云中信息技术有限公司以手机游戏开发为主营业务，早年就涉足手机网络游戏开发。公司立足信息娱乐产业，在手机网络游戏、手机单机游戏、无线通信等领域都具有非常丰富的产品开发、运营和推广经验，尤其是在手机网络游戏方面有着卓著的成果。', 'success');
INSERT INTO `company` VALUES (1250, '南京加摩信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324211.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1250', '', '南京加摩信息科技有限公司', '南京加摩信息科技有限公司成立于2013年，公司注册资本1000万元人民币，注册地南京，致力于全球移动运营商移动增值业务运营领域，为全球移动运营商提供前瞻性的、高价值和高体验的移动增值运营服务。加摩在南京设立产品研发中心、运营服务中心；为全球移动运营商提供从业务战略咨询、内容提供以及运营顾问服务直到业务产品运营等多层次、多方位的专业增值服务。目前公司已与中国移动游戏基地、中国移动MM应用商场、中国电信爱游戏、中国联通沃商店等运营商游戏应用平台建立合作关系。', 'success');
INSERT INTO `company` VALUES (1251, '深圳市卓页互动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324212.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1251', '', '深圳市卓页互动网络科技有限公司', '深圳市卓页互动网络科技有限公司成立于2007年9月，致力于休闲娱乐的网页游戏的自主研发和营运。', 'success');
