INSERT INTO `company` VALUES (1370, '深圳市正合同越科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324282.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1370', '', '正合同越', '深圳市正合同越科技有限公司经营范围包括计算机软件的技术开发、技术转让及相关信息咨询，网页的设计等。我们尊崇“踏实、拼搏、责任”的企业精神，并以诚信、共赢、开创经营理念，创造良好的办公环境。', 'success');
INSERT INTO `company` VALUES (1371, '家乡互动（厦门）网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1371', '', '家乡互动', '家乡互动是一家新兴实力软件开发公司，是一家专业从事网络棋牌游戏开发和运营的高科技软件技术公司，主要研发项目涉及PC网络游戏、手机网络游戏二大方面。目前已成为厦门市最具规模的游戏开发运营公司之一，公司先后获得系统集成国家二级资质，软件开发国家认定企业，国家文化部网络文化经营许可，ICP网络运营资质。', 'success');
INSERT INTO `company` VALUES (1372, '杭州擎鹏网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324283.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1372', '', '擎鹏网络', '杭州擎鹏网络科技有限公司，是专业网络游戏研发与运营企业，公司具备相关的网游经营资质。目前运营的游戏产品已涵盖PC客户端版、手机版多个领域，主要产品有开心三张，欢乐牛牛、开心十三张、中国象棋、新百家乐、奔驰宝马、深海捕鱼等在内的十几款游戏产品，深受玩家的喜爱。', 'success');
INSERT INTO `company` VALUES (1373, '重庆玖度科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324284.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1373', '', '玖度科技', '重庆玖度科技有限公司是一家主要经营内容为面向智能手机平台的单机以及网络游戏的企划和运营的公司。玖度科技专注手机游戏的早期孵化，并拥有自己手机游戏平台，从事手游的发行和运营。', 'success');
