INSERT INTO `company` VALUES (1378, '广州市汇邦通信技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1378', '', '汇邦通信', '广州市汇邦通信技术有限公司依托技术创新和稳健推进，创造了良好的发展前景。 公司着力打造出了一支精干的团队，建立了完整的自有渠道和代理渠道。 本着“资源共享，利益共得”的原则，我公司将秉承“服务成就品牌，团队缔造奇迹”的文化！', 'success');
INSERT INTO `company` VALUES (1379, '兰州嘻哈乐网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1379', '', '嘻哈乐网', '兰州嘻哈乐网络科技有限公司主营业务包括计算机技术咨询服务；互联网网络技术、电子技术信息、网络设备的设计开发与销售；通讯工程、网络工程的设计及施工（凭资质证经营）。', 'success');
INSERT INTO `company` VALUES (1380, '北京飞动乐驰信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324287.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1380', '', '飞动乐驰', '北京飞动乐驰信息技术有限公司以服务顾客为前提，恪守信誉，一切以使合作客户获利更多、发展更快为宗旨，以用户满意为最终目标。本着一流品质、高效发展、热情服务的办事原则，致力于打造业内知名品牌！', 'success');
INSERT INTO `company` VALUES (1381, '成都启乐科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324288.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1381', '', '启乐科技', '成都启乐科技有限公司是一家专注于移动游戏产品开发运营的技术型公司。以用户体验为前进导向，优质游戏为开发宗旨，始终坚持在移动游戏自主研发的前线。团队成员拥有丰富的业内经验，为公司成长和产品研发运营提供着有力的支撑。目前旗下游戏产品已覆盖ios及安卓平台。', 'success');
INSERT INTO `company` VALUES (1382, '深圳维萌海风科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324289.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1382', '', '维萌海风科技', '深圳维萌海风科技有限公司主要从事游戏开发和电子商务，致力于网络游戏的运营与研发，我们的愿景与使命是成为中国最优秀的社会化网络互动娱乐服务运营商。通过建立一个以用户为核心的社会化网络游戏互动平台，为每一个用户带来最大的快乐体验。', 'success');
INSERT INTO `company` VALUES (1383, '深圳市云速信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324290.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1383', '', '云速游戏', '云速游戏与中国移动以及中国电信建立深度合作关系，充分利用中国移动中国电信游戏基地以及定制机预装等优势渠道资源进行游戏产品的整体推广运营，并提供详细的接入以及运营一站式服务。', 'success');
INSERT INTO `company` VALUES (1384, '成都欢乐联盟科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324291.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1384', '', '欢乐联盟', '成都欢乐联盟科技有限公司成立于2014年5月，致力于互联网手机游戏的开发与应用。 欢乐联盟成员均来自于国内各大知名游戏公司，团队核心成员均为业内多年资深经验，通过短短的时间，凝聚成一支目标明确的队伍。公司着眼于更宽广的开放式手游竞争平台，高瞻远瞩，吸纳国内外优秀手游经验，取长补短，不断进取。', 'success');
INSERT INTO `company` VALUES (1387, '广州顶东信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1387', '', '顶东信息科技', '广州顶东信息技术有限公司是一家专业从事短信群发及软件开发的高新技术企业，是广州市短信群发行业的领先者，有着多年的电信通信技术研发与数据业务推广经验。致力于短信群发应用技术的开发、产品销售和系统集成，为广大客户提供全方位的精准短信群发应用解决方案。', 'success');
INSERT INTO `company` VALUES (1388, '成都谊睿拓信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324293.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1388', '', '成都谊睿拓信息技术有限公司', '年青的移动互联网公司 ，致力于移动娱乐手游，为玩家提供一流体验的手游产品。 用户是核心，品质是基础，专注坚持是脊梁，快乐工作不能忘。我们正在精心打磨自己的产品，积累精品化团队的经验。共同创造、共同甘苦、共同成长、共享胜利。', 'success');
INSERT INTO `company` VALUES (1389, '宁波乐游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1389', '', '宁波乐游', '宁波乐游网络科技有限公司成立于2013年，拥有专业的游戏运营与服务能力，团队核心成员均来自于网络游戏行业知名公司，拥有多年网络游戏运营管理及用户服务经验。专业的游戏产品运营能力、强大的推广渠道控制和五星级客服的服务。', 'success');
INSERT INTO `company` VALUES (1390, '北京煜森科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/bd.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1390', '', '煜森科技', '“768游戏”隶属于北京煜森科技有限公司，是一款深受大众好评的棋牌类游戏，它以“益智、休闲、娱乐”为核心诉求，为棋牌游戏爱好者量身打造的一款任务型棋牌游戏。 有即开即打零等待的比赛、采用了淘汰赛等基本赛制，支持和满足万人同场参与比赛等需求。 每场比赛随机配桌等特点，杜绝任何作弊行为，为棋牌游戏爱好者提供了一个公平、公正、便捷的游戏平台。', 'success');
INSERT INTO `company` VALUES (1391, '福建省华世伟业信息科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324294.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1391', '', '华世伟业', '福建省华世伟业信息科技股份有限公司是一家从事网络游戏经营及开发的企业。旗下游戏产品有：棋牌迷游戏中心、棋牌乐游戏中心、闽南游游戏中心、厦门游游戏中心、咔遛游戏中心、楚汉游戏中心、豆子世界。', 'success');
INSERT INTO `company` VALUES (1392, '深圳云步互娱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324295.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1392', '', '云步互娱', '深圳互娱网络科技有限公司是专注于为艺演娱乐产业提供深度产品开发、运营服务的机构。业务内容包含网站建设、技术开发、社媒运营、活动策划等。总部位于深圳，设广州分公司，北京办事处。', 'success');
INSERT INTO `company` VALUES (1393, '北京瓦力网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/20170324296.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1393', '', '瓦力网络', '北京瓦力网络科技有限公司是雷军投资的移动互联网企业，一直与小米公司合作紧密。包括原小米内部的UE中心、小米应用商店内的游戏类App运营一直由瓦力团队负责。而瓦力网络的官网直接等同于小米游戏。', 'success');
