INSERT INTO `company` VALUES (1449, '武汉万维领域网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1491978174482.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1449', '', '万维领域网络', '武汉万维领域网络技术有限公司成立于2014年，公司注册于北京，运营团队立足于湖北，万维公司现为垄上集团控股公司。', 'success');
INSERT INTO `company` VALUES (1450, '湖南快乐芒果互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1491978604901.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1450', 'MANGOFUN', '芒果互娱', '湖南快乐芒果互娱科技有限公司于2014年3月26日在长沙市工商行政管理局高新技术产业开发分局登记成立。法定代表人郑华平，公司经营范围包括计算机技术开发、技术服务；计算机技术咨询等。', 'success');
