INSERT INTO `company` VALUES (1454, '北京七众网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1491980819583.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1454', '', '七众网络', '北京七众网络科技有限公司成立于2016年。公司的营业范围包括：技术开发、技术转让、技术咨询、技术服务；电脑图文设计、制作；设计、制作广告等。', 'success');
INSERT INTO `company` VALUES (1455, '上海猎鹰网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1491981016701.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1455', '', '猎鹰网络', '上海猎鹰网络有限公司，成立于2014年，总部位于上海，在北京福州均设有研发机构，是一家致力于手机游戏的研发、运营的游戏开发公司。项目的每个阶段有相应的设计评审、代码评审，帮助员工提高设计水平。', 'success');
INSERT INTO `company` VALUES (1456, '郑州原力网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1491981429673.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1456', '', '原力网络', '郑州原力科技有限公司是一家网络技术开发，技术咨询及技术服务；计算机软件开发，技术咨询及技术转让。', 'success');
INSERT INTO `company` VALUES (1457, '深圳高新网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1491983392138.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1457', '', '高新科技', '深圳市高新科技有限公司（简称高新科技）成立于2004年，是一家专注研发的网络娱乐软件开发商。', 'success');
INSERT INTO `company` VALUES (1461, '湖南联盛网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493022208259.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1461', '', '联盛科技', '湖南联盛网络科技股份有限公司（以下简称“联盛科技”）成立于2008年，是湖南首家新三板挂牌上市的游戏企业（股票代码：835982）。 联盛科技专注于棋牌赛事，在互联网产品、游戏产品研发、运营和推广方面有着十分丰富的经验和资源优势。联盛科技以“成为中国电视体育电竞平台领航者”为发展目标，致力于为用户提供PC、TV、手机三屏合一的电竞2.0服务。', 'success');
INSERT INTO `company` VALUES (1462, '浙江剑龙网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493084362829.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1462', '', '浙江剑龙网络科技有限公司', '浙江剑龙网络科技有限公司是一家从事网络游戏服务、安防绿化和娱乐行业投资运营的多领域企业，是国内知名的项目开发投资运营商，专门提供专业棋牌游戏、德州扑克体育文化交流俱乐部、安防以及园林绿化工程的全方位发展的企业。2013年剑龙网络科技正式注册成立，注册资金达1000万，并坐落于中国印刷之城——温州市龙港，成为浙江地区最知名的网络娱乐开发公司，并创造了良好的口碑。公司成立之后，积极拓垂直信息等领域，创建了门户、社区、交友服务一条龙的用户服务模式，成为中国网络企业发展的新航标。', 'success');
INSERT INTO `company` VALUES (1463, '上海冰穹网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493084937141.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1463', '', '冰穹互娱', '上海冰穹网络科技有限公司（Dome interactive entertainment），曾以“钱宝游戏”为品牌，成立于2015年，是一家全方位立体化的集群式互动娱乐企业。', 'success');
INSERT INTO `company` VALUES (1464, '深圳市超神互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493086175061.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1464', '', '超神互娱', '《深圳市超神互娱科技有限公司》是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (1465, '阆中市普罗米网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493086329596.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1465', '', '普罗米网络', '', 'success');
INSERT INTO `company` VALUES (1466, '北京安趣科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493087009180.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1466', '', '安趣娱乐', '安趣股份成立于2011年，创始人为孙祖德，注册资本3900万元。公司初始得到德迅、清科天使投资，并在Pre-A轮天使继续跟投，新增海通文化创意产业基金及优秀互联网个人投资。2016年1月22日正式收到新三板股转系统同意挂牌函。安趣完全符合创新层意见稿标准2要求，进入优质企业行列。', 'success');
INSERT INTO `company` VALUES (1467, '杭州连珠科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493087486333.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1467', '', '杭州连珠科技有限公司', '杭州连珠科技有限公司成立于2014年，致力于k12领域在线教育产品的研发、设计和创新。公司推出的核心产品“mbaby学习网，以“优化传统教育， 提供自适应的学习模式”为使命， 致力于让每一个学生都能因材施教。产品借助大数据挖掘、 智能分析来实现对用户学习情况的了解，制定适合自己的学习方式和计划， 让学生从传统教育题海模式里走出来，释放他们更多有效时间， 提高学习效率、培养自学能力。', 'success');
INSERT INTO `company` VALUES (1468, '四川掌中世家网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493087517193.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1468', '', '掌中世家', '四川掌中世家网络有限公司成立于2016年，注册资金超千万，公司自主研发地方棋牌游戏平台，技术团队拥有超10年游戏开发经验，其开发过的游戏为千万玩家服务过，公司旗下的八戒地方麻将平台正在申请软件著作权和广电总局游戏批号，平台为正规合法的健康棋牌游戏平台，坚决抵制赌博行为，坚决打击破坏游戏公平性的非法外挂程序。公司正与某大数据公司签订战略合作协议，以后将通过大数据统计为广大棋牌爱好者提供更加流畅爽快的游戏体验，八戒地方棋牌平台最终将成为玩家心中最爱的棋牌线上切磋平台。', 'success');
INSERT INTO `company` VALUES (1469, '北京嘉合旭元科技发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493087773628.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1469', '', '嘉合游戏', '北京嘉合旭元科技发展有限公司（简称：嘉合游戏）：成立于2012年12月12日，总部位于首都北京，是一家集互动娱乐产品研发与运营为一体的互联网企业，专注于网络游戏的VR游戏的研发与运营。目前已是美国HTC Vive 、三星GearVR、索尼SonyPlayStationVR等各大VR产品的游戏内容提供商。嘉合游戏的主要产品：人人德州(vrqka)、奥马哈扑克(VRdzpk)、皇家同花顺(hjths)、VR德州扑克(vrdzpk)和VR扑鱼(vrpuyu)等系列产品的不同语言版本。', 'success');
INSERT INTO `company` VALUES (1470, '海南金通路网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493088416399.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1470', '', '金通路网络', '', 'success');
INSERT INTO `company` VALUES (1471, '浙江电宇网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493089494739.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1471', '', '电宇互动', '浙江电宇网络科技有限公司，简称“电宇互动”，位于滨江区滨康路352号中控系统集成大楼，注册资本2000万元人民币。电宇互动致力于研发和运营手机游戏，PC、页游，VR游戏。公司创始团队有多年休闲类、棋牌类游戏平台运营经验，月收入千万元级别的经验；核心团队曾效力于动视暴雪、PDE等著名国外游戏公司，参与开发过多款3A级大作。', 'success');
INSERT INTO `company` VALUES (1472, '大庆市庆莹科技开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493089856478.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1472', '', '庆莹科技', '大庆市庆莹科技开发有限公司成立于2016年11月22日，注册资金1000万元人民币，是一家从事网络游戏设计、开发、制作的专业网络公司，具备丰富的网络运营经验及雄厚的互联网技术支持，现独立开发运行的庆莹娱乐棋牌游戏，以其精良的游戏制作、趣味性及互动性，也已在黑龙江地区建立了良好的市场基础及群众口碑。大庆市庆莹科技开发有限公司将以卓越的服务品质、专业安全的技术服务实力，为不同群体的用户提供更优质的服务。', 'success');
INSERT INTO `company` VALUES (1473, '深圳博十强志科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493186352289.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1473', '', '博十强志', '深圳博十强志科技有限公司是业内知名的移动互联综合方案提供商，尤其在手机游戏领域享有盛名，于2015年全面进军手机游戏行业，已经在广州设立了运营中心，在深圳设立了研发中心，致力于成为手机游戏行业的排头兵。博十科技拥有卓越的技术创新能力，公司产品研发人员比例近70%，核心成员30%具有硕士学历，有多个覆盖海量用户群应用的开发经验。核心成员多年的技术沉淀，使团队积累了丰富的手机游戏研发与运营经验，树立了自己的行业核心竞争优势，也彰显了公司力将产品打造成细分领域标杆的精品策略。', 'success');
INSERT INTO `company` VALUES (1474, '北京微触信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493190719479.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1474', '', '微触信息', '微触科技是中国领先的网络安全技术企业、信息安全云服务商之一。专注于网络安全，首创点触验证码为核心的新一代互联网云安全系统。团队成员具有多年安全领域及数据处理等方面技术积累和经验，是一个开放自由，充满活力的整体，是一个力求将产品做到极致的技术驱动型团队。微触科技于新媒体创业大赛总决赛中以骄人战绩斩获梦工场传媒数百万元天使投资，首批入驻传媒梦工场，历经四年发展至今，公司业务稳定、持续快速增长,截至目前为止，点触用户总量达七万余家，仅在社区站点BBS领域，点触云安全系统已服务两万六千余家用户，深受好评，其中包括金山快盘、IT之家等大中型客户。', 'success');
INSERT INTO `company` VALUES (1475, '镇江傲游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493191173936.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1475', '', '傲游网络', '镇江傲游网络科技有限公司成立于2014年5月7日。公司从事计算机软件及网络研发，设计，销售等。计算机的技术研发，技术培训，技术服务，技术服务等。', 'success');
INSERT INTO `company` VALUES (1476, '上海实牛信息技术服务有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493192705312.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1476', '', '上海实牛信息', '上海实牛信息技术服务有限公司致力于移动互联网游戏产品的自主研发和运营，公司以人为本，资金雄厚，具有强大的技术团队、富有创造力的设计能力、对国内外文化的深刻理解和丰富的运营经验，能够迅速满足用户不断变化的需求及游戏市场的发展，为玩家提供高品质互动性的游戏产品，通过游戏产品，为玩家创造轻松、愉悦的快乐体验，最大限度的为玩家挖掘网络娱乐产业的乐趣。', 'success');
INSERT INTO `company` VALUES (1477, '深圳市掌玩网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493193383722.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1477', '', '掌玩网络', '深圳市掌玩网络技术有限公司，简称：掌玩网络，是国内最大的移动对战平台网络公司，旗下有手机端及智能电视端跨移动平台最大游戏对战社区KO“竞技台”、模拟器游戏平台KO“电玩城”，自主研发的对战引擎MatchVS已成为移动游戏对战开发标准配制，目前KO电玩城用户已超过1000万，成为腾讯、阿里、百度、小米、乐视、康佳、创维、海信、TCL独家战略合作伙伴。', 'success');
INSERT INTO `company` VALUES (1478, '江苏凯顺科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493194189835.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1478', '', '凯顺科技', '江苏凯顺科技有限公司成立于2015年，注册资金1000w，是网络游戏开发商和运营商结合的综合型互联网公司之一，游戏新锐企业的代表，其业务同时涵盖游戏研发、推广及运营。公司创建以来，迅速凝聚了一批数字娱乐高端技术性人才，目前江苏凯顺科技有限公司同时在北京、南京设有多家子公司，因为具有丰富研发、推广、运营的团队以及多市场、全方位立体式发展，为所有在线游戏产品，提供了全方位的市场拓展规划与运营根基。', 'success');
INSERT INTO `company` VALUES (1479, '北京火柴互娱科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493195435861.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1479', '', '北京火柴互娱科技股份有限公司', '北京火柴互娱科技股份有限公司，成立于2012年5月，立志于打造全球领先的互动娱乐品牌。基于北美先进的游戏工业基因，立足于移动互联网，火柴互娱为海内外用户提供优质的移动游戏。以泛娱乐为战略目标，推广VR前沿技术，打造全方位的移动娱乐方式，开创全面互动娱乐新时代。', 'success');
