INSERT INTO `company` VALUES (1503, '深圳市虎跃网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493790746113.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1503', '', '虎跃网络', '虎跃网络科技有限公司致力打造中国最专业的电竞视频提供商，提供原创、高清的竞技游戏解说视频及各类与电竞相关的自制节目，包括英雄联盟、 DOTA2、 炉石传说、 德州扑克WSOP世界赛等。在资讯爆炸的现代，我们希望通过高质量的原创视频，连接世界各地的玩家与观众，为竞技带来更专业诠释的同时，引领中国电竞文化。', 'success');
INSERT INTO `company` VALUES (1504, '深圳市竖魂网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1493876943037.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1504', '', '竖魂网络', '竖魂游戏平台是中国顶尖的专业游戏运营平台，为中外游戏用户提供尖端、前沿的游戏精品；竖魂游戏致力于游戏精细化运营与优质的客户服务，成为深受玩家喜爱的国际化品牌游戏运营商。', 'success');
