INSERT INTO `company` VALUES (1539, '北京炎石天创科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1494987709253.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1539', '', '炎石天创', '炎石天创是一家新锐手游发行商，成立于2015年。公司管理团队从业经验丰富，国内团队发行经验丰富，海外团队本地化运营能力极强。炎石天创主要业务方向是海外和国内市场的手机游戏发行和运营业务，致力于为海内外用户提供业内顶级手游产品。', 'success');
