INSERT INTO `company` VALUES (1541, '北京奇客创想科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1495007050769.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1541', '', '北京奇客创想科技股份有限公司', '7K7K一直以优秀的整体形象、数量庞大的精品小游戏作品吸引众多用户的钟爱，拥有超过60%以上的用户是通过朋友介绍方式访问7K7K，忠实度很高，在网站定位上，以时尚、娱乐、休闲而受众，更适合年轻人群的时尚快捷的生活方式。', 'success');
