INSERT INTO `company` VALUES (1589, '上海米洛网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1496976078799.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1589', '', '米洛科技', '米洛秉承“自强不息，厚德载物”的企业精神，坚持诚信为本的价值观，以“提供专业的技术、优秀的产品和卓越的服务，以信息化创造客户价值”为使命，专注于电子政务领域的软件与信息服务，为客户的信息化事业提供全方位的解决方案与服务，是领先的电子政务解决方案和服务提供商。', 'success');
