INSERT INTO `company` VALUES (1596, '武汉晃游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1496978517004.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1596', '', '武汉晃游网络科技有限公司', '武汉晃游网络科技有限公司成立于2014年，总部位于中国湖北省武汉市。晃游网络是一家以“手游”为核心，集手游产品开发、产品运营、游戏网站运营为一体的综合式的互动娱乐传媒公司。晃游始终坚持自主研发，致力于推动中国手机游戏行业的发展。', 'success');
