INSERT INTO `company` VALUES (1598, '武汉康美时代网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1496989993330.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1598', '', '武汉康美时代网络科技有限公司', '武汉康美时代电子商务有限公司是一家以时尚美容品、保健品、食品等为主营业务的专业电子商务公司。公司以网络销售为依托，以高效、安全、宽覆盖的物流配送系统为保证，致力为消费者提供优质生活产品，是一家充满朝气的企业，其无限的发展空间为您今后的职业发展打造了广阔的平台。', 'success');
