INSERT INTO `company` VALUES (1621, '深圳百里光芒网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1498014577792.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1621', '', '百里光芒', '作为互联网游戏研发商，百里光芒上线第一款自主小说改编游戏，并致力于打造一流独特视角的小说改编文化网游开发商。5年来，百里光芒在互联网领域中逐步发展，成功开发多款页游精品，不断的为玩家提供高品质文化类游戏产品。', 'success');
