INSERT INTO `company` VALUES (1632, '湖南红辣椒众创网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1498113695413.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=1632', '', '湖南红辣椒众创网络科技有限公司', '红辣椒，湖南省知名品牌；红辣椒股份公司旗下有山河国旅、红辣椒娱乐、百佬会资本等项目公司。在生活、工作节奏日益加快的今天，亲友相聚娱乐困难，红辣椒众创网络顺势而为，投入巨资研发出红辣椒跑得快、红辣椒麻将等时尚休闲类手游产品，并不断提升用户体验度和产品竞争力，以帮助体验游戏的玩家放松心情、释放压力、联络情谊。', 'success');
