INSERT INTO `company` VALUES (2310, '深圳市云客科技开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1515635212268.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2310', '', '云客科技', '深圳市云客科技开发有限公司，成立于2012年，是一家集自主研发、代理发行、投资孵化为一体的手游新锐企业。公司拥有稳定、专业的发行和研运团队，核心成员来自国内一线发行团队，发行经验丰富。云客游戏发行过多款精品手机游戏，其中《口袋妖怪联盟》、《霸业纵横》等都在国内手游市场上取得喜人成绩。目前，公司独代仙侠大作《天境》即将测试', 'success');
