INSERT INTO `company` VALUES (2313, '武汉掌游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516587229623.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2313', '', '掌游科技', '武汉掌游科技有限公司是中国新锐移动游戏公司，专注于手游发行、深度运营、场景分发和研发领域。安锋游戏一直致力于用移动互联网去改变世界，为中国玩家提供广泛而精彩的文化娱乐服务，并积极推动全球泛娱乐布局！', 'success');
INSERT INTO `company` VALUES (2314, '上海天赐之恒网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516587416621.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2314', '', '天赐游戏', '天赐游戏，新锐手游公司，由移动游戏领域资深人士创办，定位于精品游戏发行和运营商。研运一体化，整合行业上下游资源，为CP提供专业长线运营服务和市场化的引导，为渠道提供高品质游戏。', 'success');
