INSERT INTO `company` VALUES (2316, '北京展游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516761257900.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2316', '', '北京展游科技有限公司', '北京展游科技有限公司前身为成立于2010年的北京展程科技有限公司，创立初期就获得李开复“创新工场”的投资。公司成立仅仅4年已成为行业内盈利较高、发展较快的企业之一，同时也成为国内最具潜力的手机游戏研发与运营企业。从昔日的三人团队成长为如今近两百人战队，用户遍及台湾，韩国，泰国，日本，马来西亚等，获得傲人成绩。', 'success');
INSERT INTO `company` VALUES (2317, '芜湖易玩网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516761531295.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2317', '', '芜湖易玩网络科技有限公司', '芜湖易玩网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2318, '江苏易乐网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516761753806.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2318', '', '易乐网络', '易乐网络科技有限公司是国内领先的即时制 ARPG网页游戏和手机游戏研发商。公司团队组建于2010年1月，目前员工约700人，在镇江、上海、广州、北京、武汉等多个城市设有研发、运营、发行公司。公司自成立以来共推出二十款以上页游和手游产品，均取得不同程度的成功，其中“武尊”累计收入超过4亿元，“烈火屠龙”累计收入超过3亿元。产品销售到内陆和港澳台，并出口韩国、越南、泰国、北美、欧洲等多个国家和地区。2015年北京文投控股集团（股票代码：600715）100%全资收购易乐，构建页游+手游+VR游戏+游戏平台+IP影视联动+海外游戏等综合产业链游戏公司，同时并购耀莱影城发展影视娱乐产业。打造北京市文化创意产业航母！', 'success');
INSERT INTO `company` VALUES (2319, '北京络汀科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516846622577.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2319', '', '络汀科技', '北京络汀科技有限公司成立于2010年，是一家以Android为开发平台，从事多类型手机游戏、尤其以3D和休闲类游戏开发和推广为主的专业手机游戏公司。络汀科技以快乐下载为理念，以玩家体验为第一要求，自成立以来，不断的提升公司研发能力，积极开拓市场，以精品游戏来推动中国手机游戏业的发展。公司团队成员具有多年移动互联网从业经验，擅长游戏产品设计和开发，游戏运营，市场和渠道开拓。我们希望能够在移动互联网快速发展的时代，与业界伙伴一起，为中国的手机游戏事业贡献一份力量。', 'success');
INSERT INTO `company` VALUES (2320, '深圳市微讯移通信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516846947223.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2320', '', '微讯移通', '深圳市微讯移通信息技术有限公司由国内移动互联网IT精英和有成功创业经验的专业团队创立， 现专注于国内领先的移动游戏开发和运营。公司在深圳、广州、上海、北京均设立移动游戏研发和运营中心。现拥有国内外一流的移动游戏运营团队， 可提供专业的、深度的游戏运营服务。现己建立的专业的移动游戏运营团队和庞大用户流量渠道，微讯移通具备快速、有效、深度的游戏开发和运营能力。', 'success');
INSERT INTO `company` VALUES (2321, '北京凯罗天下科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516847305395.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2321', '', '北京凯罗天下科技有限公司', '北京凯罗天下科技有限公司成立于2012年，是一家专业手机游戏工作室，目前主要产品有《保卫萝卜》系列，团队拥有多年大量级产品的开发及运营经验，希望坚持少而精的模式，创造优质的手游产品，工作地点位于北京CBD核心区。', 'success');
INSERT INTO `company` VALUES (2322, '杭州云鸽科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516847541785.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2322', '', '杭州云鸽科技有限公司', '杭州云鸽科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2323, '重庆话语科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516847824918.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2323', '', '话语科技', '重庆话语科技有限公司，创立于2010年，是一家致力于互联网、移动互联网领域数字化营销、产品研发及发行运营的集团型企业。', 'success');
INSERT INTO `company` VALUES (2324, '湖南草花互动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1516848207228.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2324', '', '草花互动', '草花互动，全名湖南草花互动网络科技有限公司，成立于2014年8月，是国内手机游戏新锐运营平台。主要从事移动游戏发行及深度运营等业务，旗下草花手游网、草花CMS平台发展迅速，目前已成为湖南地区最大的移动游戏平台[1]  。2016年，草花已成功深度运营《坦克前线：帝国OL》、《最佳阵容》、《战争指挥官》、《永恒纪元》等多款游戏，每款游戏月流水维持在上百万甚至千万规模。并与国内主流的手游研发商、发行商巨人网络、傲世堂、游爱互动、37手游', 'success');
INSERT INTO `company` VALUES (2325, '广州大娱信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517192153080.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2325', '', '大娱游戏', '广州大娱信息科技有限公司，简称大娱游戏，成立于2014年，是一家坐落于广州大学城的手机游戏开发商，大娱游戏以“大众娱乐，娱乐至上”为宗旨，致力于打造符合大众口味的娱乐王国。', 'success');
INSERT INTO `company` VALUES (2326, '天津猎手互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517192444370.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2326', '', '猎手互娱', '猎手互娱，成立于2016年7月，旨在打造全球领先的影视游戏互动娱乐品牌。基于影视娱乐的视野，立足于移动互联网时代，猎手互娱致力于为海内外用户提供优质的移动游戏。以影游互动为战略目标，打破影视与移动游戏的行业壁垒，打造IP孵化到影视改编再到游戏定制发行的产业链闭环', 'success');
INSERT INTO `company` VALUES (2327, '广州艾游信息科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517192623909.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2327', '', '艾游网', '广州艾游信息科技股份有限公司（简称“艾游”）成立于2007年，运营总部设在广州，同时在广州、杭州、上海、北京等地设立分支机构，目前拥有员工近600人。公司以网络游戏的运营和研发为核心业务，先后成功推出《兽血沸腾》、《龙腾世界》、《凡人修仙传》等多款大型网游，并将陆续推出《魔卡精灵》、《汉武大帝》、《悟空传》等网游大作。同时，艾游将战略布局单机游戏、移动互联网、网页游戏等互动娱乐领域，在单机游戏方面，于2011年成功推出“国民游戏”仙剑系列的最新续作《仙剑奇侠传5》，并将与法国育碧合作，推出全球顶级策略游戏《英雄无敌6》。', 'success');
INSERT INTO `company` VALUES (2328, '武汉爪游互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517192980869.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2328', '', '爪游互娱', '武汉爪游互娱科技有限公司是一家以手机游戏的代理发行与联合运营为主营的企业，爪游游戏自2013年成立以来，坚持以\"玩家为根本，服务为宗旨\"的使命，发行最精品好玩的手机游戏，打造一个最便捷、最人性化的手机游戏平台。', 'success');
INSERT INTO `company` VALUES (2329, '成都若娱互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517193254732.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2329', '', '成都若娱互动科技有限公司', '成都若娱互动科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2330, '深圳市豹风网络股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517193488008.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2330', '', '深圳市豹风网络股份有限公司', '深圳市豹风网络股份有限公司，成立2008年，2015年12月新三板挂牌上市（834643） ，注册股本3000万，公司立足于手游研发、运营、发行三大核心领域，开展跨影视，文学，动漫等多领域，多平台的泛娱乐战略模式。其团队规模150人，核心人员均来自国内外知名游戏公司。2013年，公司研发的U3D横版格斗手游《黑暗光年》作为中国手游市场首款U3D横版格斗手游，在国内与海外市场上反响巨大，目前仍在全球持续运营。公司将始终如一的坚持MMO精品理念，公司即将推出', 'success');
INSERT INTO `company` VALUES (2331, '北京焱鹿科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517193753852.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2331', '', '北京焱鹿科技有限公司', '北京焱鹿科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2332, '西安西品网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517364429123.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2332', '', '西安西品网络科技有限公司', '西安西品网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2333, '天津云畅科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517365021615.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2333', '', '天津云畅科技有限公司', '云畅游戏是一家专注于全球互联网娱乐产品的发行商和运营商，其主要业务涵盖移动游戏及其他泛娱乐产品。公司已于2015年12月成功登陆新三板，并已经正式启动A股IPO进程。目前正式员工规模300人左右，公司CEO高云峥先生本科毕业于北京理工大学,英国布里斯托大学硕士学位,曾就职于IBM、360等公司，后创办云畅游戏。公司核心成员均来自于业内知名游戏及互联网公司，整体团队具有成熟的产品制作及游戏发行、运营经验。云畅游戏具有极强的游戏研发及发行能力，已经成功打造并发行了多款A级手游产品。2016年5月上线的《黑衣剑士》仅安卓平台月流水就已突破5000万，目前该游戏进入平稳运营期。2016年10月，由若森数字授权的顶级国漫3D武侠手游《不良人2》也已经强势发布，上线首月流水破5000万，获得包括“金趣奖”“金鹏奖”“最具人气手游”等专业游戏媒体评选出的诸多奖项。凭借自身优势，云畅游戏与国内外一流厂商展开深度合作，已签下了多个正版热门、具有亿万用户基础的知名IP，续写行业成功传奇。', 'success');
INSERT INTO `company` VALUES (2334, '北京达唯科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517365205442.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2334', '', '北京达唯科技股份有限公司', '北京达唯科技股份有限公司成立于2011年秋，致力于移动终端游戏的研发与运营。是集移动终端游戏开发商、发行商和运营商为一体的网络科技公司。2016年2月26日，达唯科技正式在新三板挂牌公开转让，证券简称:达唯科技 证券代码:836020。公司的研发品牌CatCap Studio曾推出众多精品单机类移动游戏，“梦回南朝”、“爱养成”、“求合体”等作品在各大移动设备应用渠道均取得优秀成绩。2015年中旬，公司做出新的战略部署并扩大规模，成立发行事业部，以合作代理国内外优秀游戏的方式，通过本地化开发和优化，向智能移动设备用户提供丰富的休闲娱乐体验。', 'success');
INSERT INTO `company` VALUES (2335, '厦门青瓷数码技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517365437140.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2335', '', '青瓷数码', '厦门青瓷数码技术有限公司成立于2012年，是一家专业从事网络游戏研发、运营的互联网科技有限公司。公司致力于网络产品的开发与技术革新；坚持技术先进、理念先进、管理先进的战略；以研发“精品网游”为目标，力争打造成国内最优秀的研发、运营企业,为全球用户提供优质的互联网娱乐服务内容。', 'success');
