INSERT INTO `company` VALUES (2337, '霍尔果斯毅讯电子科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517795379390.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2337', '', '霍尔果斯毅讯电子科技有限公司', '霍尔果斯毅讯电子科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2338, '沈阳光音网视科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517795829363.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2338', '', '沈阳光音网视科技有限公司', '沈阳光音网视科技有限公司是国内网吧行业唯一具有国资背景的影视平台提供商，该公司由星辰在线、山东万佳、新加坡EPS、网酷科技等多家公司共同投资成立，是文化部批准的网吧影视娱乐内容提供企业之一，作为中国网吧影视平台专业解决方案的提供商，联合央视网、搜狐、酷6、激动网、乐视网、盛世骄阳等国内顶级视频企业及影视版权分销商，为国内网吧提供专业的高清影视服务。', 'success');
INSERT INTO `company` VALUES (2339, '广州龙谷信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517796058861.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2339', '', '龙谷游戏', '广州龙谷信息科技有限公司是一家致力于手机游戏发行的游戏公司。我们的目标是成为国内一流的游戏发行商。公司团队核心成员均有非常资深的游戏行业经验，与国内大型游戏研发商、渠道商有良好的合作关系。', 'success');
INSERT INTO `company` VALUES (2340, '深圳银狐游戏网络有限公', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517796312436.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2340', '', '银狐游戏', '银狐游戏是泡椒互娱旗下全新的发行游戏品牌公司。2015年10月，一支年轻的团队怀着对游戏的热爱，开启了银狐的篇章。银狐游戏成立于2015年10月，在短短两年内发展迅速，其运营能力、资源整合和买量经验方面的优势，吸引了多家知名渠道的合作，并屡创佳绩，成为深圳手游企业中的后起之秀。', 'success');
INSERT INTO `company` VALUES (2341, '成都聚友趣科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517796523644.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2341', '', '成都聚友趣科技有限公司', '成都聚友趣科技有限公司（简称聚友趣科技）位于成都市高新区，是在国内知名游戏工作组——“自由拍档”的基础上发展而来。公司秉承 “为玩家创造有趣的世界，给玩家精彩的体验” 为宗旨，始终秉持着不断创新、时时用心的精神致力于手机游戏研发。目前已发展成为成都地区领先的手机游戏研发企业。聚友趣科技公司拥有一支具有国际化理念并在游戏行业拥有丰富专业经验和执行能力的研发团队。曾经自主研发并发布了手机游戏《海王之王》、《阿尔约传说》、带领开发了大型2D网络游戏《轮回Online》、参与开发了大型3D网络游戏《峥嵘天下》。公司目前研发的游戏在海外游戏市场中脱颖而出，风靡海外。目前已被法国、德国、俄罗斯、越南等国家和地区代理运营。', 'success');
INSERT INTO `company` VALUES (2342, '上海市欣承网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517797095205.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2342', '', '上海市欣承网络科技有限公司', '上海市欣承网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2343, '深圳市方块游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517797393808.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2343', '', '深圳市方块游网络科技有限公司', '深圳市方块游网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2344, '北京猎豹网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517797618327.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2344', '', '猎豹移动', '猎豹移动是一家全球领先的移动互联网公司，致力于为全球用户提供卓越的应用。公司于2010年11月，由金山安全和可牛影像合并而成，继承了金山18年的安全技术积累和可牛影像的互联网基因。2014年5月8日，猎豹移动在纽交所正式挂牌上市，交易代码CMCM。猎豹移动是中国移动互联网公司出海的领军者。截至2017年3月，公司产品在全球移动端月度活跃用户约为6亿，用户分布覆盖全球200多个国家及地区。其中，77%来自以欧美为主的海外市场。', 'success');
INSERT INTO `company` VALUES (2345, '实丰(深圳)网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517882289782.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2345', '', '实丰网络', '实丰（深圳）网络科技有限公司成立于2014年1月20日，是实丰文化旗下的全资子公司。 实丰（深圳）网络科技有限公司以下简称(实丰网络)由多名游戏行业从业者牵头组成，主要以开发移动网络游戏为主，公司地址坐落在深圳南 山科技园科兴科学园。', 'success');
INSERT INTO `company` VALUES (2346, '深圳小多科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517882583470.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2346', '', '深圳小多科技有限公司', '深圳小多科技有限公司简称“小多游戏”，成立于2015年初，主要从事网页游戏、手机游戏的发行和运营业务，是国内领先的网络游戏代理发行商之一。', 'success');
INSERT INTO `company` VALUES (2347, '杭州掌盟软件股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517882743732.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2347', '', '杭州掌盟软件股份有限公司', '杭州掌盟软件股份有限公司成立于2007年6月，是一家集移动游戏研发与发行、移动阅读、影游互动、移动互联网广告于一体的新型泛娱乐移动互联网公司。公司目前有员工160多人，其中技术研发团队100多人，平均年龄26岁。公司自成立之日起即定位于移动互联网产品的开发和服务，经过近十年的发展，杭州掌盟已成为国内实力强劲的移动互联网公司之一。公司目前已拥有60多件著作权，并先后获得“杭州市高新技术企业称号”“杭州成长型行业品牌新锐企业十强”“重点骨干企业”“优秀文创企业称号”“杭州市企业高新技术研究开发中心”等荣誉。作为最具影响力的移动互联网渠道商之一，杭州掌盟软件股份有限公司于2015年11月23日正式在新三板挂牌。', 'success');
INSERT INTO `company` VALUES (2348, '北京寻梦科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517882919567.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2348', '', '寻梦游戏', '北京寻梦科技有限公司是一家智能移动终端娱乐（服务）开发商，拥有丰富的自主知识产权手机游戏产品，致力于提供智能移动终端的轻娱乐服务和开发者增值服务，意在打造优质的轻娱乐化平台和开发者交互平台。寻梦科技坚持以游戏开发、平台运营为主线，多领域应用为补充的发展方向， 在海归技术核心和本土互联网精英的带领下，将丰富的无线互联网经验转化为用户喜爱的移动互联网产品。 公司自成立以来，在 iPhone、Android、iPad 领域上推出一系列高下载量、高用户量的优质游戏及应用， 在市场与行业内均取得良好口碑。', 'success');
INSERT INTO `company` VALUES (2349, '北京强手网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517883085315.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2349', '', '北京强手网络科技有限公司', '北京强手网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2350, '北京冰河马科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517883302607.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2350', '', '北京冰河马科技有限公司', '北京冰河马科技有限公司从事全球手游发行业务 (中、日、韩、港、澳、台、美、欧洲等地区)， 提供专业的产品运营及人员管理让合作伙伴们能够更快一步获得移动互联网信息，在合作中获得共赢。', 'success');
INSERT INTO `company` VALUES (2351, '喜悦动漫（杭州）股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517883519468.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2351', '', '喜悦动漫（杭州）股份有限公司', '喜悦动漫(杭州)股份有限公司坐落于杭州市滨江区，是文化部、财政部、国家税务总局认证的动漫企业，以动漫发行及授权、动漫展会服务、动漫制作服务和游戏开发运营为主营业务，是立足于动漫产业链一体化经营的综合文化创意型企业。经过数年的努力，公司已形成以版权内容创作与传播为核心，动漫衍生业务为拓展的产业模式。公司的领导团队实力雄厚，核心人员具有十多年的动漫产业从业经验，具有对动漫产业各方资源的整合、提升，进而变现的能力，特别在播映环节能够获得优质的发行渠道。公司是中国文化产业协会理事单位，中国未成年人网络权益保护协会发起单位。2016年4月公司在全国中小企业股份转让系统挂牌上市。', 'success');
INSERT INTO `company` VALUES (2352, '奇创星动（北京）信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517883941079.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2352', '', '奇创星动', '奇创星动（北京）信息科技有限公司（简称“奇创星动”）是国内一家新兴游戏开发公司，公司由一群热爱游戏以及拥有远大理想与抱负的青年人组成，还有手游界的资深人士加以扶持，使得公司成立不久就推出了多款高质量产品。其中《三国萌萌哒》手游的上线，让国内玩家们重新认识到了不一样的三国世界，公司也凭借该款游戏，一战成名。', 'success');
INSERT INTO `company` VALUES (2353, '杭州虎牙科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1517884328497.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2353', '', '虎牙游戏', '杭州虎牙科技有限公司是一家二次元手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2354, '成都墨仙互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519611731412.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2354', '', '墨仙游戏', '成都墨仙互娱科技有限公司是一家游戏研发运营公司。', 'success');
INSERT INTO `company` VALUES (2355, '北京网乐无限科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519612024547.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2355', '', '北京网乐无限科技有限公司', '北京网乐无限科技有限公司是一家移动互联网和电子游戏多个行业的高科技创新企业,专注于影视及娱乐明星版权运营,版权游戏研发和发行等业务。', 'success');
INSERT INTO `company` VALUES (2356, '北京唯我乐园信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519612301379.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2356', '', '北京唯我乐园信息技术有限公司', '北京唯我乐园信息技术有限公司为MyGamez在国内的全资子公司。MyGamez是一家欧洲移动游戏领先代理发行商，总部位于芬兰赫尔辛基，在香港、上海、北京、天津、吉隆坡皆设有办公室。', 'success');
INSERT INTO `company` VALUES (2357, '广州傲玩信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519612649608.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2357', '', '傲玩游戏', '傲玩游戏，成立于2016年2月15日，旨在打造全球领先的移动娱乐品牌。基于全球化的视野，立足于移动互联网时代，傲玩游戏致力于为海内外用户提供优质的移动游戏。公司创立之初即签署了数款精品游戏全球独家代理权，傲玩游戏的创始团队由游戏领域经验丰富的行业精英组成。团队成员都有着丰富的移动游戏开发和发行经验。公司充分整合自身强大运营体系、立体化的营销能力和卓越的技术优势，建立起了出色的代理发行储备和研运体系。', 'success');
INSERT INTO `company` VALUES (2358, '深圳市围信信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519613931157.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2358', '', '深圳市围信信息科技有限公司', '深圳市围信信息科技有限公司专注于应用APP及手游的研发、发行及推广。', 'success');
INSERT INTO `company` VALUES (2359, '上海游雁网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519614243019.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2359', '', '游雁科技', '游雁游戏成立于2015年，公司座落天河科韵路，是一家自主研发和运营能力的游戏公司。目前，游雁公司有多款不同类型的手机游戏项目正在火热研发中，《天天爱仙侠》《灵域修真》《仙迹》《剑雨情缘》《天剑传说》《梦回仙域》  《幻想仙灵》，《黎明悠侠》每款游戏都达到月收入数千万的流水。', 'success');
INSERT INTO `company` VALUES (2360, '广州乾游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519697245160.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2360', '', '乾游游戏', '广州乾游网络科技有限公司（以下简称“乾游游戏”）成立于2014年7月，是一家专注于手机游戏发行和运营服务的新锐游戏公司，注册资金1000万。由资深移动互联网人，前广州深海成员创立。总部设立在广州，创始团队曾任职于百度、4399、深海、菲音、 游爱等一流互联网公司，拥有丰富手游发行和运营经验。目前，乾游游戏已经成功发行手游大作《剑之刃》、《帝霸》、《日漫大暴走》，新作《梦幻仙缘》预计将在2015年6月公测，多款独代产品也在密切准备中，终将占据中国手游发行市场一席之地。', 'success');
INSERT INTO `company` VALUES (2361, '深圳鱿娱互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519697529983.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2361', '', '深圳鱿娱互动科技有限公司', '深圳鱿娱互动科技有限公司是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2362, '福建摩格网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519697707798.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2362', '', '摩格', '福建摩格网络科技有限公司（以下简称摩格）成立于2014年，位于福州市马尾区互联网游戏产业园三楼，办公面积达到1800多平方米。摩格由多位在游戏行业有着5-10年从业经验精英创办，具备手游策划，手游研发和手游运营的团队。摩格的每位成员对手游行业都保游深刻的热爱，理解。摩格是国内少数具备游戏开发、游戏运营，公会经营为一体的新兴游戏企业之一。自成立以来，摩格凭借坚实的实力基础，迅速布局手游产业，与百度游戏、360游戏、联想游戏等行业巨头达成深度战略合作伙伴关系。', 'success');
INSERT INTO `company` VALUES (2363, '上海赤月信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519698070958.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2363', '', '上海赤月信息技术有限公司', '上海赤月信息技术有限公司是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2364, '广州智竹网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519698272689.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2364', '', '智竹游戏', '广州智竹网络科技有限公司成立于2014年，是一家专注于手机游戏和网页游戏的开发和发行服务的新锐游戏公司。智竹的团队成员都是来自于知名游戏企业的行业精英，具有多款成功游戏的研发、发行、运营经验。   公司秉持“快乐游戏，快乐生活”的理念，致力于创造能给玩家带来快乐的游戏产品，打造国内一流的游戏研发和发行平台。', 'success');
INSERT INTO `company` VALUES (2365, '深圳乐聚互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519698526160.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2365', '', '深圳乐聚互动科技有限公司', '乐聚互动成立于2014年7月，致力于手机游戏发行、海外游戏代理以及游戏媒体网站运营。公司实力雄厚，团队实力强大，创业团队，拥有成熟手机游戏发行经验，并且成功发行过数款千万级流水手游产品。2014年8月由东谷集团注资数千万元入股乐聚互动欲共同打造手游行业新标杆。', 'success');
INSERT INTO `company` VALUES (2366, '广州游路网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519698797411.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2366', '', '广州游路网络科技有限公司', '广州游路网络科技有限公司是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2367, '成都雨墨科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519698972335.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2367', '', '雨墨游戏', '成都雨墨科技有限公司，成立于2013年3月，是一家集MMORPG手游自主研发、精品游戏代理等业务为一体的综合型游戏公司。 旗下产品《秦姬》、《魔灵觉醒》、《六扇门》 在以韩国、台湾地区为代表的亚洲市场和国内市场均创下佳绩， 是国产手游研发商在全亚洲市场的领先者。', 'success');
INSERT INTO `company` VALUES (2368, '霍尔果斯蓝鲸网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519699150158.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2368', '', '蓝鲸游戏', '霍尔果斯蓝鲸网络科技有限公司，成立于2015年，是一家以重度手机游戏为发展起点的运营发行公司，公司拥有雄厚的资本实力，强大的运营能力，重视玩家体验。', 'success');
INSERT INTO `company` VALUES (2369, '湖南省沃尔信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519699340510.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2369', '', '沃尔互动', '湖南省沃尔信息技术有限公司（VOLV LLC.）是世界领先的移动平台战争模拟类游戏独立开发商，成立于2011年初。公司专注于快速发展的移动娱乐市场。在增强现实、三维基础、计算物理、引擎技术等方面拥有核心技术，是Google、Apple、Amazon、Microsoft 认证的全球开发合作伙伴。公司拥有多款在海内外应用商店排名前列的游戏与应用。', 'success');
INSERT INTO `company` VALUES (2370, '广州彩瞳网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519699576829.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2370', '', '广州彩瞳网络技术有限公司', '广州彩瞳网络技术有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2371, '成都嘻游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1519699797266.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2371', '', '游戏橙子', '成都嘻游科技有限公司成立于2012年，公司总部位于四川成都，旗下有游戏橙子品牌，是一家以全球互动娱乐市场为战略目标，专注于移动互联网跨平台游戏研发的新锐公司。游戏橙子拥有多支熟掌握移动互联网游戏核心开发技术、具备丰富运营经验的优秀团队。公司聚集了来自全国各家知名互联网游戏公司的高素质人才。先后推出《军团之战》、《金字塔的宝藏》、《军团争霸》、《快来玩》等人气手游，已经受到业界的广泛关注和大量玩家好评。', 'success');
INSERT INTO `company` VALUES (2372, '杭州拓米科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520215171674.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2372', '', '拓米科技', '拓米科技致力于移动互联网游戏的开发、运营和推广，希望通过自身优秀的产品策划、研发、运营能力，打造用户喜爱的高品质游戏。我们的目标是向用户提供纯粹的、轻松的、高品质的的数字娱乐体验。', 'success');
INSERT INTO `company` VALUES (2373, '天津体育之窗互动娱乐有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520215423189.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2373', '', '天津体育之窗互动娱乐有限公司', '“体育之窗”是由国际投资公司与中美资深体育人士共同创立的一家从事体育场馆策划、运营、投资、管理的专业化公司。公司业务涉及体育赛事、文艺演出、传媒、体育场商业等领域。', 'success');
INSERT INTO `company` VALUES (2375, '浙江无端科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520216064834.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2375', '', '浙江无端科技股份有限公司', '浙江无端科技股份有限公司，2010年1月正式成立，是一家国内领先的高科技创业型网络游戏公司。公司致力于无端游戏的创作，在网页游戏和手机游戏领域都有自主研发的产品。 目前公司已有员工120余人，其中包括研发人员占比超过75%。公司骨干均来自国内知名游戏公司，具备多年游戏研发和运营经验，曾成功运作多款游戏。因此年轻化、高学历化 是这个团队的一大特色。并且公司具备文网文资质，是国内为数不多兼具研发实力与运营资质的网页游戏公司。', 'success');
INSERT INTO `company` VALUES (2376, '杭州谦游坊科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520218174785.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2376', '', '杭州谦游坊科技有限公司', '杭州谦游坊科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2377, '上海沪首网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520218806265.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2377', '', '上海沪首网络科技有限公司', '上海沪首网络科技有限公司。作为一家手游发行公司，主要集中于互联网、游戏、娱乐等高科技领域。沪首的团队具有IT、通信、游戏等技术背景及丰富的管理经验。出色的业绩及业内的美誉，赢得了广大同行及玩家的信赖。倡导“精细化”、“高品质”的运营理念，专注于精品手游大作的研发，推广和运营。', 'success');
INSERT INTO `company` VALUES (2378, '深圳市壁虎互动娱乐科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520219162538.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2378', '', '壁虎互动', '深圳市壁虎互动娱乐科技有限公司是一家国内游戏发行公司，一直致力于为用户提供丰富的精品手游。主要业务是手机网络游戏的发行。', 'success');
