INSERT INTO `company` VALUES (2381, '奇天乐地科技（北京）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520303427497.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2381', '', '奇天乐地科技（北京）有限公司', '奇天乐地科技（北京）有限公司于2013年2月在北京创立，我们长期致力于为中国近10亿移动用户提供包括单机游戏、社交游戏、及游戏互动社区等，全方位的移动娱乐游戏服务，带给每一个用户随时随地的喜悦和快乐。', 'success');
INSERT INTO `company` VALUES (2382, '广州市漫灵软件有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520303700788.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2382', '', '漫灵游戏', '广州漫灵软件有限公司成立于2014年，专注于手机游戏发行和运营。创始团队曾任职于百度、腾讯、UC、迅雷等互联网公司，漫灵游戏目前已成功发行《修仙萌主》《御剑青云传》《醉仙武》等精品手机游戏。其中《醉仙武》单产品月流水破1.5亿元！未来，漫灵将与合作伙创造更多可能，将“优质服务”落实到每一个岗位，赢得更多用户与合作伙伴的认可！', 'success');
INSERT INTO `company` VALUES (2383, '深圳市创世互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520821011672.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2383', '', '创世互动', '深圳市创世互动科技有限公司是XTone翔通动漫集团全资子公司。XTone翔通动漫集团是中国大陆最早从事手机动漫的企业，是文化部、工信部动漫产业发展重点扶持企业，是中国动漫产业最大的手机动漫公司之一。XTone翔通动漫集团以“创新动漫 美好人生”为理念，集创意、策划、开发、发行于一体，旗下子公司涉及移动互联网动漫增值服务、衍生品制作发行、动漫设计开发、影视创作等领域，致力发展为中国无线互联网的迪士尼。', 'success');
INSERT INTO `company` VALUES (2384, '深圳乐舞网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520821384861.gif', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2384', '', '乐舞游戏', '乐舞游戏是全球网页游戏研发及发行商，公司聚焦在以细分类产品为基础的网页游戏为主，向用户提供推荐服务、并布局移动游戏业务。目前，乐舞（Lewu Games）已与国内外多家顶级IP达成合作，并将于2017推出重磅大作《浪漫劲舞团》、《还珠格格》、《依依恋物语》等优秀网页游戏。', 'success');
INSERT INTO `company` VALUES (2385, '福州靠谱网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520821605241.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2385', '', '靠谱娱乐', '福州靠谱网络有限公司于2013年成立，是福州高新技术企业的领航者，获得多项互联网科技专利，技术人才大多来自于IBM，联想，华为等企业。靠谱网络以虚拟技术为基础，打造泛娱乐平台，建设移动游戏领域的闭环产业链，并获得骄人的业绩。旗下主打产品靠谱助手及天天模拟器是国内安卓模拟器行业第一。靠谱网络坚持用内容和服务提升玩家体验，视频直播秀、好友视频社交、VIP体系、游戏辅助工具，都是独有的内容和产品。靠谱助手已经成为移动游戏玩家pc玩手游的首选平台。2016年靠谱网络正式进军移动游戏上游，布局游戏内容研发及发行，已成为集合研发、运营、发行为一体的综合移动游戏服务商。', 'success');
INSERT INTO `company` VALUES (2386, '苏州大禹网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520821986255.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2386', '', '大禹网络', '大禹网络定位于“先进的数字内容生产商，优质网生IP孵化平台”。公司专注于数字内容文化产业，主营新媒体、数字动漫、数字游戏三大业务模块，致力于为全网超两亿用户提供超预期的数字内容体验。 目前，大禹已经初步形成互联网内容生产、发行、变现的完整生态链和IP全链条打造能力，是新浪微博中国区四大视频战略合作伙伴之一，腾讯认证第三方顶级开发商。', 'success');
INSERT INTO `company` VALUES (2387, '广州华武信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520822180292.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2387', '', '广州华武信息科技有限公司', '广州华武信息科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2388, '广州盛萱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520822318204.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2388', '', '广州盛萱网络科技有限公司', '广州盛萱网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2389, '天津安果科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520822668749.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2389', '', '7659游戏', '7659游戏致力于挖掘渠道价值，依托7659游戏开放平台，基于海量的游戏内容，为渠道合作伙伴提供最优流量变现方案。目前，7659游戏已经与快用苹果助手、i苹果助手、3K助手等多家移动互联网产品达成合作，为其带来了丰厚的收益，并将持续为更多移动互联网产品搭建游戏服务生态助力合作伙伴品牌与收入双丰收。', 'success');
INSERT INTO `company` VALUES (2390, '深圳市浩信科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520823192542.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2390', '', '浩信科技', '浩信科技成立于2014年，是一家专注于移动端精品游戏研发的游戏厂商。公司由多位游戏行业资深人士共同创办，核心成员均有超过十年游戏从业经历。凭借对游戏执着的热情，专注勤奋的态度，富有创意的策划能力，强大的技术实力，以及对游戏用户的深刻理解，团队立足于全球数字互动娱乐市场，致力于为广大玩家提供精品化的在线娱乐体验，持续创造快乐。 浩信科技期待热爱游戏的你加盟。', 'success');
INSERT INTO `company` VALUES (2391, '北京启明在线科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520824043601.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2391', '', '北京启明在线科技有限公司', '北京启明在线科技有限公司是一家专门研发android安卓游戏等游戏的开发商，北京启明在线科技有限公司的主要游戏开发类型包括等。北京启明在线科技有限公司开发商安卓游戏大全合集及下载功能由拇指玩提供，二战世界、王者之路、二战风云2等为其开发的非常受欢迎的游戏。', 'success');
INSERT INTO `company` VALUES (2392, '杭州玄机科技信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520824242443.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2392', '', '玄机科技', '杭州玄机科技信息技术有限公司，是国内顶尖影视制作及品牌运营的科技和娱乐公司。主要从事软件、系统、影视、动画、漫画、图书、游戏等相关数字内容开发、制作和品牌运营。2005年正式成立的玄机科技，是中国十大优秀原创动画企业之一，如今更已成长为充满活力的全品牌运营娱乐公司。公司以原创动漫影视制作为基础，实现了从原创内容孵化、动漫创作、图书音像、衍生产品、网络游戏、影视摄制的IP全产业链品牌运营能力，以精工细作的“匠心”精神和不断创新的艺术风格，诠释“原创”二字的至高价值。', 'success');
INSERT INTO `company` VALUES (2393, '广州爱玩网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520908555695.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2393', '', '爱玩游戏', '广州爱玩网络科技有限公司从事CP立项，提供游戏预热、渠道联运、买量等一整套流量解决方案等工作。', 'success');
INSERT INTO `company` VALUES (2394, '成都多元互动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520908803323.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2394', '', '多元互动', '成都多元互动网络科技有限公司成立于2015年，注册资本1000万元人民币，目前员工数33人。公司主营业务：游戏开发运营、智慧景区平台研发运营、互联网+主题公园模式研究。 公司专门从事线上线下（O2O）娱乐互动产品的开发及运营服务。公司由经过多年线下娱乐产业（国色天乡乐园）运营实践与互联网游戏开发、发行、运营经验人员组成核心主创团队。以线上线下（O2O）运营结合为核心，致力于研究网络游戏与线下乐园（景区）间的运营互动关系，打造智慧化的智能景区服务管理平台。 智慧化景区服务管理平台，是基于互联网条件下，以提供游客游玩相关服务诉求及个性化、人性化服务为突破口，整合乐园（景区）CRM管理、安全管理、动态管理的智慧平台。同时提供互联网周边游戏应用、电子商务、用户互动社区等功能配套，旨在为用户提供全方位线上线下（O2O）服务体验和为旅游企业提供运营管理、营销推广的平台支持。', 'success');
INSERT INTO `company` VALUES (2395, '杭州刺暮软件科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520909900477.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2395', '', '刺暮', '杭州刺暮软件科技有限公司从事移动游戏、主机游戏开发工作。', 'success');
INSERT INTO `company` VALUES (2396, '群宇信息科技(上海)有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1520910226236.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2396', '', '群宇信息科技(上海)有限公司', '群宇信息科技（上海）有限公司是由台湾大宇资讯在中国上海成立的内容开发公司,专注于网页游戏、社交游戏产品的开发与制作，并且是一家拥有独立经营权的外商投资高科技娱乐数据内容开发公司，拥有知名游戏如《仙剑奇侠传》、《大富翁》、《轩辕剑》等系列的版权与开发经验。   自创立之日起，便注重于各平台游戏内容的开发，目前已有《大富翁》系列及自主开发的原创游戏将陆续上市。    随着多个游戏开发项目陆续展开，群宇信息扩大招聘有志于网页游戏及社交游戏开发的各类专才，也希望对仙剑奇侠传、大富翁、轩辕剑等系列游戏有深厚情感的朋友能加入我们的团队，为喜爱游戏的玩家创造高质量、更好玩的产品而努力。', 'success');
INSERT INTO `company` VALUES (2397, '杭州塔图因科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521425427442.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2397', '', '杭州塔图因科技有限公司', '杭州塔图因科技有限公司是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2398, '上海聚力传媒技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521426006121.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2398', '', '上海聚力传媒技术有限公司', '上海聚力传媒技术有限公司是一家由多家国际知名风险投资公司投资的，服务于中国及全球互联网用户社群的网络电视技术平台提供商，是第五代网络新媒体中的领军企业。聚力传媒始终致力于新一代流媒体传输技术和网络视频技术的开发、推广和应用，是第一家向海外输出中国自主知识产权技术及专利，并被国际知名企业/机构（哈佛、麻省理工、微软研究院）广泛的引用互联网视频企业。凭借自主研发的专利P2P流媒体技术，在行业内始终保持发展迅猛的开拓者地位，其全球超大规模分布式视频网络，计算效率超过全球同类型网络500倍，致力于打造具有高清播放品质的网络电视新媒体，并向广大用户提供包括在线直播/点播、高清影视、视频搜索、视频加速等网络视频尖端技术及应用。', 'success');
INSERT INTO `company` VALUES (2399, '北京阿夏庄园文化创意有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521426281679.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2399', '', '北京阿夏庄园文化创意有限公司', '北京阿夏庄园文化创意有限公司是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2400, '北京宜阁枫科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521426563002.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2400', '', '北京宜阁枫科技有限公司', '北京宜阁枫科技有限公司是中国一家致力于原创游戏开发及代理发行的互联网公司，公司自2014年2月创立，注册资金人民币1000万元。公司将立足于游戏，扎根于本土，以自主原创为根本，与不断创新相结合，在以游戏研发运营为核心业务的基础上，向多元化移动互动娱乐领域扩展，不断为用户奉献精品优质的娱乐服务。公司的宗旨是“持续提供超越玩家期望的产品和服务”为发展目标，致力于为中国乃至全球的玩家提供最优质的网络游戏服务。', 'success');
INSERT INTO `company` VALUES (2401, '广州市九尊网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521427171079.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2401', '', '广州市九尊网络科技有限公司', '广州市九尊网络科技有限公司是一家以精品单机手游以及IP发展为核心业务的游戏公司。', 'success');
INSERT INTO `company` VALUES (2402, '广州联亿网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521427535150.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2402', '', '广州联亿网络科技有限公司', '广州联亿网络科技有限公司成立于2009年6月，连续获得腾讯、北极光创投、广州科技金融创新投资控股有限公司等知名VC投资，迅速成长为国内领先的游戏泛娱乐媒体集团。现公司旗下有任玩堂、任玩娱乐、任玩游戏三大业务品牌，涵盖手游发行、娱乐电竞和手游媒体，在游戏和电子竞技行业有着极强的影响力，是腾讯、网易、小米、迪士尼、EA等中外知名游戏企业首要合作伙伴。目前到达用户已经突破 200,000,000！', 'success');
INSERT INTO `company` VALUES (2403, '上海美峰数码科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521427750289.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2403', '', '上海美峰数码科技有限公司', '美峰数码是国内最大的国际化手机网络和单机游戏供应商、运营商之一；旨在通过\"移动娱乐\"，为广大手机用户创造一种全新的无线娱乐生活方式，让更多的人通过手机体会到更多的乐趣。 公司成立以来一直以手机游戏开发为主营业务，是中国最早涉足手机网络游戏开发及运营的公司之一。立足信息娱乐产业，在手机网络游戏、手机单机游戏、无线通信等领域都具有非常丰富的产品开发、运营和推广经验，尤其擅长手机网络游戏的开发、运营。', 'success');
INSERT INTO `company` VALUES (2404, '上海才宝网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521427992768.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2404', '', '上海才宝网络科技有限公司', '上海才宝网络科技有限公司成立于2016年1月21日，以泛娱乐为战略目标，推广“影游联动”概念，致力于打造出全方位的影游联动娱乐方式，开创全民互动娱乐新时代。公司专注于高品质手游研制，坚持扎实的研运一体方向，立志成为全球领先的互动娱乐游戏企业。', 'success');
INSERT INTO `company` VALUES (2405, '上海羽厚亦网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521512899605.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2405', '', '11玩', '11wan是上海羽厚亦网络科技有限公司旗下的手游运营平台，成立于2016年11月，是一家具有自主运营能力的新锐手机游戏发行商，团队骨干拥有丰富的游戏渠道推广、精细化运营的实战经验，专注精品手机游戏发行。', 'success');
INSERT INTO `company` VALUES (2406, '深圳尚米网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521513080561.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2406', '', '深圳尚米网络技术有限公司', '深圳尚米网络技术有限公司是一家主要从事游戏运营与发行的专业化互联网公司。公司成立于2012年，经过7年时间的快速发展，凭借着优秀的团队组织、强大的技术支持、丰富的行业经验和市场运作实力，迅速在游戏行业内开拓出一片新天地。公司初期主营页游相关业务，后看准移动互联网市场，集中力量进军手游市场，公司手游充值折扣福利平台的运营模式迅速在业内引起广泛关注，获得了大量游戏玩家的青睐。', 'success');
INSERT INTO `company` VALUES (2407, '福州创星互娱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521513718086.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2407', '', '创星互娱', '福州创星互娱网络科技有限公司，设立在福州市仓山区红坊海峡创意产业园内。是以应用开发为核心，集研发、代理、运营、推广、销售为一体的综合性互联网公司。 作为一家以手机游戏发展而成的公司，创星互娱在手游运营和推广方面具有得天独厚的优势。通过与各大苹果和安卓手机助手密切合作，公司的主营游戏在iOS正版商店都有不菲的成绩。在手游平台、游戏发行、软件研发与销售，手游渠道推广等领域，拥有国内最专业的ASO优化团队及国内最庞大的客服团队。更专业的游戏运营，从PC端、移动互联，直至虚拟现实与VR，从线上到线下，虚拟游戏与营销平台的深入整合，将在未来焕发出更强大的活力与竞争力。', 'success');
INSERT INTO `company` VALUES (2408, '北京六趣网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1521513957205.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2408', '', '北京六趣网络科技有限公司', '北京六趣网络科技有限公司，简称六趣、北京六趣、六趣科技等，由柳晓宇创建于2012年5月，总部设在北京，前身是柳柳工作室。在工作室时期发布的作品有《格斗纹章》、《姜太公的小岛》、《神兽大战倭寇》、《少侠一炷香》，公司制作的作品《永夜幻想曲》，已在2013年第一季度上线销售。同年5月，六趣推出了开放性制作工具——《橙光制作工具》，与其前身《彩虹文字制作精灵·红》一样，快速、可视化、人性化、无编码的操作过程将用户的操作难度降到最低。', 'success');
INSERT INTO `company` VALUES (2409, '上海追趣网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522031077376.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2409', '', '追趣游戏', '追趣网络科技有限公司（以下简称“追趣游戏”），由一群真正热爱游戏的游戏行业精英于2015年6月正式创立，是一家专注于游戏产品运营及研发的新一代文化创意企业。追趣游戏的核心成员均为八零后出生，均是历经中国网游行业十年沉浮的精英人物，无论对于游戏研发和游戏运营都有着自己独特而深刻的理解。追趣游戏的核心成员一致认为，只有能够带领用户找回游戏最初乐趣（简单、好玩、耐玩）的产品，才有可能在日益激烈的市场竞争中获得最终的成功。为了这个共同的目标，追趣游戏所有同仁都将为之奉献全部的青春和热忱。我们热切期待着更多志同道合的小伙伴们的加入!', 'success');
INSERT INTO `company` VALUES (2410, '浙江五翼互娱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522031379541.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2410', '', '浙江五翼互娱网络科技有限公司', '浙江五翼互娱网络科技有限公司主推的运营平台，于2009年12月进军网页游戏行业，勇于抓住机遇，迎接挑战，不断成长，成功运营了多款风格迥异的大型网页游戏，是国内最专业的网页游戏平台之一。乐趣网页游戏平台致力于领先的游戏开发及运营，倾力打造并运营真正属于民族的，健康的，拥有自主知识产权的高质量的网游精品，真正做到为用户服务。为所有游戏玩家提供健康、休闲、好玩的WebGame是我们共同的目标。', 'success');
INSERT INTO `company` VALUES (2411, '北京族星灵凡科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522031528288.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2411', '', '族星灵凡', '北京族星灵凡科技有限公司成立于2014年10月，是一支专注于二次元细分领域精品游戏开发的创新团队。族星注重游戏创意与乐趣本质，核心成员在各自专业领域均具有多年游戏开发经验，对3D游戏研发尤其擅长。画师团队由在日本生活多年经验的主管带领，能够准确把握核心受众口味。族星灵凡首款移动端大作《战舞幻想曲》已将于2016年5月双端上线运营。《战舞幻想曲》拥有着同类游戏绝无仅有的创新设计和视觉表现力，在游戏上线运营的后，受到玩家广泛赞誉，获得了高留存、高付费率的优秀成绩。族星灵凡团队的原创游戏设计能力及技术研发实力，也同样得到了业界的支持与认可。', 'success');
INSERT INTO `company` VALUES (2412, '上海东方梦工厂影视技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522032116116.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2412', '', '东方梦工厂', '东方梦工厂为世界各地的观众打造高品质的娱乐，包括电脑制作的动画片与电视连续剧。东方梦工厂志在成为总部设在中国的家庭品牌娱乐公司，在中国为中国人打造娱乐并出口到世界其他地区。公司总部设在上海，拥有世界一流的创意人才、实力雄厚、经验丰富的管理团队，以及先进的电影制作技术与技法。东方梦工厂的所有故事片都将制作成3D版本。', 'success');
INSERT INTO `company` VALUES (2413, '深圳市灼华网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522033250047.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2413', '', '灼华网络', '深圳市灼华网络科技有限公司成立于2012年10月26日，由国内互联网IT精英和有成功创业经验的团队创立于深圳。专注于移动网络游戏研发领域，现有员工50多人，公司秉承创业型公司的极客精神，坚持以自主研发为主。截止目前，公司已经推出了《进击的部落》、《部落奇兵》游戏，即将封测上线的游戏《盗墓OL》。', 'success');
INSERT INTO `company` VALUES (2414, '杭州九翎网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522033756861.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2414', '', '杭州九翎网络科技有限公司', '杭州九翎网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2415, '上海旭梅网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522033961210.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2415', '', '旭梅游戏', '上海旭梅网络科技有限公司是一家集网络游戏研发和运营于一体的互联网企业。上海旭梅网络科技有限公司还处于成长期，公司拥有自主研发的游戏引擎，自主研发的游戏产品和一个团结向上，充满激情的团队。', 'success');
INSERT INTO `company` VALUES (2416, '广州狼旗网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522034162548.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2416', '', '狼旗股份', '广州狼旗网络科技股份有限公司创建于2010年1月，由国内知名金牌游戏制作人、《大话西游》系列之父黄铧先生创立，是广东地区规模较大的以自主研发和运营为一体的互联网高新科技企业。公司位于广州市科学城，高质量生态环境，完善的城市基础设施，高效益的投资创业软环境，索尼、三星等世界500强知名企业毗邻。', 'success');
INSERT INTO `company` VALUES (2417, '北京掌中米格信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522034377875.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2417', '', '米格', '北京掌中米格信息技术有限公司专注于手机游戏，注册资本为人民币壹仟万元。在中国拥有丰富的无线增值业务经验与市场发行经验，持之以恒地坚持“以内容为王，走精品路线”的核心价值认同，以内容保证品质，以品质保证品牌，要让“掌中米格”成为对消费者游戏品质的承诺，成为消费者选择游戏的依据，成为消费者品味的象征。', 'success');
INSERT INTO `company` VALUES (2418, '上海召合网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522117530810.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2418', '', '上海召合网络科技有限公司', '上海召合网络科技有限公司是一家独立手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2419, '北京一骑当千网络科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522117809194.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2419', '', '北京一骑当千网络科技股份有限公司', '北京一骑当千网络科技股份有限公司成立于2013年12月，2016年9月6日正式挂牌新三板。股票代码：838780，是密云区第一个登陆新三板的网络游戏文化创意开发企业。2015年成为文化部首批对外贸易扶持企业，出口创汇上千万元。2016年获得国家文创产业基金的支持。公司目前员工人数约80人，90%为大专本科及研究生学历。公司是移动游戏及VR电竞游戏领域的新锐研发与运营商，公司拥有多项知识产权，同时也是“国家高新技术企业”、“中关村高新技术产业”双高新技术企业。公司致力于两个发展方向：中国传统文化策略游戏及VR军事电竞游戏的研发及推广。《一骑当千ol》系列产品、《新特种部队》系列产品分别取材于三国及军事反恐题材，技术上采用自有研发引擎，保证了业内领先的游戏效果的实现。', 'success');
INSERT INTO `company` VALUES (2420, '武汉手盟网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522118057787.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2420', '', '手盟网络', '武汉手盟网络科技股份有限公司（简称手盟网络）是一家专注于移动游戏领域精品游戏业务的高新技术企业。公司集游戏发行和运营为一体，为玩家提供最精品的游戏、为游戏研发商提供最全面的发行和运营服务。2013年初成立之后，手盟网络坚持游戏精品化、产品IP化、运营精细化、服务全面化的战略，旗下已成功发行并运营数百款网络游戏。', 'success');
INSERT INTO `company` VALUES (2421, '成都闪游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522634831682.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2421', '', '成都闪游科技有限公司', '成都闪游科技是一家专注手机游戏研发的软件公司。核心团队成员都在30岁左右，曾一起开发过《铸剑》、《石器宝贝》等页游产品。来自成都本土各大游戏公司。 成熟、稳重、创新、坚持是公司的做事理念。团队技术实力强劲，本着“为中国创造好游戏”的愿景，目前产品已经国内代理已经签出，海外代理权正在洽谈中，成都闪游正在快速发展的道路上。 目前闪游科技拥有产品《御魂三国》，是一款3D手游产品，由自主研发的3D引擎完成开发，可实现多平台快速移植。引擎工具包括地形编辑、场景关卡、角色动作编辑、3D特效编辑、着色器、音乐音效编辑、UI整合等。', 'success');
INSERT INTO `company` VALUES (2422, '福州海牛网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522635694597.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2422', '', '海牛网络', '海牛网络技术有限公司（简称“海牛网络”），创建于2008年04月，地处海峡西岸经济特区福建福州，是稳步发展的手机网络游戏开发商及运营商，拥有一定的地位及国际客户基础。目前海牛网络在越南、印尼、等地都设有分支机构， 海牛网络将继续加快全球化的进程。近年来，海牛稳步持续发展，在手游研发以及运营方面取得了一定的成绩，公司主打游戏《口袋妖怪VS》、《真三国快打》以及《天问》《英雄法则》《龙威》都取得了很好的业绩；公司在多语种运营上也有一定的建树，例如英语，俄语、阿拉伯、越南语、印尼语、等各国的重要语种都有涉及。', 'success');
INSERT INTO `company` VALUES (2423, '福州佳和互娱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522636032783.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2423', '', '福州佳和互娱网络科技有限公司', '福州佳和互娱网络科技有限公司是一家专门研发android安卓游戏等游戏的开发商，福州佳和互娱网络科技有限公司的主要游戏开发类型包括等。福州佳和互娱网络科技有限公司开发商安卓游戏大全合集及下载功能由拇指玩提供，幻想街机、一拳超人、超神觉醒等为其开发的非常受欢迎的游戏。', 'success');
INSERT INTO `company` VALUES (2424, '北京欢乐之旅科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522637095759.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2424', '', '欢乐之旅', '欢乐之旅（Joy Game）是一家致力于大型多人在线手机网络游戏（MMORPG）研发及运营的游戏公司。公司致力于采用家中机横版双卷轴技术开发超强立体感游戏，领跑浪漫型手机网游。公司旗下产品有基于java，android，wap和mtk技术平台的多种产品线，面向所有使用手机上网的用户。', 'success');
INSERT INTO `company` VALUES (2425, '广州杰茜卡信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522722128799.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2425', '', '广州杰茜卡信息科技有限公司', '广州杰茜卡信息科技有限公司，是国内领先的手机游戏开发商、运营商，专注于手机游戏的开发和运营。我们拥有成熟的研发、运营团队，成功研发了手机单机游戏等多种类型游戏的独立引擎，并独立制作多款手机游戏且成功上线，公司掌握了自主研发技术，具备快速独立研发新产品的实力。', 'success');
INSERT INTO `company` VALUES (2426, '苏州晶石互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522722684206.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2426', '', '晶石互娱', '苏州晶石互娱科技有限公司是一家新锐手游发行商，成立于2016年。公司管理团队从业经验丰富，国内团队发行经验丰富，海外团队本地化运营能力极强。苏州晶石互娱科技有限公司主要业务方向是海外和国内市场的手机游戏发行和运营业务，致力于为海内外用户提供业内顶级手游产品。', 'success');
INSERT INTO `company` VALUES (2427, '厦门魔兔网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522722860751.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2427', '', '厦门魔兔', '厦门魔兔网络科技有限公司，办公地点位于厦门市思明区民族路世纪中心，专注手游研发以及发行。团队成员平均游戏行业经验超过6年，曾开发过Kjava、MTK、Symbian、Windows Mobile、IOS、Android等平台游戏，是国内一流的研发团队。具备优良的手游研发基因，核心成员均是共事多年的同事或好友，造就了如今团结默契的高效团队。开发的产品具有国际化的品质和视野，注重游戏体验，擅长挖掘新玩法，大胆尝试新风格。', 'success');
INSERT INTO `company` VALUES (2428, '米游互动（海南）科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522723122145.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2428', '', '米游互动（海南）科技有限公司', '米游互动（海南）科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2429, '北京鸿腾驰骋科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1522723331064.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2429', '', '北京鸿腾驰骋科技有限公司', '北京鸿腾驰骋科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2430, '顽石互动（北京）网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523240289011.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2430', '', '顽石互动', '顽石互动（北京）网络科技有限公司（简称顽石），是中国领先的移动游戏开发商和运营商之一。成立于2006年6月，创始人曹红，2009年由吴刚担任CEO。 顽石先后获得IDG资本、成为基金和挚信资本的投资。目前员工数量130人。成立之初，凭借强大的技术实力，顽石互动与诺基亚、 摩托罗拉、NEC等国际厂商合作开发单机手游很快得到广大玩家的喜爱。 此后公司为满足全球用户不断变化的需求和市场发展， 开拓移动网络游戏市场。先后开发了《二战风云》、 《契约OL》、《三国之杀场》等产品，由公司独立发行，并全面拓展海外市场，均取得不菲成绩，成为具备一线开发实力、全球发行能力的中国顶级手游公司。', 'success');
INSERT INTO `company` VALUES (2431, '游民部落科技（北京）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523240637999.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2431', '', '游民部落科技（北京）有限公司', '游民部落科技（北京）有限公司成立于2013年3月15日，是中国动漫集团下属中娱文化股份有限公司的控股子公司，承担集团所有核心业务，包括动漫游戏内容制作、生产、运营以及衍生品的设计、开发、推广，还包括创意孵化和人才培养。2011年新启动的厦门基地就是作为中国动漫集团节目制作中心而成立，它承载着大量动漫游戏作品的孵化和制作任务。', 'success');
INSERT INTO `company` VALUES (2432, '广州市久邦数码科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523241053716.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2432', '', '广州市久邦数码科技有限公司', '久邦成立于2003年，总部位于广州，员工人数超过500人，在国内北上深杭、海外美国欧洲及东南亚都设有分支机构。久邦主要业务板块包括业务覆盖全球的手机工具类应用软件系列和面向国内的网络文学阅读平台以及3G门户资讯网站。', 'success');
INSERT INTO `company` VALUES (2433, '北京集英舍软件科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523241311659.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2433', '', '北京集英舍软件科技有限公司', '北京集英舍软件科技有限公司，成立于2014年。创业团队由来自于多个知名手游公司的核心高管组成，核心成员均拥有10年以上互联网及游戏行业产品研发管理经验。公司业务领域覆盖游戏研发、运营等领域，公司秉承努力、开心、共成长的企业理念，致力于为用户、员工、股东创造最大利益。娱乐用户是我们的梦想，为员工创造灿烂的未来是我们的使命。', 'success');
INSERT INTO `company` VALUES (2434, '北京友皆乐通信技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523241886595.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2434', '', '北京友皆乐通信技术有限公司', '北京友皆乐通信技术有限公司是一家专业从事网络游戏开发与运营的公司，包括软件开发、多种游戏自主研发及自主运营。', 'success');
INSERT INTO `company` VALUES (2435, '上海哈邻网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523242247563.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2435', '', '上海哈邻网络科技有限公司', '上海哈邻网络科技有限公司是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2436, '上海锐移计算机科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523242464312.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2436', '', '锐移游戏', '上海锐移计算机科技有限公司是一家集手机游戏研发、发行、运营为一体的移动互联网公司。公司始终坚持面向广大移动互联网用户和移动运营商等各平台提供一流的娱乐产品和增值服务。目前公司下设多个特色游戏工作室，核心研发及发行运营团队。', 'success');
INSERT INTO `company` VALUES (2437, '杭州空游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523328489828.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2437', '', '杭州空游网络科技有限公司', '杭州空游网络科技有限公司由游戏行业内资深人士组成，核心团队磨合超过5年，曾主导开发了2款月收入千万级的网游产品。现在公司立志于成为移动游戏领域的领军企业，开发和运营高质量的移动游戏产品，为玩家提供优质游戏体验和务。', 'success');
INSERT INTO `company` VALUES (2438, '吉安市井开区科迅网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523328767198.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2438', '', '吉安市井开区科迅网络科技有限公司', '吉安市井开区科迅网络科技有限公司是一家专门研发android安卓游戏等游戏的开发商。', 'success');
INSERT INTO `company` VALUES (2439, '深圳市逸飞冲天网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523328956296.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2439', '', '深圳市逸飞冲天网络科技有限公司', '深圳市逸飞冲天网络科技有限公司（5Gwan）专业提供手机游戏运营服务，打造5Gwan手机游戏联运平台。为中国用户提供单机游戏、手机网游、社交游戏、及游戏互动社区等。', 'success');
INSERT INTO `company` VALUES (2440, '广州苍鸾网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523844354757.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2440', '', '苍鸾网络', '2016年，广州苍鸾网络科技有限公司（以下简称苍鸾网络），正式在广州成立，注册资本1000万元，是一家以手机游戏发行为主要业务的新兴企业。 苍鸾网络的发行团队主要由网易、阿里的班底打造而成，拥有丰富的发行经验以及广阔的行业人脉，曾参与发行过《梦幻西游》、《天下HD》等过亿级别流水的产品。 苍鸾网络致力于精品手机游戏的发行业务，同时将全面升级多元化互动娱乐经营发展，力求打造具备创新性、全球化的游戏品牌。', 'success');
INSERT INTO `company` VALUES (2441, '天津航信科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523844547580.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2441', '', '天津航信科技有限公司', '天津航信科技有限公司是一家游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2442, '江苏三九互娱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523844810576.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2442', '', '3975游戏网', '江苏三九互娱网络科技有限公司，位于江苏无锡。3975游戏网（3975.com）创建于2010年，是目前国内最受欢迎的手游网站。多年来，我们一直秉承着服务用户、快乐至上的发展理念，致力提供最丰富、最优先的游戏内容。发展至今，3975游戏网已经成为一个涵盖小游戏、网页游戏、手机游戏、游戏资讯等多个领域的综合性休闲游戏平台。', 'success');
INSERT INTO `company` VALUES (2443, '成都白银时代科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523845255693.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2443', '', '成都市高新区天府软件园d区7栋103b室', '成都白银时代科技有限公司成立于2014年，公司位于成都市高新区天府软件园D区7栋。我们一直专注于网络游戏的开发，核心创始人拥有10年以上的丰富行业经验和世界顶尖游戏公司的管理经验。公司致力于打造精良游戏产品，以世界级游戏质量作为标准，立足国内市场而开拓海外市场。公司正处于高速发展的时期，诚邀喜欢并且致力于游戏行业的你们加入！', 'success');
INSERT INTO `company` VALUES (2444, '深圳摩天轮网络游戏有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523845418908.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2444', '', '深圳摩天轮网络游戏有限公司', '深圳摩天轮网络游戏有限公司是一家网络游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2445, '艾莱斯（北京）科技发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523845903073.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2445', '', '艾莱斯（北京）科技发展有限公司', '艾莱斯（北京）科技发展有限公司（Aries Games），成立于北京，是一家全球领先的移动互联网游戏发行商。艾莱斯（北京）科技发展有限公司，代理运营的产品涵盖iOS和Android等移动平台的游戏及应用。艾莱斯Aries Games遍及全球的渠道合作伙伴，让手机游戏有机会在全球范围赢得最广泛的玩家青睐。', 'success');
INSERT INTO `company` VALUES (2447, '深圳米趣玩科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523846727248.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2447', '', '深圳米趣玩科技有限公司', '深圳米趣玩科技有限公司，专注于手机游戏，应用软件研发和发行。米趣玩现在是一个飞速成长的团队，现已拥有一支具有超强竞争实力的手机游戏开发团队，团队成员均拥有三年以上手机应用软件开发经历，美术及创意开发人员均出自专业娱乐公司及游戏公司，拥有长期为手机开发游戏及应用软件的经验，截止到目前，已研发了几十款深受玩家喜爱高品质手机游戏，拥有多项软件著作权登记。公司以“合作、分享、共赢”的理念，积极寻求优质精品手游产品进行买断、独代、首发和联运合作，与国内外优秀的手 机游戏开发商共赢手机游戏市场！', 'success');
INSERT INTO `company` VALUES (2448, '上海沃势文化传播有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523847113637.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2448', '', 'Play 800', '上海沃势文化传播有限公司一家专注精品IP改编移动游戏的移动互联网游戏公司，公司秉持着“为世界创造快乐”的核心价值理念，深度挖掘精品IP，多方联动改编发行手游作品，致力于多领域联动战略布局，打造超一流的泛娱乐化互联网平台。', 'success');
INSERT INTO `company` VALUES (2449, '上海风格信息技术股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1523847426596.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2449', '', '亿马联盟', '上海风格信息技术股份有限公司是一家专注精品移动互联网游戏发行的集团公司，致力于为全球移动平台领域的发行。总公司于2013年在中国上海成立，2014年在北京开设了独立渠道商务事业部。整个发行团队目前由技术团队、产品运营、市场营销和商务渠道四大核心部门组成，汇集了来自多家知名手游平台、发行公司和巨头渠道的运营、运维、商务、市场精英。所有主干成员均拥有八年以上的业界经验，成功发行过多个畅销榜名列前茅的不同类型优秀手机游戏，对成功游戏的数据表现拥有深厚的积累优势。在游戏市场前瞻、用户认知、渠道经验、产品立项、技术攻关、商业化和发行推广等各环节逐层把控，出色的产品打磨经验，给予产品更佳的修正方向建议，确保产品精品品质，真正做到手游发行的一站式服务。', 'success');
INSERT INTO `company` VALUES (2450, '深圳市大胜互娱网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524449291432.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2450', '', '大胜互娱', '深圳市大胜互娱网络科技有限公司成立于2017年11月，位于深圳市宝安区新安街道留仙三路汇聚创业园。大胜互娱的核心团队拥有丰富的国内网页游戏及手机游戏的研发，推广和运营经验。面对千变万化的市场需求和层出不穷的挑战，以及由市场需求产生的机遇，我们成立深圳市大胜互娱网络科技有限公司，专注于移动互联网游戏的研发和推广运营，凭借雄厚的技术力量、丰富的运营经验、优质的服务打造行业领先的移动游戏运营平台。我们坚持“客户至上，玩家为主”的服务宗旨，不断的为玩家提供高品质的游戏内容服务，打造一个精彩的、全面的游戏平台，为中国网页游戏、手机游戏行业的发展做出积极贡献。', 'success');
INSERT INTO `company` VALUES (2451, '深圳花生互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524449686473.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2451', '', '花生互娱', '花生互娱是南方银谷旗下全资子公司，专注于优质移动游戏的发行与运营，服务于广大游戏玩家， 致力于为游戏玩家提供更好的游戏娱乐生活，赢得了广大游戏玩家与游戏研发企业的高度认可。', 'success');
INSERT INTO `company` VALUES (2452, '深圳市掌中酷柚科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524450051789.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2452', '', '掌中酷柚', '掌中酷柚 KooGame 创办于 2009 年底，继承以往专注高品质的优良传统，于 2010 年初推出国内 首款跨平台支持 iOS、Android、Symbian、Windows Mobile 四大智能机平台的手机网游《酷柚Online》 ， 并凭借创新的玩法和一流的品质赢得了业界和用户良好的口碑。并于 2010 年 6 月获得经纬中国的 A 轮投资。', 'success');
INSERT INTO `company` VALUES (2453, '的彩网络科技（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524450364040.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2453', '', '的彩网络科技（上海）有限公司', '的彩网络科技（上海）有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2454, '美娱信息技术（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524450596309.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2454', '', '美娱信息技术（上海）有限公司', '美娱信息技术（上海）有限公司于2010年初成立于上海，是由韩国游戏公司ACTOZ SOFT全资投资建立，并隶属于盛大集团的游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2455, '深圳市冉斯网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524450757597.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2455', '', '深圳市冉斯网络科技有限公司', '深圳市冉斯网络科技有限公司成立于2012年6月，是一家从事智能手机游戏的研发与运营一体的手机游戏公司，拥有多款自主研发的手机游戏产品，覆盖android和IOS两大手机平台。 研发和运营团队核心人员均来自于著名的互联网企业，有着丰富的游戏行业经验。以追求极致的用户体验为理念，在移动互联网时代，展现给用户完美的无线娱乐产品。 公司旗下九寸钉游戏工作室研发的大型战争策略手机网游《三国世界》，一经推出市场，便深受广大玩家的热爱。 移动互联时代，冉斯将风雨无阻，开启远航，打造一流的移动游戏产品！', 'success');
INSERT INTO `company` VALUES (2456, '深圳市天拓立方通讯科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524450968123.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2456', '', '深圳市天拓立方通讯科技有限公司', '天拓立方成立于2009年11月11日, 公司座落于环境优美、创业氛围浓厚的深圳高新技术产业园内。 这里有并肩奋斗的学霸，低调高冷的极客，自信幽默的高玩,我们有多年的手机游戏开发经历， 从单机游戏到网络游戏，', 'success');
INSERT INTO `company` VALUES (2457, '上海晓枫网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524451369970.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2457', '', '晓枫网络', '晓枫网络是一家专注于原创手机游戏开发的公司，公司位于上海徐汇区。旗下现有《希望之光》、《冒险大乱斗》、《暴走英雄坛》等多款精品移动游戏。', 'success');
INSERT INTO `company` VALUES (2458, '广州龙空网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524451640939.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2458', '', '广州龙空网络科技有限公司', '广州龙空网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2459, '大连乐川网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524451844429.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2459', '', '大连乐川网络科技有限公司', '乐川网络科技有限公司成立于中国最美海滨城市——大连。她是一家以手机游戏设计、研发以及运营的科技公司。我们始终认为 “游戏是一种创意而不是一味简单而枯燥的复制，游戏产业应该是一个创意产业而不应该是一个技术产业”，秉持着 “做自己喜欢的游戏，玩自己做的游戏”的理念，专注于“强思维、娱心智”的高智商玩法手机游戏的设计、研发与运营，希望成为促进中国手机游戏向前发展的一支特殊力量。', 'success');
INSERT INTO `company` VALUES (2460, '浙江雀牛网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524452414581.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2460', '', '浙江雀牛网络科技有限公司', '浙江雀牛网络科技有限公司成立于2012年2月。总部位于风景妙曼，青春动感的动漫之都——杭州。公司注册资金1000万元，是一家致力于移动平台及互联网游戏应用、开发及经营的高新技术产业公司。', 'success');
INSERT INTO `company` VALUES (2461, '霍尔果斯酷玩网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524452668168.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2461', '', '酷玩', '霍尔果斯酷玩网络科技有限公司位于新疆伊犁州霍尔果斯口岸欧陆经典小区，是一家充满朝气、研发迅速、合作紧密的新兴互联网公司，主营业务是移动端网络游戏。酷玩网络科技由几位对互联网行业充满热情的资深人士成立于2011年。目前公司拥有超过100名优秀员工并仍在扩张中，团队成员中既包括多位拥有国内以及海外知名互联网公司从业经验的资深人才，也包括一些对行业充满热情的优质互联网新人。公司鼓励平等、开放、充满激情、积极进取的精神，并且拥有成熟的产品、技术、设计、运营和HR团队。', 'success');
INSERT INTO `company` VALUES (2462, '北京天赐之恒网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1524453013937.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2462', '', '天赐游戏', '天赐游戏是一家成立于2014年12月22日的新锐手游公司，由华谊兄弟和博派资本投资，由移动游戏领域资深人士创办，定位于精品移动游戏的发行和运营业务。旗下发行的明星产品包括《三剑豪2》《霹雳天下》《诸天至尊》《元气大冒险》《一骑当千》等等。', 'success');
