INSERT INTO `company` VALUES (2465, '广州乐牛软件科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525227086600.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2465', '', '乐牛', '广州乐牛软件科技有限公司成立于2014年，是一家专注于移动游戏研发与发行的新兴游戏公司，致力于打造一流的移动游戏精品。公司核心研发团队自2008年就专注于游戏研发、发行与运营，产品年流水数亿，多款自研、自发产品月流水超过千万。其中，自研产品《猎魔传说》月流水达7000万以上，并多次获得中国游戏行业的重要奖项，代理发行产品《仙灵奇缘》创下年流水2.5亿的佳绩，《昆仑墟》目前月流水突破4000万！公司现阶段正紧张进行全面的战略布局，火力全开，多个实力雄厚的研发团队同时进行多款U3D手游项目的制作，覆盖目前手游市场主流及有爆发潜力的项目类型！其中部分产品将在近期强势上线大推!', 'success');
INSERT INTO `company` VALUES (2466, '深圳市土星网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525227235286.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2466', '', '深圳市土星网络有限公司', '深圳市土星网络有限公司成立于2014年10月，是一家朝气蓬勃、发展迅速的移动游戏开发商和运营商。土星网络专注于手机网络游戏的研发及运营，致力于打造品质一流的精品游戏，让用户享受指尖上的无限精彩。', 'success');
INSERT INTO `company` VALUES (2467, '北京微游互动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525227536334.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2467', '', '微游戏', '北京微游互动网络科技有限公司是一家新浪的子公司。从事的业务是：新浪微博的专属游戏平台的设计、研发、与运营。', 'success');
INSERT INTO `company` VALUES (2468, '北京掌上明珠科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525227783247.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2468', '', '掌上明珠', '北京掌上明珠科技股份有限公司是一家专注于提供最领先的移动游戏和娱乐产品的开发商、运营商和发行商，致力于为全球移动用户提供更好玩、更易用的移动游戏娱乐产品和在线服务,开拓全新的无线互动娱乐生活方式。', 'success');
INSERT INTO `company` VALUES (2469, '广州闪乐文化传播有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525228235632.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2469', '', '广州闪乐文化传播有限公司', '广州闪乐文化传播有限公司是一家处于高速发展阶段的从事手机游戏研发的高科技公司，致力于为广大用户提供一流的手机游戏服务。', 'success');
INSERT INTO `company` VALUES (2470, '北京青游易乐科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525228433919.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2470', '', '北京青游易乐科技股份有限公司', '北京青游易乐科技有限公司是一家集手机游戏研发和运营于一体的企业，致力于为广大游戏爱好者提供最优秀的游戏产品及最佳的游戏体验。公司拥有业内实力一流的人才团队，目前旗下拥有《妖姬三国》、《一代天娇》两款产品，此外北京青游易乐科技有限公司首款3D大型RPGMMO游戏《魔灵幻想》于2015年发行', 'success');
INSERT INTO `company` VALUES (2471, '北京魔方无限科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525228781884.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2471', '', '北京魔方无限科技有限公司', '北京魔方无限科技有限公司成立于2012年11月3日，是专门从事手机游戏开发，网络游戏开发的新锐高新技术企业。获知名vc投资，具有较强的产品开发能力。《仙变》属于MMORPG（大型多人在线角色扮演）类手机游戏，在2013年4月份推出，近期，在当乐网、91手机助手、小米游戏等平台上，有一款手机游戏《仙变》被推荐到明显的位置。在当乐网玩家社区的热词搜索中，和《仙变》一起被列为热词标签的还有眼下大热、已进入月流水千万俱乐部的游戏。', 'success');
INSERT INTO `company` VALUES (2472, '上海游品网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525228971032.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2472', '', '上海游品网络科技有限公司', '上海游品网络科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2473, '北京小小世界科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525229557438.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2473', '', '北京小小世界科技有限公司', '北京小小世界科技有限公司是一家手机游戏研发公司。', 'success');
INSERT INTO `company` VALUES (2474, '广州简逸网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525230554209.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2474', '', '广州简逸网络科技有限公司', '广州简逸网络科技有限公司专注于优质移动社交游戏、休闲游戏的开发，秉持为用户提供更优、更新、更酷的游戏体验为产品制作宗旨,自有游戏开发平台支持Android、 Windows Phone 7系统，与chillingo等游戏发行公司保持良好的合作关系。', 'success');
INSERT INTO `company` VALUES (2475, '苏州玩友时代科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525230916779.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2475', '', '玩友时代', '玩友时代---成立于2010年5月，是一家专注于移动互联网行业的股份制集团公司，集团业务涵盖游戏产品研发、手游发行运营、游戏资讯媒体、移动社交平台、产业投资孵化等，总部位于中国•苏州，在韩国、香港、上海、杭州等地均设有分支机构。公司研发的多款古风宫廷题材手游在市场中拥有较高占有率和用户群体。其中代表作品《熹妃传》、《熹妃Q传》、《京门风月》、《宫廷计》等精品手游，受到众多玩家青睐并获得多项业类大奖，同时在港澳台、韩国、东南亚、北美等海外地区也享有盛誉。目前公司已拥有累计高达1亿用户及多款全平台产品。', 'success');
INSERT INTO `company` VALUES (2476, 'The Creative Assembly', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1525949319229.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2476', 'The Creative Assembly', 'The Creative Assembly', 'The Creative Assembly是一个英国的游戏开发公司，由提姆 ·安塞尔 在 1987 年成立。该公司的基地坐落在英国西萨塞克斯郡的霍舍姆镇中，并且在澳大利亚昆士兰省的佛特谷设立了一个分支部门。', 'success');
INSERT INTO `company` VALUES (2480, '游族网络股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1526031676740.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2480', 'YOOZOO GAMES', '游族网络', '游族网络股份有限公司成立于2009年，创始人林奇，是中国领先的轻娱乐供应商 。公司总部位于上海 ，在南京、台北、新加坡等地设有分支机构，立足全球化游戏研发与发行、大数据应用、IP管理工程、泛娱乐产业投资四大业务全面发展。2014年6月，游族网络正式登陆A股主板。', 'success');
INSERT INTO `company` VALUES (2481, '上海胖布丁网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1526032944266.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2481', 'Cotton Game', '上海胖布丁网络科技有限公司', 'Cotton Game是一家专注开发品牌形象的游戏开发公司。公司以高质量的休闲精品游戏为依托，推广独特的游戏角色形象，进而拓展成一家游戏、动画、漫画都综合发展的实力企业。目前制作解密游戏“南瓜先生大冒险”。', 'success');
