INSERT INTO `company` VALUES (2526, '多放辣椒工作室', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527231639584.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2526', 'More Chili Studio', '多放辣椒工作室', '多放辣椒工作室，萌芽在帝都，由两个80后和两个90后以及一只叫做“小软”的喵星人组成。这帮人不惧雾霾，顽强成长，专注于移动游戏产品研发（H5、IOS、安卓、WP），力争在竞争激烈的游戏市场占据一席之地。那么问题来了，游戏制作究竟哪家强？', 'success');
