INSERT INTO `company` VALUES (2537, '杭州快定网络股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527374001715.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2537', 'KDING', '快定', '杭州快定网络股份有限公司于2010年11月18日在杭州市市场监督管理局登记成立。法定代表人叶敏，公司经营范围包括一般经营项目：服务：第二类增值电信业务中的信息服务业务（仅限互联网信息服务）等。', 'success');
INSERT INTO `company` VALUES (2538, '杭州乐易付网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527374142886.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2538', '', '乐易付', '杭州乐易付网络技术有限公司于2004年06月08日在杭州市高新区（滨江）市场监督管理局登记成立。法定代表人孔强，公司经营范围包括技术开发、技术咨询、技术服务、成果转让：手机支付产品技术等。', 'success');
INSERT INTO `company` VALUES (2540, '浙江无限动力信息技术股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527374641354.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2540', '', '无限动力', '无限动力成立于2005年，是在浙江省信息产业厅的支持指导下，依托浙江大学的一家致力于移动互联网开发和运营的技术型公司。<br/>自成立以来，我公司一直将数据采集分析、信息处理推送、技术完善改进和市场推广营销完美结合在一起，通过技术创新和精准定向、行为数据给用户带来更多的价值体现。<br/>近年来，我公司更是凭借优秀的整体技术实力和高效的运作团队，迅速在中国移动互联网广告分发领域抢占一席之地。', 'success');
INSERT INTO `company` VALUES (2541, '杭州易特旺旺网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527375154057.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2541', 'YiTe Network', '易特旺旺', '杭州易特旺旺网络技术有限公司于2006年02月28日在杭州市西湖区工商行政管理局登记成立。法定代表人陈东亮，公司经营范围包括一般经营项目：服务：计算机软件开发，网页设计等。', 'success');
INSERT INTO `company` VALUES (2542, '杭州掌游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527375347818.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2542', 'game2palm', '掌游科技', '杭州掌游科技有限公司由多名无线互联网资深人士合资成立于2009年，是一家领先的行业移动通讯平台产品开发商和移动商务平台服务商，2010年9月获得了来自国内上市公司浙大网新(股票代码: 600797.sh)的战略投资。作为中国移动、中国电信、中国联通三大移动运营商战略伙伴，掌游围绕短彩信服务与流量分销、物联网业务提供线上线下一体化整合解决方案，具备“验证码5秒到”“实时流量全覆盖”的核心竞争能力', 'success');
INSERT INTO `company` VALUES (2543, '广州互动派文化传播有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527375543841.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2543', '', '互动派', '互动派科技股份有限公司是一家强化以「用户为中心」 辅助品牌企业实现大数据营销转型的品牌大数据营销公司。 目前公司有员工近300人，其中核心数据研发团队超过120人，运营（策略、创意、运营、BD、应用技 术、媒介等）团队超过110人，60%具有硕士学位。', 'success');
INSERT INTO `company` VALUES (2544, '广州际讯数码科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527375762237.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2544', '', '际讯数码科技', '广州际讯数码科技有限公司是成立于2003年8月的高新IT公司，致力于运用领先的信息技术为广大用户提供最新足球资讯的无线增值应用服务。作为一家专业从事网络系统设计开发、集成于一体的高科技现代企业，紧把握网络时代赋予的机遇，为电信营运商提供各种网络集成和互联网应用、系统软件开发以及移动增值服务平台的服务，提供宽带网络的建设、扩容和改造等服务；并提供有效、先进的解决方案。优秀的运营使得业务用户过百万，浏览量在华南地区排名领先，用户扎根华南遍及全国，延伸至香港、澳门、新加坡等地区。', 'success');
INSERT INTO `company` VALUES (2545, '广州聚好玩信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527390488060.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2545', '', '8868', '广州聚好玩信息科技有限公司是一家具有自主研发、运营能力的专业服务型公司，倾力打造具有自主知识产权的专业化手机游戏服务平台（8868.cn）。', 'success');
INSERT INTO `company` VALUES (2546, '广州市汇俊信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527390730471.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2546', '139pp', '广州汇俊', '广州市汇俊信息科技有限公司于2002年12月注册成立，公司注册资本壹仟万元人民币。公司主要从事无线增值服务拥有优质的增值业务资源和通道，近年业务重心定位在手机漫画的研发和运营上，培养了一支实力雄厚的漫画研发团队，并研发最优质的漫画。在2014年开始增加漫画的衍生产品，其中有动漫、漫画和阅读等。发展打造互联网行业的多种内容产品。', 'success');
INSERT INTO `company` VALUES (2547, '广州通摩信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527390929222.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2547', 'uodoo', '广州通摩', '广州通摩信息技术有限公司 成立于2013年10月，是移动互联网信息服务和应用服务提供商。为用户提供便捷的移动互联网服务，满足用户对移动生活的需求，最终实现用户对移动互联网的全形态应用。公司成立的愿景是专注于推动中国移动互联网事业，始终以卓越的市场前瞻力和技术创新力推动着中国移动互联网领域的发展进程。', 'success');
INSERT INTO `company` VALUES (2548, '广州翼道网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527391263796.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2548', 'edaotech', '翼道网络', '致力于“无密码安全认证”的研发运营及推广。以结合PKI数字认证及移动互联网技术，以“云+APP”方式为企业、机构和个人提供数字身份认证服务。', 'success');
INSERT INTO `company` VALUES (2549, '广州掌淘网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527391565337.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2549', 'ShareSDK', '掌淘网络', '掌淘网络成立于2012年9月。总部和研发中心设在广州，在北京、上海、成都设立分公司。自成立以来，持续专注于移动互联网的研究，致力于为移动开发者提供优质服务。2013年7月已获得知名VC深创投A轮投资。', 'success');
INSERT INTO `company` VALUES (2550, '创新谷', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527419579615.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2550', 'Innovation Valley', '创新谷', '创新谷是一个TMT和移动互联网产业的孵化器和加速器。创新谷存在的独特价值是能帮助创业者提升创业项目的成功率。', 'success');
INSERT INTO `company` VALUES (2551, '福州甲壳虫网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527419978990.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2551', 'Beatles Game', '甲壳虫游戏', '福州甲壳虫网络科技有限公司是一家新锐的手机网络游戏研发和运营商，公司致力于研发基于iOS、Android、Windows Phone上的手机网络游戏。旗下的“甲壳虫游戏”手游品牌经过甲壳虫游戏人的共同努力，现已发展成为业内较为知名的研发品牌，研发的手机网游已在全球范围内进行运营，涉及的语言版本包括简体中文、繁体、英语、印尼语、马来西亚语、越南语等。', 'success');
INSERT INTO `company` VALUES (2552, '广东奥飞数据科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527420185311.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2552', 'ofidc', '奥飞数据', '广东奥飞数据科技股份有限公司是专业的数据中心业务运营商和通信综合运营企业。奥飞数据依靠自身雄厚的技术与丰富的资源，秉承“为客户提供优质服务”的宗旨，获得了多项专业认证与服务资质。服务范围包括数据中心服务（IDC）、容灾备份、异地双活等互联网增值服务和产品。作为专业的云计算与大数据应用基础服务提供商，奥飞数据严守“高稳定性、高可用性、高安全性”的原则在华南地区设计建设了多个自建数据中心，并在全国各地运营着众多高标准数据中心，具备覆盖全国的服务能力。奥飞数据凭借自身的优势和技术，为客户构建安全稳定高速的数据未来！', 'success');
INSERT INTO `company` VALUES (2553, '广州市安墨网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527420431706.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2553', 'earn more', '安墨', '安墨网络科技有限公司,拥有移动互联网广告平台，总部设立于广州，北京设有移动互联网广告媒体研发中心。', 'success');
INSERT INTO `company` VALUES (2554, '广州沣首信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527421663451.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2554', 'FS', '沣首', '广州沣首信息科技有限公司致力于移动增值业务运营领域。为三大运营商（中国移动、中国联通、中国电信）提供优质的移动增值运营产品和服务。从成立之初至今，在无线增值和互联网运营以及相关技术开发领域形成了深厚的业务知识和技术积累。', 'success');
INSERT INTO `company` VALUES (2555, '北京棱镜云桥科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527421976776.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2555', 'Once For All', '棱镜', '北京棱镜云桥科技有限公司，于2014年3月在北京正式成立，是一家为手机游戏开发者提供渠道SDK云接入服务的第三方服务公司。棱镜公司从2013年4月份开始尝试解决国内手游渠道SDK接入困难这个开发者亟待解决的问题，2014年3月成功推出棱镜SDK平台，致力于为手游开发者提供最好的渠道SDK接入解决方案。其最大的特点在于，一次接入，多渠道自动打包。帮助手游厂商有效解决渠道SDK接入难问题，一次性覆盖国内TOP 40+的安卓手游渠道。', 'success');
INSERT INTO `company` VALUES (2556, '北京沙堡领跃科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527422089391.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2556', 'sandcastle', '沙堡领跃', '北京沙堡领跃科技有限公司（简称：沙堡领跃）历经多年时间研发出成熟跨平台3D引擎，大大节约了研发成本，引擎的高效表现可以自由开发包括多人在线角色扮演游戏、休闲游戏等多样化的跨平台3D移动互联产品。 <br/><br/>团队骨干成员曾是搜狐畅游、完美世界、蓝港在线等公司的游戏研发精英，具有多年游戏研发经验。 <br/><br/>经过多年共同研发多款产品的积累，团队拥有一套完善的管理体系，并拥有自主研发的成熟的跨平台引擎及工具。已完全支持跨平台次世代技术效果，技术实力达国内外一线水平。', 'success');
INSERT INTO `company` VALUES (2557, '北京首都在线科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527422324363.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2557', 'CDS', '首都在线', '首都在线是一家公众公司；是云计算IAAS服务提供商；为中小微企业提供云计算服务。在北京、上海、武汉、美国建有云平台。', 'success');
INSERT INTO `company` VALUES (2558, '北京新锋艾普网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527422682814.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2558', '', '新锋艾普', '新锋艾普网络科技有限公司致力于为windows phone手机用户提供应用下载、手机管理等服务。自2011年起，公司旗下产品已包括新锋网、爱应用、爱应用PC、爱应用HD，已积累了大量忠实用户。新锋网与爱应用已成为WP手机用户首选产品。', 'success');
INSERT INTO `company` VALUES (2559, '北京洋浦伟业科技发展有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527423126551.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2559', 'Bangcle', '梆梆安全', '北京洋浦伟业科技发展有限公司（简称“梆梆安全”）成立于 2010 年，是全球专业的移动应用安全服务提供商，运用领先的技术提供专业可靠的服务，为全球的政府、企业、开发者和消费者打造安全、稳固、可信的移动应用生态环境，让每个人都能自由地创造、分享和使用移动信息。<br/>梆梆安全不仅提供APP安全保护、威胁情报、事前/事后应急响应等服务，同时面向行业提供全套安全方案，针对业务定向威胁提供贯穿生命周期的纵深防御体系。今天，梆梆安全的使命是“保护智能生活”，并已经推出车联网、智能家居等相关解决方案。在将安全能力渗透到各类终端的同时，梆梆安全还打造出了把安全能力延伸至传输端以及云端的泛在安全云防护系统。面对快速发展的人工智能，梆梆安全更是前瞻性的提出认知安全理念。<br/>到目前为止，梆梆安全已经为7万家注册企业及开发者的超过70万个移动应用提供移动应用安全服务，这些应用已经累计安装在7亿个移动终端上。梆梆安全的企业用户遍及金融、互联网、物联网、政府、企业等各大行业，覆盖中国、亚洲、欧洲及北美等主要市场。', 'success');
INSERT INTO `company` VALUES (2563, '宝付网络科技（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527424082216.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2563', 'baofu', '宝付支付', '宝付网络科技（上海）有限公司是漫道金服旗下的一家第三方支付公司   ，于2011年底荣获了由央行颁发的《支付业务许可证》。宝付旨在为个人及企业提供灵活、自助、安全的互联网支付产品与服务。宝付产品种类丰富，受众广泛，费率优惠，提供365天不间断结算服务，帮助商户资金快速回笼，让电子商务与资金流安全无缝衔接，同时将“实时清算整体解决方案”作为核心商业模式落实贯彻，使宝付的市场规模在近两年来保持了高速的增长势头。', 'success');
INSERT INTO `company` VALUES (2569, '北京创世蓝点国际文化传播有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527507230254.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2569', 'Blue Point', '创世蓝点', '蓝点咨询是一个专门为IT企业提供资质咨询业务，专业从事计算机系统集成资质认证、安防资质认证、双软认证、软件软件著作权登记。特别擅长北京市计算机系统集成三级资质和全国计算机系统集成一、二级资质的认证。', 'success');
INSERT INTO `company` VALUES (2572, '成都游龙科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527585303332.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2572', '', '游龙科技', '成都游龙科技有限公司是一家网络游戏研发公司，核心成员来自业内知名企业。我们是一支朝气蓬勃，年轻热血的队伍，拥有国内一流的研发团队，具备了扎实的技术能力以及大型网络游戏项目的成功开发经验。', 'success');
INSERT INTO `company` VALUES (2573, '厦门千骥网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527585492682.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2573', 'Changit', '千骥网络', '厦门千骥网络科技有限公司是一家专注于游戏研发和运营的企业，公司自2013年成立以来多款产品均获得了良好的市场反馈和行业赞许。<br/>    公司创始人有着超过18年游戏行业经验，熟悉游戏行业，拥有丰富的行业资源。在竞争激烈的游戏市场中，一贯以创新为企业立足根本，力求为玩家带来耳目一新的游戏产品。<br/>    世界顶级游戏开发商-莉莉丝是我司重要股东之一。莉莉丝的创新精神和全球化视野有目共睹，在强大的行业资源背景下，我司的创新型竞技游戏《反击风暴》即将面向全球发行。', 'success');
INSERT INTO `company` VALUES (2574, '上海妙触信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527919947214.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2574', 'BPJOY', 'BPJOY', '2013年中国区最具创意的手机网游《主公打牌啦》研发商，上海妙触信息技术有限公司 已上线区域：中国大陆、台港澳，日本<br/>即将上线区域：东南亚、韩国<br/>目前已获国内知名投资机构A轮投资，建立了与国内，海外众多知名发行商战略、深入、长期的合作关系', 'success');
INSERT INTO `company` VALUES (2575, '北京乐智堡网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527922139506.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2575', 'LETSMOBO', '乐智堡', '乐智堡始终致力于精品手游开发，在对手游行业的现状及前景进行深度分析的基础上，制定出符合自身优势的战略，依靠业内积累的资源，构建出在产品研发，推广，运营各阶段的相对优势。隶属于北京乐智堡网络科技有限公司。', 'success');
INSERT INTO `company` VALUES (2576, '文传世纪', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527922593754.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2576', 'century media', '文传世纪', '文传世纪，跨平台穿创新整合传播的倡导者和实践者，以创新思维整合传播产业链的各个关键环节，为客户打造专属整合传播方案，全力支持客户品牌的卓越成长。', 'success');
INSERT INTO `company` VALUES (2577, '成都天创乐游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527922843105.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2577', 'Aig Games', '天创乐游', '成都天创乐游网络科技有限公司（简称：天创乐游）是一家从事无线网络娱乐产品研发的创新性公司。天创乐游公司正式注册于2013年8月，注册资金为500万。位于成都高新区天府软件园，现有两个项目部。<br/>天创乐游前身为成都兴兴四方科技有限公司手游事业部。2013年3月,兴兴四方团队第一款自主研发并运营的西游题材游戏《大话至尊宝》成功上线，最高排名在APP免费榜上为第11名，畅销榜为第26名，累计运营收入过千万。<br/>目前，公司拥有经验丰富的研发团队，技术实力雄厚，成员平均年龄28岁，平均研发经验6年。核心成员均来自于腾讯、金山、人人网、育碧等优秀公司，拥有多款成功项目经验。在端游、页游和手游平台皆有过多款成功项目的开发经历。从轻度休闲游戏，到重度MMO，都驾轻就熟。<br/>     天创乐游自成立以来，一直秉承“ 务实、专注、激情、超越  ”的企业精神，专注于手机游戏研发，倾力打造精品手游，致力于为广大手机用户带来愉悦、新颖、优质的体验。', 'success');
INSERT INTO `company` VALUES (2578, '上海际动网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527922993592.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2578', '', '际动网络', '上海际动网络科技股份有限公司，简称际动网络，是一家以IP化运作的移动端游戏公司，坚持自创IP和合作IP的路线，涵盖：移动端网络游戏发行、研发；网络文学和漫画制作、周边电商运营等链接娱乐产业链上下游的综合性移动互联网公司，励志将际动网络打造成中国版迪士尼。<br/><br/>初创期公司和迪士尼合作正版IP手游产品《加勒比海盗》；目前公司具有：软件著作权、双软企业、上海市高新技术企业、网络文化经营许可证、增值电信业务经营许可证等资质认证！', 'success');
INSERT INTO `company` VALUES (2579, '南京魔韵软件科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527923182664.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2579', 'Morln Game', '魔铃手游', 'Morln Game(魔铃手游)，由斯坦福大学、南京大学等知名高校校友组建而成，目前总共12人，平均年龄25岁。面向全球市场，专注于移动互联网精品游戏的研发，代表作品是手机游戏《爱掼蛋》，目前用户数量已过100万!', 'success');
INSERT INTO `company` VALUES (2580, '大连思为拓科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527923534828.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2580', '', '思为拓科技', '大连思为拓科技有限公司是一家专业从事网络游戏研发的高科技公司。自成立以来，一直致力于自主创新，开发出了完全自主知识产权的3D网络游戏引擎。公司秉持“专注，创新，快乐为本”的宗旨，将不断丰富强大自有的3D网游引擎，陆续推出自主创新的网游精品，并力求拓展客户端游戏、网页游戏、手机游戏等多种平台。', 'success');
INSERT INTO `company` VALUES (2581, '厦门游心谷网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527923769774.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2581', '', '游心谷', '厦门游心谷网络技术有限公司成立于2013年，由一批资深的国内外IT业界资深人士创立，是国内最新锐的手机游戏开发商之一。致力于带给每个玩家随时随地娱乐的分享与喜悦。公司成员是游戏的玩家，也是经验丰富的游戏制作者，不断创新不断挑战是他们对塑造精品的激情动力。', 'success');
INSERT INTO `company` VALUES (2582, '游智信息技术（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527923958360.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2582', 'Wish Games', '游智互动', '游智互动是一家集游戏研发、运营、发行为一体的高质量的互动娱乐公司。游智拥有雄厚的资本实力、强大的研发能力、娴熟的运营能力、精湛的技术支持能力。运营的游戏全部由公司内部开发，同时将自己开发的游戏授权给世界各地的发行商合作伙伴，这些合作伙伴位于中国、新加坡、菲律宾、马来西亚、泰国、土耳其、巴西等国家和地区。', 'success');
INSERT INTO `company` VALUES (2583, '上海冠趣信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527924090682.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2583', '', '冠趣游戏', '上海冠趣信息科技有限公司是专注于互联网/移动互联网/电子商务行业的民营企业公司，主要经营计算机技术、网络技术领域内的技术开发、技术转让、技术咨询、技术服务，计算机网络工程，计算机系统集成，商务咨询，计算机、软硬件及辅助设备（除计算机信息系统安全专用产品）的销售。【依法须经批准的项目，经相关部门批准后方可开展经营活动】并通过国家工商管理局注册成立的专业化的公司，成立于2011年11月11日，注册资本1000万人民币。公司坐落于嘉定区银翔路655号807-1室，上海冠趣信息科技有限公司以规范、专业、创新、共赢的经营理念，高效贴心的服务，团结协作、敬业负责、服务奉献、求实进取的企业精神，始终贯彻以追求合作伙伴最大利益为目标，竭诚为合作伙伴提供最大程度的保障。 主要从事手游的自主研发。 目前研发中的产品获得国内外顶尖发行商的赞誉认可。', 'success');
INSERT INTO `company` VALUES (2584, '上海思游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527924561642.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2584', '', '思游网络', '上海思游网络科技有限公司是一家技术型研发公司，成立于2006年，总部位于上海。公司主要经营范围：网络科技领域内的技术开发、技术服务、技术咨询、技术转让。思游网络员工以90后年轻一代为主，研发团队主要成员来自盛大网络、动网先锋等，参与过多个网页游戏项目研发。旗下产品：U3D网页游戏《斗ONLINE》。', 'success');
INSERT INTO `company` VALUES (2585, '南京凡腾动画制作有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527924698219.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2585', '', '凡腾', 'FANTENG是一家致力于电脑和电视游戏产业，为客户提供专业级互动娱乐制作服务的公司。以低廉的成本为客户提供高品质的开发制作服务是FANTENG的一贯宗旨。作为一个全套解决方案的服务提供商，我们的服务范围涵盖游戏制作的各个方面，包括游戏的设计开发、实时美术、动画和技术等服务。', 'success');
INSERT INTO `company` VALUES (2586, '上海卓庆网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527924957370.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2586', '', '卓庆游戏', '上海卓庆网络科技有限公司是某知名上市公司旗下公司，是一家专注移动互联网游戏的开发商，致力于为全球移动平台开发游戏.专注打造游戏精品。公司核心团队人员有着丰富的游戏、移动互联网行业经验。', 'success');
INSERT INTO `company` VALUES (2587, '拉阔', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527925184714.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2587', '', '拉阔游戏', '拉阔游戏成立于香港，自手机游戏市场还没出现的1999年起，从2G到4G、离线到在线、功能手机到智能手机，梦想开辟出带粤语文化色彩的canto-game，拉阔游戏光谱，補完本土文化。', 'success');
INSERT INTO `company` VALUES (2588, '深圳市乐平方科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527925448334.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2588', 'Joy Game2', '乐平方', '深圳市乐平方科技有限公司建立于2013年，创始人&CEO薛扬，至少两款以上端游的开发经验，至少一款以上手游的产品运营经验，团队磨合度高。', 'success');
INSERT INTO `company` VALUES (2591, '上海乐酷游艺（福邑轩）网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527926089833.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2591', '6KYOU', '乐酷游艺', '上海乐酷游艺（福邑轩）网络科技有限公司主要从事网页游戏、手机游戏的自主研发及运营服务，致力于为广大玩家带来更高品质、更具内涵的互联网游戏和手机游戏，并提供良好的技术支持与服务。', 'success');
INSERT INTO `company` VALUES (2597, '上海触韵网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1527938923760.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2597', '', '触韵网络', '上海触韵网络科技有限公司是一家以手游为主的网络公司，主要产品有泡泡泰迪熊、水果消消乐等。', 'success');
INSERT INTO `company` VALUES (2598, '北京盛天堂科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528450425978.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2598', 'sotodo', '盛天堂', '盛天堂是手机游戏及网页游戏开发商，旗下产品有LBS社交游戏《怪兽岛》、IOS产品《小小星球》、SLG页游作品《星际世界》、休闲系列《囧龙传说》、竞技类游戏《跑跑乐欢堂》、手机游戏《佣兵天下》等。', 'success');
INSERT INTO `company` VALUES (2599, '北京酷蜂软件开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528450752629.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2599', 'KUFENG Studio', '酷蜂软件', '酷蜂工作室于2007年4月在北京成立，在自主研发网络游戏的同时，也与韩国专业网络游戏运营商MGAME公司建立了战略合作关系。', 'success');
INSERT INTO `company` VALUES (2600, '上海宣合网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528450944098.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2600', 'ecngame', '宣合游戏', '上海宣合网络科技有限公司成立十三年间，先后从事游戏虚拟物品交易、端游开发、页游开发，至2012年底全面进入手游研发，以开发大型MMORPG为主。', 'success');
INSERT INTO `company` VALUES (2601, '北京零禾谷网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528451048646.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2601', '', '零禾谷', '零禾谷拥有完整建制团队，自研3D游戏引擎，自有美术团队，资深数值策划领衔，十年游戏开发经验，目前致力于手游、页游和微端游戏的研发。', 'success');
INSERT INTO `company` VALUES (2602, '北京桃源西谷信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528451155127.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2602', 'tao secret', '桃源西谷', '桃源西谷是一家专业的手游研发商，致力于为全世界的玩家提供高质量的移动设备内的快乐的游戏体验，并为我们的客户持续创造精神文明的价值和持续提供增值服务；公司目前所有员工均为游戏行业3-10年经验的资深人士，参与设计研发运营多打五十几款PC端和手机端的全球知名游戏，其中十余款月流水稳定千万级，两款月流水稳定过亿', 'success');
INSERT INTO `company` VALUES (2603, '上海页游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528451400567.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2603', '', '页游网络', '上海页游网络科技有限公司成立于2012年，是一家致力于网络游戏研发与运营的高科技企业，专注于网页游戏与移动平台等领域，经过半年多的努力，目前已经拥有了2个网页游戏项目研发组和游戏运营平台,同时也打造出一支具有强大研发实力的队伍，以高学历，高能力，高效率而领先于行业，为上海页游的崛起作出不可磨灭的贡献。', 'success');
INSERT INTO `company` VALUES (2604, '上海哒嗒网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528451597263.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2604', 'Data Nretwork', '哒嗒网络', '上海哒嗒网络科技有限公司（Data Network Co.Ltd）专业从事虚拟现实、增强现实、数字营销和云技术等领域的研发和运用，是虚拟现实场景设计和增强现实技术的方案供应商，公司总部位于上海市宝山区。<br/>上海哒嗒网络科技有限公司提供虚拟现实技术开发、增强现实技术解决方案、数字营销等服务。', 'success');
INSERT INTO `company` VALUES (2606, '成都游外游科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528451811870.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2606', '', '游外游科技', '都游外游科技有限公司于2013年7月在成都创立，公司现有人员近70人。公司核心成员平均游戏从业经验超过 6年，具有丰富的游戏开发管理、研发与制作经验，而且研发团队技术实力雄厚，由一批充满干劲的IT精英组成。', 'success');
INSERT INTO `company` VALUES (2607, '凡尔网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528451896902.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2607', 'FRnet', '凡尔网络', '凡尔网络科技有限公司成立于2013年5月，位于上海浦东陆家嘴，是一家专注于社交游戏以及移动互联网游戏研发的新锐互动娱乐公司。 <br/>　　凡尔网络其核心人员均拥有5年以上网游从业经验，其中不乏盛大、久游、游族、微软等公司中的核心领导人才。公司同时拥有强大的运营、商务资源对中国大陆和全球海外市场有着丰富的运营经验及渠道关系。 <br/>　　凡尔网络以制作最优秀的网络游戏，制造最完美的用户体验为公司最高目标。目前凡尔网络员工超过30名，数条研发线同时开展游戏研发工作，尤其Coco2D方向走在行业技术前端。 <br/>　　2014年是凡尔网络产品集中发力之年,邀请业内媒体平台洽谈合作推广，共商游戏行业美好未来。', 'success');
INSERT INTO `company` VALUES (2608, '厦门乐顽信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528452003876.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2608', 'DUOMAX', '厦门乐顽', '厦门乐顽信息科技有限公司是一家专注于移动领域创新游戏研发和运营的综合性互动娱乐企业，公司选择在国内网游发源地之一的厦门开始创业，远离了帝都的雾霾天，也剥离了魔都的浮躁。温馨的工作环境和游戏化的工作方式，让公司更加专注于打磨手机游戏精品。团队年轻活泼，合作融洽，以富有创新游戏的设计理念和熟稔的开发能力共同创建心目中的游戏世界。2014年团队推出诚意之作3DARPG《英雄觉醒》，今后将继续专注高品质网游的研发。<br/>       作为一个年轻的创业公司，乐顽深信改变世界那是乔布什和斯卡利的事，乐顽人只想静静的把游戏做好，通过游戏改变自己的未来。乐顽寻求真诚、严谨、勤奋、勇于创新和突破自我的你，加入乐顽这个年轻、奋进、互相学习，共同成长的集体。', 'success');
INSERT INTO `company` VALUES (2610, '浙江巅峰网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528453060685.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2610', '', '巅峰科技', '浙江巅峰网络技术有限公司于2010年05月13日在杭州市江干区工商行政管理局登记成立。法定代表人高瞻，公司经营范围包括许可经营项目：利用信息网络经营网络游戏产品（含网络游戏虚拟货币发行等。', 'success');
INSERT INTO `company` VALUES (2612, '上海鲸歌网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528453319360.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2612', 'Jinggle', '鲸歌网络', '公司创始团队来自盛大和淘米，有十多年的端游，页游，手游开发经验，拥有国际领先的3d游戏开发技术。本着爱游戏才做游戏的初衷，坚持做好玩的游戏。公司潜心研发四年的大型3d海战射击竞技手游《战舰猎手》已经由空中网代理发行上线。', 'success');
INSERT INTO `company` VALUES (2613, '上海杰姆火网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528453538336.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2613', 'DreamFire', '杰姆火网络', '上海杰姆火（Dreamfire）网络科技有限公司成立于2012年3月，是由一开<br/>投资创建的互动娱乐研发企业，致力于PC及移动互联平台游戏产品研发和服务。<br/>Drenmfire兼具创新设计能力和游戏领域的专业性，凭借其雄厚的软件开发实力<br/>和雄厚资本，充分利用互动娱乐行业的发展契机,设计、制作高品质的Unity3D、<br/>Flash及IOS等跨平台游戏产品。<br/>公司团队核心技术及管理人员来自国内外各大游戏公司，拥有丰富的项目开<br/>发经验以及成功的上线产品。办公环境优美、洁净。公司未来目标是在三年内，<br/>致力于跨平台游戏的开发，做精品游戏，并努力跻身于中国优秀游戏开发商行列。', 'success');
INSERT INTO `company` VALUES (2617, '上海尖娱信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528561738126.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2617', 'What‘s up Labs', '尖娱科技', '尖娱信息科技是一家成立于2012年的游戏软件开发公司。从2002年起，公司的创办者们就致力于在线游戏的开发工作，在移动网络游戏的时代，尖娱信息科技的工作目标非常简单--提供令人印象深刻的游戏体验。 公司旗下游戏《萌货战队》（台湾名《怪兽联萌》）由游戏新干线代理，在2014年6月在港澳台地区展开运营。', 'success');
INSERT INTO `company` VALUES (2618, '上海星之歌数码科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528561898498.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2618', 'M7 Game', 'M7 Game', '上海星之歌数码科技有限公司（M7 Game）位于上海创智天地广场，致力于提供基于客户端网络游戏，网页游戏及智能移动平台等游戏的制作和开发。', 'success');
INSERT INTO `company` VALUES (2619, '魔橙互联科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528562018123.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2619', 'MOZAT', '魔橙', '激情投身游戏事业，去中东土豪之地淘金。', 'success');
INSERT INTO `company` VALUES (2620, '漫风网络科技（上海）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528562303169.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2620', 'Manstyle', '漫风网络', '漫风网络科技有限公司是一家高新科技企业，公司研发及客服中心设在福州市，致力于网页游戏及手机网络游戏的研发与运营。力求打造最具特色、绿色健康的精品游戏，真正专心、用心为用户服务。 公司以为广大用户提供最优质的游戏产品与增值服务为导向。', 'success');
INSERT INTO `company` VALUES (2621, '深圳七子游戏', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528562399486.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2621', '7ZGAME', '七子游戏', '深圳七子计算机有限公司,简称七子游戏，成立于2012年，由一群经验丰富、技术扎实、有理想的年轻人创建，是中国领先的游戏开发商。 公司主要致力于网页游戏、手机游戏和电视游戏的研发，顺应潮流，布局三屏，着眼未来。 七子游戏秉承“用户体验至上”的宗旨，为玩家提供高品质的游戏产品， 共同推进中国互联网娱乐产业的蓬勃发展，为中国网络娱乐产业的发展做出积极贡献。', 'success');
INSERT INTO `company` VALUES (2622, '深圳市手手游戏有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528562614522.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2622', 'Pkpuke', '手游科技', '深圳市手游科技有限公司(简称手游科技)是一家拥有自主知识产权,专门从事网络在线休闲游戏、手机游戏研发的高科技公司。', 'success');
INSERT INTO `company` VALUES (2625, '东莞市群英九五网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528563270692.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2625', '', '群英九五', '东莞市群英九五网络科技有限公司为中外合资公司，隶属于东莞新文传媒集团旗下，是东莞地区新兴的互联网科技公司。致力于PC端互联网和移动互联网的开发及运营，有网络游戏（网页游戏和手机游戏）软件开发和网站运营业务。', 'success');
INSERT INTO `company` VALUES (2626, '就爱游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528563683671.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2626', '9K9K', '9K9K', '就爱游网络科技有限公司成立于2013年。公司主要经营范围为：软件，网络游戏研发，设计，销售，网络工程，信息系统工程施工，网站建设与维护及推广。<br/>公司拥有高水平高竞争力的研发团队，由资深游戏、软件开发高手及年轻有为的研发人员、技术支持人员、市场人员组成，为公司的稳定、健康、快速发展提供了坚实的基础。<br/>就爱游网络科技有限公司致力于网络游戏研发，将创新融入其中，通过自身坚持不懈的努力和钻研，望为广大用户提供最高品质的游戏产品。', 'success');
INSERT INTO `company` VALUES (2627, '哈尔滨森阳科技开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1528563915219.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2627', '', '森阳科技', '哈尔滨森阳科技开发有限公司是一家主要从事网页游戏开发的创业型公司，已经正式入驻哈尔滨平房动漫基地。', 'success');
INSERT INTO `company` VALUES (2629, '杭州亚瑟网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529323263472.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2629', 'yasegame', '亚瑟游戏', '亚瑟游戏成立于2011年1月，位于美丽的西子湖畔杭州，是长期专注于页游研发的专业性技术公司，是国内页游研发实力较强的公司之一。<br/><br/>亚瑟游戏专注页游研发，在公司发展过程中不断提升完善技术、研究提升用户体验，目前已经拥有一套成熟的技术架构以及用户体验设计方案。<br/><br/>亚瑟游戏拥有一支精于技术、充满热情的团队，以对游戏事业的理想为基础，立志于回归游戏本质、服务玩家，能够在帮助合作伙伴创收的同时对游戏研发产业起到正面的积极作用。', 'success');
INSERT INTO `company` VALUES (2630, '上海竹灵网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529323781877.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2630', 'ZOLIN', '竹灵网络', '一家国内领先的页游、手游研发公司。拥有《战神录》《世界杯足球》《狂野足球》《勇者大作战》等多款项目。', 'success');
INSERT INTO `company` VALUES (2631, '江苏欧飞电子商务有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529324008001.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2631', 'ofpay', '欧飞', '欧飞是一家致力于虚拟数字产品分销和服务的创新型企业，合作伙伴包括运营商、电商、银行、互联网公司、保险、支付、福利等行业巨头为代表的数万家企业。经过逾十年的发展，依托先进的技术及互联网管理理念，欧飞已成为国内领先的数字电商领导品牌。', 'success');
INSERT INTO `company` VALUES (2633, '福州盛天游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529324264223.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2633', '', '火龙动漫', '福州盛天游网络科技有限公司,位于福州市鼓楼区铜盘软件园F区7号楼9层。<br/>福州盛天游，是一家互联网新锐企业。成立于2013年。公司多年精耕于网页游戏的开发和发行，研运一体，其成员均为游戏行业资深精英，已积累多款成功项目经验。', 'success');
INSERT INTO `company` VALUES (2634, '湖北盛天网络技术股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529324362770.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2634', 'century', '盛天网络', '湖北盛天网络技术股份有限公司成立于2006年，是国内领先的场景化互联网用户运营平台，是国家规划布局内重点软件企业。盛天网络以场景化运营为核心，通过泛娱乐内容提供和大数据技术支撑进行企业战略布局，为用户提供个性化的互联网产品与服务。<br/>盛天网络主营网娱平台、电子竞技、游戏运营、媒体内容、移动互联与大数据等业务。目前产品已覆盖超过39.5%的中国网吧市场，为超过50000家场所提供服务，每天有超过2500万的用户通过盛天网络旗下的各类产品获取内容与服务。未来盛天网络将跨场景建立泛娱乐大生态，构建数字营销闭环。', 'success');
INSERT INTO `company` VALUES (2635, '上海龙会洲科技网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529324515606.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2635', '', '龙会洲', '龙会洲网络科技有限公司，在上海成立于2009年，是一家放眼于全世界但只专注于网页游戏的研发与运营的公司。<br/>龙会洲网络科技有限公司丰富的国际资源背景成功的让我们与美国、日本、俄国等众多游戏大国建立了良好的合作关系。', 'success');
INSERT INTO `company` VALUES (2636, '上海微游网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529324641471.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2636', 'MicroGame', '微游网络', '上海月千网络科技有限公司，是一家专注于网页游戏、移动互联网游戏的公司。<br/><br/>         月千《辉煌足球》团队的前身，曾研发与运营了《热血球球》与《热血篮球》两款成功的体育经理类页游。 《热血球球》 于2010年推出，是第一款有实时比赛画面的足球经理类游戏，后者2012年推出，是第一款有实时比赛画面的篮球经理类游戏。<br/>         热血系列深入了解玩家需求，配合丰富有趣的球员培养玩法，容易上手。特有的球员技能极大满足了玩家求新求刺激的心理。颠覆了以往玩家对体育经理类游戏的认知，成功创造了一种新的游戏形态。', 'success');
INSERT INTO `company` VALUES (2639, '北京忆唐创元文化游戏公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529325419456.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2639', '', '亿唐', '北京亿唐创元文化游戏公司为一家网络游戏研发公司，主要研发产品有命运永恒等。', 'success');
INSERT INTO `company` VALUES (2641, '安徽英速网络', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529325771531.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2641', 'ysjoy', '英速网络', '安徽英速网络科技有限公司定位力求于打造国内顶级游戏运营平台，从平台功能深度挖掘用户需求性，针对用户体验做差异化营销。正在形成网络平台、网络媒体、互联网增值业务、广告业务、动漫平台、电子商城等“一体化”业务体系，并逐步形成“一网式”综合平台服务体系。为各年龄段玩家提供手游端、网页无端、客户端三端网游最好的游戏体验和最优质的服务，努力与用户和合作伙伴通过更好交流和沟通，共同创造一流的娱乐平台环境。', 'success');
INSERT INTO `company` VALUES (2642, '上海恩侬网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529326222603.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2642', '', '恩侬网络', '上海恩侬网络科技有限公司是一家专注于体育类网页游戏研发的公司。', 'success');
INSERT INTO `company` VALUES (2643, '杭州冰蓝网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529326521019.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2643', 'Iceblue Network', '冰蓝网络', '杭州冰蓝网络科技有限公司(Iceblue Network)，简称冰蓝网络。冰蓝网络成立于2010年，是一家聚焦网页游戏领域的制作与发行公司。成立至今不久，已得到了大量国内一线网游行业精英的青睐。公司总部坐落于中国游戏业的“天堂硅谷”，素有“人间天堂”之称的江南名城——杭州。冰蓝网络自成立之初，就秉承“独立创新，铸就精品”的企业宗旨，倾力打造中国网页游戏精品旗舰。', 'success');
INSERT INTO `company` VALUES (2644, '北京游蛙网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529326656562.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2644', 'YOUWA', '游蛙网络', '北京游蛙网络科技有限公司成立于2013年1月，是家专注于游戏研发及外包服务的专业化公司。<br/>经过几年的磨砺和锤炼，已沉淀出一支极为优秀的制作团队。核心成员均来自于国内知名一线游戏公司，如完美，腾讯。这些资深的专业人士将用自己的敬业和创新精神为项目成功提供了强力保障。', 'success');
INSERT INTO `company` VALUES (2645, '杭州游牧网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529326793423.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2645', 'FlybearGame', '牧游', '杭州牧游网络技术有限公司是国内最早专注于历史题材类游戏的开发商之一。<br/>牧游公司拥有优秀的原创开发精英团队及资深管理团队，倾力打造拥有自主知识产权的高品质互联网游戏产品，为玩家提供涵盖PC机、移动手机、平板电脑等各类游戏平台的互动娱乐服务，诣在将中国悠远流长的历史文化与领先的互联网技术进一步融合、深化，并且积极推动数字娱乐产业的蓬勃发展。', 'success');
INSERT INTO `company` VALUES (2646, '北京绘色时雨网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529327089098.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2646', '', '绘色时雨', '北京绘色时雨网络是一家游戏美术外包服务提供商，支持2D、3D、游戏特效等。', 'success');
INSERT INTO `company` VALUES (2647, '北京那些时空软件有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529327342930.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2647', 'NXGame', '那些时空', '北京那些时空软件有限公司是一家以手机网络游戏研发为主营业务的公司，成立于2013年4月，团队初期成员7人，现公司实际人员12人，目前已研发三款独立的中型手机网游。目前已上线的产品有《宠物风暴》、《宝贝大乱斗》等，新游戏《逆世》也将于近期上线。Nxgame是一个创业型公司，期待充满斗志的你前来加入。<br/>        公司尊重知识，尊重个性，让每个员工都能追求自己的个性，发挥、强化自己的潜力，同时又提倡员工团队合作精神，形成紧密合作关系，使公司形成一支开拓创新、合作敬业的优秀人才队伍。倡导民主平等的上下级关系，自由公开的沟通方式，使工作和生活之间形成平衡、融洽的关系。', 'success');
INSERT INTO `company` VALUES (2648, '成都西米互动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529327489046.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2648', '', '西米互动', '成都西米互动科技有限公司是“西山居”和“小米科技”共同投资的一家高科技公司，公司专注于“棋牌和休闲游戏”的开发运营，立志要“做全球最好玩的棋牌休闲游戏”，要“为世界创造更多的棋牌快乐”。<br/>西米互动成立于2014年初，现有员工100余人，公司员工主要来自于金山、腾讯、华为等著名互联网公司。<br/>公司的游戏类型丰富，涉及“扑克类、麻将类、棋类、休闲类”，更多精彩游戏正在陆续上线。', 'success');
INSERT INTO `company` VALUES (2649, '成都甜橙网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529327581072.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2649', '', '甜橙网络', '成都甜橙网络技术有限公司是一家成立于2012年末的游戏研发公司。公司由一群热爱游戏的资深开发者组成，致力于客户端、手机游戏的研发。一直以带给玩家最好的游戏体验作为最高的目标，积极收集听取玩家的意见，尊重玩家的习惯，雕琢细节。尽自己最大的努力完善游戏产品，争取让每个玩家在玩我们的游戏时能有愉快美好的感受。', 'success');
INSERT INTO `company` VALUES (2650, '西安云曜互联信息技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529327978033.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2650', '', '云曜互联', '西安云曜互联信息技术有限公司成立于2013年，注册资本1000万元，是一家专注于提供移动增值服务和手游开发、Web页游开发与运营服务的综合性互联网公司。 <br/>云曜互联始终坚持自主研发，致力于推动网络游戏与及互联网020行业的发展，为满足用户不断变化的需求及市场发展，云曜互联将不断进取，坚持创新，以期成长为具有鲜明个性的网络游戏、移动互联网应用的领跑者和知名企业。', 'success');
INSERT INTO `company` VALUES (2652, '沈阳龙鳞动漫游戏科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529328249318.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2652', '', '龙鳞游戏', '沈阳龙鳞动漫游戏科技有限公司简称龙鳞游戏，是一家从事游戏研发企业。公司位于辽宁省沈阳市。<br/>沈阳龙鳞动漫游戏科技有限公司成立于2014年4月份，业务包括主机游戏、移动端游、网络游戏等游戏的 开发制作；虚拟城市地图建设；高等院校专业教材研发等。公司由来自著名游戏公司的技术人员组成，团队成 员曾参与《星球大战-1313》、《全球使命》、等大型 游戏项目，运用最新最先进的Unreal4引擎技术，以及3DsMax、Unity5、Quixel等图形软件，与奇虎360、陌陌等游戏运营商合作为其订制相关社交平台游戏；公司 已合作立项大型多平台主机游戏《僵尸世界大战-----新秩序》、移动端游《星界坦克》。', 'success');
INSERT INTO `company` VALUES (2653, '山东中动文化传媒有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529328644144.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2653', '', '中动传媒', '山东中动文化传媒有限公司（简称中动传媒）是中国国内具有一定规模的三维制作团队之一，拥有超200人的国际水准动画制作团队。 团队已多次承接国际高质量三维动画项目，其中《食功夫之美食大冒险》、《毛驴小侦探》等原创高收益作品被海内外市场广泛认可，取得了良好的社会效益与经济效益。<br/>2015年中动传媒将发力中国动画大电影市场，联合万达等共同制作的《嘻哈英熊》动画大电影即将上映，预期到2017年将有三部以上原创国际水准动画大电影问世，中动传媒未来将用五年的时间打造专业的互联网动漫娱乐平台。', 'success');
INSERT INTO `company` VALUES (2654, '北京上古彩科技股份有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529328749359.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2654', '', '上古彩', '北京上古彩科技股份有限公司是一家从事电子商务、网络营销及软件开发的新型高科技公司，属国家级高新技术企业，公司成立于2008年，注册资本2411万元。 <br/>    公司团队由行业资深专业人士组成，汇聚了来自业内的投资、业务、技术、运营等各路精英人才，具有强大商业运作实力和丰富的客户资源与渠道资源，与国际知名的互联网公司建立了战略合作伙伴关系，为长期业务发展奠定了坚实的基础。', 'success');
INSERT INTO `company` VALUES (2655, '上海六本网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529328864266.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2655', '', '六本', '上海六本网络科技有限公司，一家刚刚成立的网络专业从事游戏开发及运营的公司。投资方是已经在香港成功上市的国内知名大型企业。<br/>上海六本网络科技有限公司扎根上海，面向全国，长期规划、稳步发展。本着和为本，信为本，悟为本，韧为本，学为本，勤为本的经营理念，通过在内部建立既<br/>和谐又充满激情他业环境，给海内外热爱游戏用户提供最具价值的网络游戏，为加盟公司的每一位员工创造最完善的追求成功的平台！', 'success');
INSERT INTO `company` VALUES (2656, '深圳市刑天科技开发有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529328991390.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2656', 'OXTGAME', '刑天游戏', '研发团队均是原第七大道创始核心人员(弹弹堂、神曲主程、第七大道创始成员美术总监、<br/>弹弹堂主UI。 主策共同创立刑天游戏），主导研发了耳熟能详的网页游戏产品《弹弹堂》、<br/>《神曲》等项目。在产品技术积累、市场分析、用户交互体验方面拥有深厚的研发功底。<br/>自成立刑天科技以来，项目组不忘创业初心，在2013-2014年陆续推出的《龙之纹章》、<br/>《龙魂大陆》等产品获得了很好的市场反响。同时公司开发团队不断加强在移动产品方<br/>面的技术研发和产品布局。', 'success');
INSERT INTO `company` VALUES (2657, 'Quantic Dream 工作室', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1529330223331.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2657', 'Quantic Dream', 'Quantic Dream 工作室', 'Quantic Dream推动开拓了交互式电影流派，同时将游戏重心放在故事讲述、情绪表达和内容创新上。', 'success');
INSERT INTO `company` VALUES (2658, 'Bethesda Softworks,LLC,', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530087516555.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2658', 'Bethesda', 'Bethesda', 'Bethesda Softworks,LLC,是一家美国游戏发行商，并且为Zenimax Media 的子公司。公司于1986年始创于美国马里兰州的Bethesda（Bethesda，Maryland），并且于1990年迁址至马里兰州Rockville（Rockville,Maryland）。旗下有诸多不同题材和类型的游戏发系列，包括角色扮演（RPG），竞速，模拟类和体育类型等。', 'success');
INSERT INTO `company` VALUES (2659, '任天堂', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530088300837.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2659', '英语Nintendo', '任天堂', '任天堂（英语Nintendo）是一家全球知名的娱乐厂商，电子游戏业三巨头之一，现代电子游戏产业的开创者。任天堂始建于1889年9月23日，  创始人山内房治郎。 主营业务为家用游戏机和掌上游戏机的软硬件开发与发行。', 'success');
INSERT INTO `company` VALUES (2660, '微软游戏工作室', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530089295395.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2660', 'Microsoft Game Studios', 'MGS', '微软游戏工作室（Microsoft Game Studios，简称MGS），原名Microsoft Game Division，于2002年起使用现时的商标与名字，是微软的全资子公司，为基于Windows的个人电脑与Xbox及Xbox 360游戏机开发与发行游戏。MGS为数个微软所有的第一方开发商发行游戏，包括了Bungie Studios与Rare，亦有发行由如Bioware与Bizarre Creations等第三方开发商制作的游戏。', 'success');
INSERT INTO `company` VALUES (2661, 'LandfallGames', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530089824714.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2661', 'LandfallGames', 'LandfallGames', '位于瑞典斯德哥尔摩的游戏工作室，代表作有《Stick Fight：The Game》、《TABS》。', 'success');
INSERT INTO `company` VALUES (2662, '成都玉狐网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530291854822.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2662', '', '玉狐网络', '成都玉狐网络科技有限公司成立于2011年，是立足于游戏原画设计的专业美术设计外包公司。成立至今，玉狐秉承“满足游戏市场需求”的经营理念，在跌宕起伏、大进大出的互联网行业始终保持稳健的发展态势和阶段性的突破创新。<br/>从最初为游戏美术多元化服务内容，到专业的游戏原画设计，玉狐着力打造“一流游戏原画设计”。以强大的美术团队为依托，从设计理念、客户需求和市场导向等层面为游戏研发商提供最佳的游戏原画设计。', 'success');
INSERT INTO `company` VALUES (2663, '上海千艺网络技术有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530292163085.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2663', '', '千艺网络', '上海千艺网络技术有限公司始创于2013年，是一家讲究细致、追求卓越、不断拼搏进取的游戏研发公司。<br/>目前公司首款自研手游《寻秦外传》业已蓄势待发。届时预计会在全国多个手机推广平台进行联合首发，意在为武侠类RPG的手机游戏掀起一波新的浪潮。<br/>公司的核心价值观是：进取、成长、分享、共赢。', 'success');
INSERT INTO `company` VALUES (2664, '杭州宣诚科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530292410489.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2664', '', '宣诚科技', '杭州宣诚科技有限公司是一家集网络游戏开发商务软件开发于一体的综合性高科技企业公司成立于2008年位于杭州市中环大厦公司凭借着强大的技术实力成为西门子PLM SOFTWARE全球合作伙伴 宣诚科技倾力创造优质的互动产业品牌打造拥有自主知识产权的高品质软件公司现有自主知识产权的Web2.5免客户端互动引擎拥有王国印记魔法学院两款webgame以及高级中学信息化立体解决方案宣诚中学教学管理系统公司已从一个单纯的软件开发企业成长为集研发运营销售服务于一体的网络互动平台服务提供商', 'success');
INSERT INTO `company` VALUES (2665, '源泉创新网络科技（北京）有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530292757088.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2665', '', '源泉创新', '源泉创新网络科技（北京）有限公司，公司注册资本700万，公司设在北京CBD建外soho（永安里和国贸地铁站附近），前身成立于2007年9月。是中国最早专门从事富媒体互动娱乐平台产品和MMORPG WebGame 开发、运营的提供商之一。公司致力于研发和发展MMORPG WebGame 、富媒体应用技术、高性能服务器通讯以及相关多媒体娱乐交易网络平台。', 'success');
INSERT INTO `company` VALUES (2666, '四川齐齐乐网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530292877584.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2666', 'QQL', '齐齐乐', '齐齐乐是一家以网络游戏为发展起点，集研发、运营、销售为一体的综合性互动娱乐企业。目前员工总数49名，主要组成集合了国内有丰富开发经验（包含游戏、数字娱乐内容）及成功运营经验的资深专业团队，并紧跟全球游戏娱乐行业最新发展脉动，进军网络数字娱乐市场，为用户带来最新型态的网络数字互动娱乐方式。', 'success');
INSERT INTO `company` VALUES (2669, '北京傲雪互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530293285969.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2669', '', '傲雪互娱', '北京傲雪互娱科技有限公司成立于2012年，致力于打造一流游戏精品。研发成员30人左右。团队核心均从业多年，经验丰富。经过近四年的磨合与锻炼，已有多款上线运营的页游产品，如《灵宝迷城》、《魔境》等，产品均采用自研《BlackRain》（黑雨）游戏引擎开发。我们是一支富有激情，有着强烈的进取心和不断创新意识的优良团队。团队于今年成立手游研发部，此外公司旗下还有一家游戏美术培训机构。', 'success');
INSERT INTO `company` VALUES (2672, '杭州皇池网络有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530293921874.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2672', '', '皇池网络', '杭州皇池网络有限公司团队创立于2009年，公司正式成立于2012年9月，位于浙江省杭州市滨江区中恒世纪科技园内，具备良好的工作环境。皇池网络作为一家创业型互联网公司，完全秉承“追求品质，服务为先”的企业精神，致力于为中国互联网用户提供高品质互动娱乐产品，打造成为一家用户满意、行业领先的新锐企业。', 'success');
INSERT INTO `company` VALUES (2673, '北京青果灵动科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530294058571.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2673', '', '青果灵动', '北京青果灵动科技有限公司成立于2009年9月，是中国领先的Web 3D互动娱乐软件研发企业，是中国第一款页面3D MMO游戏的缔造者。公司拥有完全自主知识产权的跨平台开发引擎Fancy3D（以下简称F3D），可支持企业开发基于Windows、iOS、Android 平台的大型复杂多人交互式3D游戏应用，是国内极少数拥有核心研发技术的企业。', 'success');
INSERT INTO `company` VALUES (2674, '海南坏坏信息科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530460287006.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2674', '', '808wan', '坏坏信息科技有限公司注册资金1000万元人民币，坐落海口市信息科技园，是一家拥有100位员工的中小型网络游戏创新及信息服务公司，是一家以网络游戏发展为起点、集网络创新、游戏推广、游戏宣传、网络服务及软件开发为一体的综合性互动网络创新企业。', 'success');
INSERT INTO `company` VALUES (2676, '暴雪娱乐公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530460899952.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2676', 'Blizzard', '暴雪', '暴雪公司推出过多款经典系列作品：魔兽争霸、星际争霸、暗黑破坏神系列，守望先锋，炉石传说，魔兽世界。魔兽争霸，风暴英雄及星际争霸均被多项著名电子竞技比赛列为主要比赛项目，在电脑游戏界享有高评价。', 'success');
INSERT INTO `company` VALUES (2677, '天津游奕互动软件有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530463402101.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2677', '', '游奕互动', '游奕互动（UEGAME）成立于2014年，是一家专注于移动端精品游戏的研发公司，主要研发制作3DMMORPG的中重度游戏。<br/><br/>公司主力成员均来自国内一线游戏企业，平均行内从业经验8年，参与并负责过《完美世界》、《诛仙》、《神魔大陆》、《笑傲江湖》、《圣斗士》、《冰火破坏神》等游戏的开发与制作，并于2014年大胆创新研发了移动MMO《神魔大陆3D》，取得了巨大成功。', 'success');
INSERT INTO `company` VALUES (2678, '广州传悦网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530463475238.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2678', '', '叶子猪', '2003年一群游戏玩家成立了叶子猪，它是国内知名的游戏门户网站之一，网站有全方位的网络游戏资讯、攻略和上千个游戏专区...', 'success');
INSERT INTO `company` VALUES (2679, '上海鸣勃网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530463635751.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2679', 'Fungo', '鸣勃网络', 'Fungo是一个年轻的App，是一个手游平台，却又不是传统的手游平台。<br/>致力于成为最优质的手游平台<br/>致力于帮6亿手游玩家找寻他们的游戏口味<br/>致力于让每个年轻玩家构建起自己的虚拟世界', 'success');
INSERT INTO `company` VALUES (2680, '北京新芮互娱科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530463737123.jpg', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2680', '', '新芮互娱', '北京新芮互娱科技有限公司是一家处于发展成长期的手游公司，主力成员均来自于知名端游和手游研发公司，有着实力雄厚的产品制作能力及深厚的商务能力。目前有员工50人。新芮互娱的首款手游产品《全民破坏神》，成功登录微信平台并在2015年取得良好的成绩作为开端。目前新的创新手游和IP产品正在火热研发中。', 'success');
INSERT INTO `company` VALUES (2681, '上海聪盛网络科技有限公司', 'http://gamemap.cgigc.com.cn/uploadImages/corporation/avatar/2/1530463803351.png', 'http://gamemap.cgigc.com.cn/detail.html?type=2&queryid=2681', '', '悟饭游戏', '悟饭游戏（原恺英网络（A股：002517）内部项目）<br/>基于全球化视野，紧跟移动互联网发展，致力于为每一位用户，提供优质产品和全方位娱乐服务。现主营两大项目：悟饭游戏厅（原啪啪游戏厅）和悟饭游戏发行。<br/>悟饭游戏厅：经典游戏对战平台，拥有自主研发的GBA、FC、街机、PSP等模拟器，独创用户定制游戏机制，更有海量经典游戏免费下载；2015年底，平台总用户数已超百万；2016年底，平台日活人数已超百万。', 'success');
